import * as path from 'path';

import { StrykerOptions } from '@stryker-mutator/api/core';
import { File } from '@stryker-mutator/api/core';
import { MutantResult, MutantStatus } from '@stryker-mutator/api/report';
import { TestFramework } from '@stryker-mutator/api/test_framework';
import { RunResult, RunStatus, TestRunner, TestStatus } from '@stryker-mutator/api/test_runner';
import { normalizeWhitespaces } from '@stryker-mutator/util';
import { getLogger } from 'log4js';
import * as mkdirp from 'mkdirp';

import LoggingClientContext from './logging/LoggingClientContext';
import ResilientTestRunnerFactory from './test-runner/ResilientTestRunnerFactory';
import TestableMutant, { TestSelectionResult } from './TestableMutant';
import TranspiledMutant from './TranspiledMutant';
import { findNodeModules, symlinkJunction, writeFile } from './utils/fileUtils';
import { wrapInClosure } from './utils/objectUtils';
import { TemporaryDirectory } from './utils/TemporaryDirectory';

interface FileMap {
  [sourceFile: string]: string;
}

export default class Sandbox {
  private readonly log = getLogger(Sandbox.name);
  private testRunner: Required<TestRunner>;
  private fileMap: FileMap;
  private readonly workingDirectory: string;

  private constructor(
    private readonly options: StrykerOptions,
    private readonly index: number,
    private readonly files: readonly File[],
    private readonly testFramework: TestFramework | null,
    private readonly timeOverheadMS: number,
    private readonly loggingContext: LoggingClientContext,
    temporaryDirectory: TemporaryDirectory
  ) {
    this.workingDirectory = temporaryDirectory.createRandomDirectory('sandbox');
    this.log.debug('Creating a sandbox for files in %s', this.workingDirectory);
  }

  private async initialize(): Promise<void> {
    await this.fillSandbox();
    await this.symlinkNodeModulesIfNeeded();
    return this.initializeTestRunner();
  }

  public static create(
    options: StrykerOptions,
    index: number,
    files: readonly File[],
    testFramework: TestFramework | null,
    timeoutOverheadMS: number,
    loggingContext: LoggingClientContext,
    temporaryDirectory: TemporaryDirectory
  ): Promise<Sandbox> {
    const sandbox = new Sandbox(options, index, files, testFramework, timeoutOverheadMS, loggingContext, temporaryDirectory);
    return sandbox.initialize().then(() => sandbox);
  }

  public run(timeout: number, testHooks: string | undefined, mutatedFileName?: string): Promise<RunResult> {
    return this.testRunner.run({ timeout, testHooks, mutatedFileName });
  }

  public dispose(): Promise<void> {
    return this.testRunner.dispose() || Promise.resolve();
  }

  public async runMutant(transpiledMutant: TranspiledMutant): Promise<MutantResult> {
    const earlyResult = this.retrieveEarlyResult(transpiledMutant);
    if (earlyResult) {
      return earlyResult;
    } else {
      const mutantFiles = transpiledMutant.transpileResult.outputFiles;
      if (transpiledMutant.mutant.testSelectionResult === TestSelectionResult.Failed) {
        this.log.warn(
          `Failed find coverage data for this mutant, running all tests. This might have an impact on performance: ${transpiledMutant.mutant.toString()}`
        );
      }
      await Promise.all(mutantFiles.map(mutatedFile => this.writeFileInSandbox(mutatedFile)));
      const runResult = await this.run(
        this.calculateTimeout(transpiledMutant.mutant),
        this.getFilterTestsHooks(transpiledMutant.mutant),
        this.fileMap[transpiledMutant.mutant.fileName]
      );
      await this.reset(mutantFiles);
      return this.collectMutantResult(transpiledMutant.mutant, runResult);
    }
  }

  private readonly retrieveEarlyResult = (transpiledMutant: TranspiledMutant): MutantResult | null => {
    if (transpiledMutant.transpileResult.error) {
      if (this.log.isDebugEnabled()) {
        this.log.debug(
          `Transpile error occurred: "${transpiledMutant.transpileResult.error}" during transpiling of mutant ${transpiledMutant.mutant.toString()}`
        );
      }
      const result = transpiledMutant.mutant.createResult(MutantStatus.TranspileError, []);
      return result;
    } else if (!transpiledMutant.mutant.runAllTests && !transpiledMutant.mutant.selectedTests.length) {
      const result = transpiledMutant.mutant.createResult(MutantStatus.NoCoverage, []);
      return result;
    } else if (!transpiledMutant.changedAnyTranspiledFiles) {
      const result = transpiledMutant.mutant.createResult(MutantStatus.Survived, []);
      return result;
    } else {
      // No early result possible, need to run in the sandbox later
      return null;
    }
  };

  private collectMutantResult(mutant: TestableMutant, runResult: RunResult): MutantResult {
    const status: MutantStatus = this.determineMutantState(runResult);
    const testNames = runResult.tests.filter(t => t.status !== TestStatus.Skipped).map(t => t.name);
    if (this.log.isDebugEnabled() && status === MutantStatus.RuntimeError) {
      const error = runResult.errorMessages ? runResult.errorMessages.toString() : '(undefined)';
      this.log.debug('A runtime error occurred: %s during execution of mutant: %s', error, mutant.toString());
    }
    return mutant.createResult(status, testNames);
  }

  private determineMutantState(runResult: RunResult): MutantStatus {
    switch (runResult.status) {
      case RunStatus.Timeout:
        return MutantStatus.TimedOut;
      case RunStatus.Error:
        return MutantStatus.RuntimeError;
      case RunStatus.Complete:
        if (runResult.tests.some(t => t.status === TestStatus.Failed)) {
          return MutantStatus.Killed;
        } else {
          return MutantStatus.Survived;
        }
    }
  }

  private reset(mutatedFiles: readonly File[]) {
    const originalFiles = this.files.filter(originalFile => mutatedFiles.some(mutatedFile => mutatedFile.name === originalFile.name));

    return Promise.all(originalFiles.map(file => writeFile(this.fileMap[file.name], file.content)));
  }

  private writeFileInSandbox(file: File): Promise<void> {
    const fileNameInSandbox = this.fileMap[file.name];
    return writeFile(fileNameInSandbox, file.content);
  }

  private fillSandbox(): Promise<void[]> {
    this.fileMap = Object.create(null);
    const copyPromises = this.files.map(file => this.fillFile(file));
    return Promise.all(copyPromises);
  }

  private async symlinkNodeModulesIfNeeded(): Promise<void> {
    if (this.options.symlinkNodeModules) {
      // TODO: Change with this.options.basePath when we have it
      const basePath = process.cwd();
      const nodeModules = await findNodeModules(basePath);
      if (nodeModules) {
        await symlinkJunction(nodeModules, path.join(this.workingDirectory, 'node_modules')).catch((error: NodeJS.ErrnoException) => {
          if (error.code === 'EEXIST') {
            this.log.warn(
              normalizeWhitespaces(`Could not symlink "${nodeModules}" in sandbox directory,
              it is already created in the sandbox. Please remove the node_modules from your sandbox files.
              Alternatively, set \`symlinkNodeModules\` to \`false\` to disable this warning.`)
            );
          } else {
            this.log.warn(`Unexpected error while trying to symlink "${nodeModules}" in sandbox directory.`, error);
          }
        });
      } else {
        this.log.warn(`Could not find a node_modules folder to symlink into the sandbox directory. Search "${basePath}" and its parent directories`);
      }
    }
  }

  private fillFile(file: File): Promise<void> {
    const relativePath = path.relative(process.cwd(), file.name);
    const folderName = path.join(this.workingDirectory, path.dirname(relativePath));
    mkdirp.sync(folderName);
    const targetFile = path.join(folderName, path.basename(relativePath));
    this.fileMap[file.name] = targetFile;
    return writeFile(targetFile, file.content);
  }

  private async initializeTestRunner(): Promise<void> {
    const fileNames = Object.keys(this.fileMap).map(sourceFileName => this.fileMap[sourceFileName]);
    this.log.debug('Creating test runner %s', this.index);
    this.testRunner = ResilientTestRunnerFactory.create(this.options, fileNames, this.workingDirectory, this.loggingContext);
    await this.testRunner.init();
  }

  private calculateTimeout(mutant: TestableMutant) {
    const baseTimeout = mutant.timeSpentScopedTests;
    return this.options.timeoutFactor * baseTimeout + this.options.timeoutMS + this.timeOverheadMS;
  }

  private getFilterTestsHooks(mutant: TestableMutant): string | undefined {
    if (this.testFramework && !mutant.runAllTests) {
      return wrapInClosure(this.testFramework.filter(mutant.selectedTests));
    } else {
      return undefined;
    }
  }
}

import * as os from 'os';

import { File, StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { MutantResult } from '@stryker-mutator/api/report';
import { TestFramework } from '@stryker-mutator/api/test_framework';
import { Observable, range, Subject } from 'rxjs';
import { filter, flatMap, map, merge, tap, zip } from 'rxjs/operators';
import { Disposable } from 'typed-inject';

import { coreTokens } from './di';
import LoggingClientContext from './logging/LoggingClientContext';
import { InitialTestRunResult } from './process/InitialTestExecutor';
import Sandbox from './Sandbox';
import TranspiledMutant from './TranspiledMutant';
import { TemporaryDirectory } from './utils/TemporaryDirectory';

const MAX_CONCURRENT_INITIALIZING_SANDBOXES = 2;

export class SandboxPool implements Disposable {
  private readonly allSandboxes: Array<Promise<Sandbox>> = [];
  private readonly overheadTimeMS: number;

  public static inject = tokens(
    commonTokens.logger,
    commonTokens.options,
    coreTokens.testFramework,
    coreTokens.initialRunResult,
    coreTokens.transpiledFiles,
    coreTokens.loggingContext,
    coreTokens.temporaryDirectory
  );
  constructor(
    private readonly log: Logger,
    private readonly options: StrykerOptions,
    private readonly testFramework: TestFramework | null,
    initialRunResult: InitialTestRunResult,
    private readonly initialFiles: readonly File[],
    private readonly loggingContext: LoggingClientContext,
    private readonly tempDir: TemporaryDirectory
  ) {
    this.overheadTimeMS = initialRunResult.overheadTimeMS;
  }

  public runMutants(mutants: Observable<TranspiledMutant>): Observable<MutantResult> {
    const recycledSandboxes = new Subject<Sandbox>();
    // Make sure sandboxes get recycled
    const sandboxes = this.startSandboxes().pipe(merge(recycledSandboxes));
    return mutants.pipe(
      zip(sandboxes),
      flatMap(this.runInSandbox),
      tap(({ sandbox }) => {
        recycledSandboxes.next(sandbox);
      }),
      map(({ result }) => result)
    );
  }

  private readonly runInSandbox = async ([mutant, sandbox]: [TranspiledMutant, Sandbox]) => {
    const result = await sandbox.runMutant(mutant);
    return { result, sandbox };
  };

  private startSandboxes(): Observable<Sandbox> {
    const concurrency = this.determineConcurrency();

    return range(0, concurrency).pipe(
      flatMap(async n => {
        if (this.isDisposed) {
          return null;
        } else {
          return this.registerSandbox(
            Sandbox.create(this.options, n, this.initialFiles, this.testFramework, this.overheadTimeMS, this.loggingContext, this.tempDir)
          );
        }
      }, MAX_CONCURRENT_INITIALIZING_SANDBOXES),
      filter(sandboxOrNull => !!sandboxOrNull),
      map(sandbox => sandbox as Sandbox)
    );
  }

  private determineConcurrency() {
    let numConcurrentRunners = os.cpus().length;
    if (this.options.transpilers.length) {
      // If transpilers are configured, one core is reserved for the compiler (for now)
      numConcurrentRunners--;
    }
    let numConcurrentRunnersSource = 'CPU count';
    if (numConcurrentRunners > this.options.maxConcurrentTestRunners && this.options.maxConcurrentTestRunners > 0) {
      numConcurrentRunners = this.options.maxConcurrentTestRunners;
      numConcurrentRunnersSource = 'maxConcurrentTestRunners config';
    }
    if (numConcurrentRunners <= 0) {
      numConcurrentRunners = 1;
    }
    this.log.info(`Creating ${numConcurrentRunners} test runners (based on ${numConcurrentRunnersSource})`);
    return numConcurrentRunners;
  }

  private readonly registerSandbox = async (promisedSandbox: Promise<Sandbox>): Promise<Sandbox> => {
    this.allSandboxes.push(promisedSandbox);
    return promisedSandbox;
  };

  private isDisposed = false;
  public async dispose() {
    this.isDisposed = true;
    const sandboxes = await Promise.all(this.allSandboxes);
    await Promise.all(sandboxes.map(sandbox => sandbox.dispose()));
  }
}

import * as path from 'path';

import { MutationScoreThresholds } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { MutantResult, MutantStatus, ScoreResult } from '@stryker-mutator/api/report';

import flatMap = require('lodash.flatmap');
import groupBy = require('lodash.groupby');

import { freezeRecursively, setExitCode } from './utils/objectUtils';

const defaultScoreIfNoValidMutants = 100;

export default class ScoreResultCalculator {
  public static inject = tokens(commonTokens.logger);
  constructor(private readonly log: Logger) {}

  public calculate(results: MutantResult[]): ScoreResult {
    const scoreResult = this.calculateScoreResult(results, '');
    return this.wrapIfSingleFileScoreResult(scoreResult);
  }

  public determineExitCode(score: ScoreResult, thresholds: MutationScoreThresholds | undefined) {
    const breaking = thresholds && thresholds.break;
    const formattedScore = score.mutationScore.toFixed(2);
    if (typeof breaking === 'number') {
      if (score.mutationScore < breaking) {
        this.log.error(`Final mutation score ${formattedScore} under breaking threshold ${breaking}, setting exit code to 1 (failure).`);
        this.log.info('(improve mutation score or set `thresholds.break = null` to prevent this error in the future)');
        setExitCode(1);
      } else {
        this.log.info(`Final mutation score of ${formattedScore} is greater than or equal to break threshold ${breaking}`);
      }
    } else {
      this.log.debug(
        "No breaking threshold configured. Won't fail the build no matter how low your mutation score is. Set `thresholds.break` to change this behavior."
      );
    }
  }

  private wrapIfSingleFileScoreResult(scoreResult: ScoreResult): ScoreResult {
    if (scoreResult.representsFile) {
      return this.copy(scoreResult, {
        childResults: [this.copy(scoreResult, { name: path.basename(scoreResult.name) })],
        name: path.dirname(scoreResult.name)
      });
    } else {
      return scoreResult;
    }
  }

  private calculateScoreResult(results: MutantResult[], basePath: string): ScoreResult {
    const numbers = this.countNumbers(results);
    const facts = this.determineFacts(basePath, results);
    return freezeRecursively(Object.assign(numbers, facts));
  }

  private copy(defaults: ScoreResult, overrides: Partial<ScoreResult>): ScoreResult {
    return Object.assign({}, defaults, overrides);
  }

  private determineFacts(basePath: string, results: MutantResult[]) {
    const name = this.determineCommonBasePath(results, basePath);
    const childResults = this.calculateChildScores(results, name, basePath);
    return {
      childResults,
      name,
      path: path.join(basePath, name),
      representsFile: childResults.length === 0 && results.length > 0
    };
  }

  private compareScoreResults(a: ScoreResult, b: ScoreResult) {
    const sortValue = (scoreResult: ScoreResult) => {
      // Directories first
      if (scoreResult.representsFile) {
        return `1${scoreResult.name}`;
      } else {
        return `0${scoreResult.name}`;
      }
    };
    return sortValue(a).localeCompare(sortValue(b));
  }

  private calculateChildScores(results: MutantResult[], parentName: string, basePath: string) {
    const childrenBasePath = parentName.length ? path.join(basePath, parentName) + path.sep : '';
    const resultsGroupedByFiles = groupBy(results, result => result.sourceFilePath.substr(childrenBasePath.length));
    const uniqueFiles = Object.keys(resultsGroupedByFiles);

    if (uniqueFiles.length > 1) {
      const filesGroupedByDirectory = groupBy(uniqueFiles, file => file.split(path.sep)[0]);
      return Object.keys(filesGroupedByDirectory)

        .map(directory =>
          this.calculateScoreResult(
            flatMap(filesGroupedByDirectory[directory], file => resultsGroupedByFiles[file]),
            childrenBasePath
          )
        )
        .sort(this.compareScoreResults);
    } else {
      return [];
    }
  }

  private determineCommonBasePath(results: MutantResult[], basePath: string) {
    const uniqueFiles = [...new Set(results.map(result => result.sourceFilePath))];
    const uniqueFileDirectories = uniqueFiles.map(file => file.substr(basePath.length).split(path.sep));

    if (uniqueFileDirectories.length) {
      return uniqueFileDirectories.reduce(this.filterDirectories).join(path.sep);
    } else {
      return '';
    }
  }

  private filterDirectories(previousDirectories: string[], currentDirectories: string[]) {
    for (let i = 0; i < previousDirectories.length; i++) {
      if (previousDirectories[i] !== currentDirectories[i]) {
        return previousDirectories.splice(0, i);
      }
    }

    return previousDirectories;
  }

  private countNumbers(mutantResults: MutantResult[]) {
    const count = (mutantResult: MutantStatus) => mutantResults.filter(_ => _.status === mutantResult).length;

    const killed = count(MutantStatus.Killed);
    const timedOut = count(MutantStatus.TimedOut);
    const survived = count(MutantStatus.Survived);
    const noCoverage = count(MutantStatus.NoCoverage);
    const runtimeErrors = count(MutantStatus.RuntimeError);
    const transpileErrors = count(MutantStatus.TranspileError);
    const totalDetected = timedOut + killed;
    const totalUndetected = survived + noCoverage;
    const totalCovered = totalDetected + survived;
    const totalValid = totalUndetected + totalDetected;
    const totalInvalid = runtimeErrors + transpileErrors;
    const totalMutants = totalValid + totalInvalid;
    const mutationScore = totalValid > 0 ? (totalDetected / totalValid) * 100 : defaultScoreIfNoValidMutants;
    const mutationScoreBasedOnCoveredCode = totalValid > 0 ? (totalDetected / totalCovered) * 100 || 0 : defaultScoreIfNoValidMutants;
    return {
      killed,
      mutationScore,
      mutationScoreBasedOnCoveredCode,
      noCoverage,
      runtimeErrors,
      survived,
      timedOut,
      totalCovered,
      totalDetected,
      totalInvalid,
      totalMutants,
      totalUndetected,
      totalValid,
      transpileErrors
    };
  }
}

import { File, Location, Position, Range } from '@stryker-mutator/api/core';

const enum CharacterCodes {
  MaxAsciiCharacter = 0x7f,
  LineFeed = 0x0a, // \n
  CarriageReturn = 0x0d, // \r
  LineSeparator = 0x2028,
  ParagraphSeparator = 0x2029
}

export function isLineBreak(ch: number): boolean {
  // ES5 7.3:
  // The ECMAScript line terminator characters are listed in Table 3.
  //     Table 3: Line Terminator Characters
  //     Code Unit Value     Name                    Formal Name
  //     \u000A              Line Feed               <LF>
  //     \u000D              Carriage Return         <CR>
  //     \u2028              Line separator          <LS>
  //     \u2029              Paragraph separator     <PS>
  // Only the characters in Table 3 are treated as line terminators. Other new line or line
  // breaking characters are treated as white space but not as line terminators.

  return (
    ch === CharacterCodes.LineFeed ||
    ch === CharacterCodes.CarriageReturn ||
    ch === CharacterCodes.LineSeparator ||
    ch === CharacterCodes.ParagraphSeparator
  );
}

export default class SourceFile {
  private readonly lineStarts: number[];

  constructor(public file: File) {
    this.lineStarts = this.computeLineStarts();
  }

  public get name() {
    return this.file.name;
  }

  public get content(): string {
    return this.file.textContent;
  }

  public getLocation(range: Range): Location {
    return {
      end: this.getPosition(range[1]),
      start: this.getPosition(range[0])
    };
  }

  public getPosition(pos: number): Position {
    let lineNumber = this.binarySearch(pos);
    if (lineNumber < 0) {
      // If the actual position was not found,
      // the binary search returns the 2's-complement of the next line start
      // e.g. if the line starts at [5, 10, 23, 80] and the position requested was 20
      // then the search will return -2.
      //
      // We want the index of the previous line start, so we subtract 1.
      // Review 2's-complement if this is confusing.
      lineNumber = ~lineNumber - 1;
    }
    return {
      column: pos - this.lineStarts[lineNumber],
      line: lineNumber
    };
  }

  /**
   * Performs a binary search, finding the index at which 'value' occurs in 'array'.
   * If no such index is found, returns the 2's-complement of first index at which
   * number[index] exceeds number.
   * @param array A sorted array whose first element must be no larger than number
   * @param number The value to be searched for in the array.
   */
  private binarySearch(position: number, offset = 0): number {
    let low = offset;
    let high = this.lineStarts.length - 1;

    while (low <= high) {
      const middle = low + ((high - low) >> 1);
      const midValue = this.lineStarts[middle];

      if (midValue === position) {
        return middle;
      } else if (midValue > position) {
        high = middle - 1;
      } else {
        low = middle + 1;
      }
    }

    return ~low;
  }
  private computeLineStarts(): number[] {
    const result: number[] = [];
    let pos = 0;
    let lineStart = 0;
    const markLineStart = () => {
      result.push(lineStart);
      lineStart = pos;
    };
    while (pos < this.file.textContent.length) {
      const ch = this.file.textContent.charCodeAt(pos);
      pos++;
      switch (ch) {
        case CharacterCodes.CarriageReturn:
          if (this.file.textContent.charCodeAt(pos) === CharacterCodes.LineFeed) {
            pos++;
          }
          markLineStart();
          break;
        case CharacterCodes.LineFeed:
          markLineStart();
          break;
        default:
          if (ch > CharacterCodes.MaxAsciiCharacter && isLineBreak(ch)) {
            markLineStart();
          }
          break;
      }
    }
    result.push(lineStart);
    return result;
  }
}

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, PluginKind } from '@stryker-mutator/api/plugin';
import { MutantResult } from '@stryker-mutator/api/report';
import { Injector } from 'typed-inject';

import { buildMainInjector, coreTokens, MainContext, PluginCreator } from './di';
import InputFileCollection from './input/InputFileCollection';
import InputFileResolver from './input/InputFileResolver';
import LogConfigurator from './logging/LogConfigurator';
import { MutantTestMatcher } from './mutants/MutantTestMatcher';
import { MutatorFacade } from './mutants/MutatorFacade';
import InitialTestExecutor from './process/InitialTestExecutor';
import { MutationTestExecutor } from './process/MutationTestExecutor';
import { MutationTestReportCalculator } from './reporters/MutationTestReportCalculator';
import { SandboxPool } from './SandboxPool';
import ScoreResultCalculator from './ScoreResultCalculator';
import { transpilerFactory } from './transpiler';
import { MutantTranspileScheduler } from './transpiler/MutantTranspileScheduler';
import { TranspilerFacade } from './transpiler/TranspilerFacade';

export default class Stryker {
  private readonly log: Logger;
  private readonly injector: Injector<MainContext>;

  private get reporter() {
    return this.injector.resolve(coreTokens.reporter);
  }

  private get options(): Readonly<StrykerOptions> {
    return this.injector.resolve(commonTokens.options);
  }

  private get timer() {
    return this.injector.resolve(coreTokens.timer);
  }

  private get temporaryDirectory() {
    return this.injector.resolve(coreTokens.temporaryDirectory);
  }

  /**
   * The Stryker mutation tester.
   * @constructor
   * @param {Object} [cliOptions] - Optional options.
   */
  constructor(cliOptions: Partial<StrykerOptions>) {
    LogConfigurator.configureMainProcess(cliOptions.logLevel, cliOptions.fileLogLevel, cliOptions.allowConsoleColors);
    this.injector = buildMainInjector(cliOptions);
    this.log = this.injector.resolve(commonTokens.getLogger)(Stryker.name);
    // Log level may have changed
    LogConfigurator.configureMainProcess(this.options.logLevel, this.options.fileLogLevel, this.options.allowConsoleColors);
  }

  public async runMutationTest(): Promise<MutantResult[]> {
    const loggingContext = await LogConfigurator.configureLoggingServer(
      this.options.logLevel,
      this.options.fileLogLevel,
      this.options.allowConsoleColors
    );
    this.timer.reset();
    const inputFiles = await this.injector.injectClass(InputFileResolver).resolve();
    if (inputFiles.files.length) {
      this.temporaryDirectory.initialize();
      const inputFileInjector = this.injector.provideValue(coreTokens.loggingContext, loggingContext).provideValue(coreTokens.inputFiles, inputFiles);
      const initialTestRunProcess = inputFileInjector
        .provideValue(commonTokens.produceSourceMaps, this.options.coverageAnalysis !== 'off')
        .provideFactory(coreTokens.pluginCreatorTranspiler, PluginCreator.createFactory(PluginKind.Transpiler))
        .provideClass(coreTokens.transpiler, TranspilerFacade)
        .injectClass(InitialTestExecutor);
      const initialRunResult = await initialTestRunProcess.run();
      const mutator = inputFileInjector.injectClass(MutatorFacade);
      const transpilerProvider = inputFileInjector
        .provideValue(coreTokens.initialRunResult, initialRunResult)
        .provideValue(commonTokens.produceSourceMaps, false)
        .provideFactory(coreTokens.transpiler, transpilerFactory);
      const transpiler = transpilerProvider.resolve(coreTokens.transpiler);
      const transpiledFiles = await transpiler.transpile(inputFiles.files);
      const mutationTestProcessInjector = transpilerProvider
        .provideValue(coreTokens.transpiledFiles, transpiledFiles)
        .provideClass(coreTokens.mutantTranspileScheduler, MutantTranspileScheduler)
        .provideClass(coreTokens.sandboxPool, SandboxPool);
      const testableMutants = await mutationTestProcessInjector
        .injectClass(MutantTestMatcher)
        .matchWithMutants(mutator.mutate(inputFiles.filesToMutate));
      try {
        if (initialRunResult.runResult.tests.length && testableMutants.length) {
          const mutationTestExecutor = mutationTestProcessInjector.injectClass(MutationTestExecutor);
          const mutantResults = await mutationTestExecutor.run(testableMutants);
          await this.reportScore(mutantResults, inputFileInjector);
          await this.logDone();
          return mutantResults;
        } else {
          this.logTraceLogLevelHint();
        }
      } finally {
        // `injector.dispose` calls `dispose` on all created instances
        // Namely the `SandboxPool`, `MutantTranspileScheduler` and `ChildProcessProxy` instances
        await mutationTestProcessInjector.dispose();
        await LogConfigurator.shutdown();
      }
    }
    return Promise.resolve([]);
  }

  private logDone() {
    this.log.info('Done in %s.', this.timer.humanReadableElapsed());
  }

  private logTraceLogLevelHint() {
    if (!this.log.isTraceEnabled()) {
      this.log.info('Trouble figuring out what went wrong? Try `npx stryker run --fileLogLevel trace --logLevel debug` to get some more info.');
    }
  }

  private async reportScore(mutantResults: MutantResult[], inputFileInjector: Injector<MainContext & { inputFiles: InputFileCollection }>) {
    inputFileInjector.injectClass(MutationTestReportCalculator).report(mutantResults);
    const calculator = this.injector.injectClass(ScoreResultCalculator);
    const score = calculator.calculate(mutantResults);
    this.reporter.onScoreCalculated(score);
    calculator.determineExitCode(score, this.options.thresholds);
    await this.reporter.wrapUp();
  }
}

import { Command } from 'commander';
import { getLogger } from 'log4js';
import { DashboardOptions, StrykerOptions, ALL_REPORT_TYPES } from '@stryker-mutator/api/core';
import { Config } from '@stryker-mutator/api/config';
import { Logger } from '@stryker-mutator/api/logging';

import { CONFIG_SYNTAX_HELP } from './config/ConfigReader';
import { initializerFactory } from './initializer';
import LogConfigurator from './logging/LogConfigurator';
import Stryker from './Stryker';

/**
 * Interpret a command line argument and add it to an object.
 * @param object The object to assign the value to.
 * @param key The property name under which the value needs to be stored.
 */
function deepOption<T extends string, R>(object: { [K in T]?: R }, key: T) {
  return (value: R) => {
    object[key] = value;
    return undefined;
  };
}

function list(val: string) {
  return val.split(',');
}

function parseBoolean(val: string) {
  const v = val.toLocaleLowerCase();
  return v !== 'false' && v !== '0';
}

export default class StrykerCli {
  private command: string = '';
  private strykerConfig: string | null = null;

  constructor(
    private readonly argv: string[],
    private readonly program: Command = new Command(),
    private readonly runMutationTest = (options: Partial<StrykerOptions>) => new Stryker(options).runMutationTest(),
    private readonly log: Logger = getLogger(StrykerCli.name)
  ) {}

  public run() {
    const dashboard: Partial<DashboardOptions> = {};
    const defaultValues = new Config();
    this.program
      .version(require('../package.json').version)
      .usage('<command> [options] [stryker.conf.js]')
      .description(
        `Possible commands:
        run: Run mutation testing
        init: Initialize Stryker for your project

    Optional location to the stryker.conf.js file as last argument. That file should export a function which accepts a "config" object\n${CONFIG_SYNTAX_HELP}`
      )
      .arguments('<command> [stryker.conf.js]')
      .action((cmd: string, config: string) => {
        this.command = cmd;
        this.strykerConfig = config;
      })
      .option(
        '-f, --files <allFiles>',
        `A comma separated list of globbing expression used for selecting all files needed to run the tests. For a more detailed way of selecting input files, please use a configFile.
      Example: node_modules/a-lib/**/*.js,src/**/*.js,!src/index.js,a.js,test/**/*.js`,
        list
      )
      .option(
        '-m, --mutate <filesToMutate>',
        `A comma separated list of globbing expression used for selecting the files that should be mutated.
      Example: src/**/*.js,a.js`,
        list
      )
      .option(
        '--coverageAnalysis <perTest|all|off>',
        `The coverage analysis strategy you want to use. Default value: "${defaultValues.coverageAnalysis}"`
      )
      .option('--testFramework <name>', 'The name of the test framework you want to use.')
      .option('--testRunner <name>', 'The name of the test runner you want to use')
      .option('--mutator <name>', 'The name of the mutant generator you want to use')
      .option('--transpilers <listOfTranspilers>', 'A comma separated list of transpilers to use.', list)
      .option('--reporters <name>', 'A comma separated list of the names of the reporter(s) you want to use', list)
      .option('--plugins <listOfPlugins>', 'A list of plugins you want stryker to load (`require`).', list)
      .option('--timeoutMS <number>', 'Tweak the absolute timeout used to wait for a test runner to complete', parseInt)
      .option('--timeoutFactor <number>', 'Tweak the standard deviation relative to the normal test run of a mutated test', parseFloat)
      .option('--maxConcurrentTestRunners <n>', 'Set the number of max concurrent test runner to spawn (default: cpuCount)', parseInt)
      .option(
        '--logLevel <level>',
        `Set the log level for the console. Possible values: fatal, error, warn, info, debug, trace, all and off. Default is "${defaultValues.logLevel}"`
      )
      .option(
        '--fileLogLevel <level>',
        `Set the log4js log level for the "stryker.log" file. Possible values: fatal, error, warn, info, debug, trace, all and off. Default is "${defaultValues.fileLogLevel}"`
      )
      .option('--allowConsoleColors <true/false>', 'Indicates whether or not Stryker should use colors in console.', parseBoolean, true)
      .option(
        '--dashboard.project <name>',
        'Indicates which project name to use if the "dashboard" reporter is enabled. Defaults to the git url configured in the environment of your CI server.',
        deepOption(dashboard, 'project')
      )
      .option(
        '--dashboard.version <version>',
        'Indicates which version to use if the "dashboard" reporter is enabled. Defaults to the branch name or tag name configured in the environment of your CI server.',
        deepOption(dashboard, 'version')
      )
      .option(
        '--dashboard.module <name>',
        'Indicates which module name to use if the "dashboard" reporter is enabled.',
        deepOption(dashboard, 'module')
      )
      .option(
        '--dashboard.baseUrl <url>',
        `Indicates which baseUrl to use when reporting to the stryker dashboard. Default: "${defaultValues.dashboard.baseUrl}"`,
        deepOption(dashboard, 'baseUrl')
      )
      .option(
        `--dashboard.reportType <${ALL_REPORT_TYPES.join('|')}>`,
        `Send a full report (inc. source code and mutant results) or only the mutation score. Default: ${defaultValues.dashboard.reportType}`,
        deepOption(dashboard, 'reportType')
      )
      .option(
        '--tempDirName <name>',
        'Set the name of the directory that is used by Stryker as a working directory. This directory will be cleaned after a successful run'
      )
      .parse(this.argv);

    // Earliest opportunity to configure the log level based on the logLevel argument
    LogConfigurator.configureMainProcess(this.program.logLevel);

    // Cleanup commander state
    delete this.program.options;
    delete this.program.rawArgs;
    delete this.program.args;
    delete this.program.Command;
    delete this.program.Option;
    delete this.program.commands;
    for (const i in this.program) {
      if (i.startsWith('_') || i.startsWith('dashboard.')) {
        delete this.program[i];
      }
    }

    if (this.strykerConfig) {
      this.program.configFile = this.strykerConfig;
    }
    this.program.dashboard = dashboard;

    const commands: { [cmd: string]: () => Promise<any> } = {
      init: () => initializerFactory().initialize(),
      run: () => this.runMutationTest(this.program)
    };

    if (Object.keys(commands).includes(this.command)) {
      commands[this.command]().catch(err => {
        this.log.error('an error occurred', err);
        if (!this.log.isTraceEnabled()) {
          this.log.info('Trouble figuring out what went wrong? Try `npx stryker run --fileLogLevel trace --logLevel debug` to get some more info.');
        }
        process.exitCode = 1;
        process.kill(process.pid, 'SIGINT');
      });
    } else {
      this.log.error('Unknown command: "%s", supported commands: [%s], or use `stryker --help`.', this.command, Object.keys(commands));
    }
  }
}

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, PluginKind, tokens } from '@stryker-mutator/api/plugin';
import { TestFramework } from '@stryker-mutator/api/test_framework';

import { coreTokens } from './di';
import { PluginCreator } from './di/PluginCreator';

export default class TestFrameworkOrchestrator {
  public static inject = tokens(commonTokens.logger, commonTokens.options, coreTokens.pluginCreatorTestFramework);
  constructor(
    private readonly log: Logger,
    private readonly options: StrykerOptions,
    private readonly pluginCreator: PluginCreator<PluginKind.TestFramework>
  ) {}

  public determineTestFramework(): TestFramework | null {
    if (this.options.coverageAnalysis !== 'perTest') {
      this.log.debug(
        'The `coverageAnalysis` setting is "%s", not hooking into the test framework to achieve performance benefits.',
        this.options.coverageAnalysis
      );
      return null;
    } else {
      return this.determineFrameworkWithCoverageAnalysis();
    }
  }

  private determineFrameworkWithCoverageAnalysis(): TestFramework | null {
    let testFramework: TestFramework | null = null;
    if (this.options.testFramework) {
      try {
        testFramework = this.pluginCreator.create(this.options.testFramework);
        this.log.debug(`Using testFramework ${this.options.testFramework} based on \`testFramework\` setting`);
      } catch (error) {
        this.log.warn(`Could not create test framework \`${this.options.testFramework}\``, error);
      }
    } else {
      this.log.warn('Missing config settings `testFramework`. Set `coverageAnalysis` option explicitly to "off" to ignore this warning.');
    }
    return testFramework;
  }
}

import { Location } from '@stryker-mutator/api/core';
import { Mutant } from '@stryker-mutator/api/mutant';
import { MutantResult, MutantStatus } from '@stryker-mutator/api/report';
import { TestSelection } from '@stryker-mutator/api/test_framework';
import { RunResult, TestResult } from '@stryker-mutator/api/test_runner';

import SourceFile, { isLineBreak } from './SourceFile';
import { freezeRecursively } from './utils/objectUtils';

export enum TestSelectionResult {
  Failed,
  FailedButAlreadyReported,
  Success
}

class TestFilter {
  public timeSpentScopedTests = 0;
  public runAllTests = false;
  public selectedTests: TestSelection[] = [];

  public selectAllTests(runResult: RunResult) {
    this.timeSpentScopedTests = runResult.tests.reduce((time, test) => time + test.timeSpentMs, this.timeSpentScopedTests);
    this.runAllTests = true;
  }

  public selectTest(testResult: TestResult, index: number) {
    this.selectedTests.push({ id: index, name: testResult.name });
    this.timeSpentScopedTests += testResult.timeSpentMs;
    this.runAllTests = false;
  }
}

export default class TestableMutant {
  public specsRan: string[] = [];
  private readonly filter = new TestFilter();
  private _location: Location;
  public testSelectionResult = TestSelectionResult.Success;

  public get selectedTests(): TestSelection[] {
    return this.filter.selectedTests;
  }

  public get runAllTests(): boolean {
    return this.filter.runAllTests;
  }

  public get timeSpentScopedTests() {
    return this.filter.timeSpentScopedTests;
  }

  public get fileName() {
    return this.mutant.fileName;
  }

  public get mutatorName() {
    return this.mutant.mutatorName;
  }

  public get range() {
    return this.mutant.range;
  }

  public get replacement() {
    return this.mutant.replacement;
  }

  public get location() {
    if (!this._location) {
      this._location = this.sourceFile.getLocation(this.range);
    }
    return this._location;
  }

  public get mutatedCode() {
    return this.sourceFile.content.substr(0, this.range[0]) + this.replacement + this.sourceFile.content.substr(this.range[1]);
  }

  public get originalCode() {
    return this.sourceFile.content;
  }

  public selectAllTests(runResult: RunResult, testSelectionResult: TestSelectionResult) {
    this.filter.selectAllTests(runResult);
    this.testSelectionResult = testSelectionResult;
  }

  public selectTest(testResult: TestResult, index: number) {
    this.filter.selectTest(testResult, index);
  }

  constructor(public readonly id: string, public mutant: Mutant, public sourceFile: SourceFile) {}

  public get originalLines() {
    const [startIndex, endIndex] = this.getMutationLineIndexes();
    return this.sourceFile.content.substring(startIndex, endIndex);
  }

  public get mutatedLines() {
    const [startIndex, endIndex] = this.getMutationLineIndexes();
    return (
      this.sourceFile.content.substring(startIndex, this.mutant.range[0]) +
      this.mutant.replacement +
      this.sourceFile.content.substring(this.mutant.range[1], endIndex)
    );
  }

  private getMutationLineIndexes() {
    let startIndexLines = this.mutant.range[0];
    let endIndexLines = this.mutant.range[1];
    while (startIndexLines > 0 && !isLineBreak(this.originalCode.charCodeAt(startIndexLines - 1))) {
      startIndexLines--;
    }
    while (endIndexLines < this.sourceFile.content.length && !isLineBreak(this.originalCode.charCodeAt(endIndexLines))) {
      endIndexLines++;
    }
    return [startIndexLines, endIndexLines];
  }

  public createResult(status: MutantStatus, testsRan: string[]): MutantResult {
    return freezeRecursively({
      id: this.id,
      location: this.location,
      mutatedLines: this.mutatedLines,
      mutatorName: this.mutatorName,
      originalLines: this.originalLines,
      range: this.range,
      replacement: this.replacement,
      sourceFilePath: this.fileName,
      status,
      testsRan
    });
  }

  public toString() {
    return `${this.mutant.mutatorName}: (${this.replacement}) file://${this.fileName}:${this.location.start.line + 1}:${this.location.start.column}`;
  }
}

import { StrykerError } from '@stryker-mutator/util';

export default class ChildProcessCrashedError extends StrykerError {
  constructor(public readonly pid: number, message: string, public readonly exitCode?: number, public readonly signal?: string, innerError?: Error) {
    super(message, innerError);
    Error.captureStackTrace(this, ChildProcessCrashedError);
    // TS recommendation: https://github.com/Microsoft/TypeScript/wiki/Breaking-Changes#extending-built-ins-like-error-array-and-map-may-no-longer-work
    Object.setPrototypeOf(this, ChildProcessCrashedError.prototype);
  }
}

import { ChildProcess, fork } from 'child_process';
import * as os from 'os';

import { File, StrykerOptions } from '@stryker-mutator/api/core';
import { OptionsContext } from '@stryker-mutator/api/plugin';
import { isErrnoException } from '@stryker-mutator/util';
import { getLogger } from 'log4js';
import { Disposable, InjectableClass, InjectionToken } from 'typed-inject';

import LoggingClientContext from '../logging/LoggingClientContext';
import { deserialize, kill, padLeft, serialize } from '../utils/objectUtils';
import StringBuilder from '../utils/StringBuilder';
import { ExpirableTask, Task } from '../utils/Task';

import ChildProcessCrashedError from './ChildProcessCrashedError';
import { autoStart, ParentMessage, ParentMessageKind, WorkerMessage, WorkerMessageKind } from './messageProtocol';
import OutOfMemoryError from './OutOfMemoryError';

type Func<TS extends any[], R> = (...args: TS) => R;

type PromisifiedFunc<TS extends any[], R> = (...args: TS) => Promise<R>;

export type Promisified<T> = {
  [K in keyof T]: T[K] extends PromisifiedFunc<any, any> ? T[K] : T[K] extends Func<infer TS, infer R> ? PromisifiedFunc<TS, R> : () => Promise<T[K]>;
};

const BROKEN_PIPE_ERROR_CODE = 'EPIPE';
const IPC_CHANNEL_CLOSED_ERROR_CODE = 'ERR_IPC_CHANNEL_CLOSED';
const TIMEOUT_FOR_DISPOSE = 2000;

export default class ChildProcessProxy<T> implements Disposable {
  public readonly proxy: Promisified<T>;

  private readonly worker: ChildProcess;
  private readonly initTask: Task;
  private disposeTask: ExpirableTask | undefined;
  private currentError: ChildProcessCrashedError | undefined;
  private readonly workerTasks: Task[] = [];
  private readonly log = getLogger(ChildProcessProxy.name);
  private readonly stdoutBuilder = new StringBuilder();
  private readonly stderrBuilder = new StringBuilder();
  private isDisposed = false;

  private constructor(
    requirePath: string,
    requireName: string,
    loggingContext: LoggingClientContext,
    options: StrykerOptions,
    additionalInjectableValues: unknown,
    workingDirectory: string
  ) {
    this.worker = fork(require.resolve('./ChildProcessProxyWorker'), [autoStart], { silent: true, execArgv: [] });
    this.initTask = new Task();
    this.log.debug('Starting %s in child process %s', requirePath, this.worker.pid);
    this.send({
      additionalInjectableValues,
      kind: WorkerMessageKind.Init,
      loggingContext,
      options,
      requireName,
      requirePath,
      workingDirectory
    });
    this.listenForMessages();
    this.listenToStdoutAndStderr();
    // Listen to `close`, not `exit`, see https://github.com/stryker-mutator/stryker/issues/1634
    this.worker.on('close', this.handleUnexpectedExit);
    this.worker.on('error', this.handleError);
    this.proxy = this.initProxy();
  }

  /**
   * @description Creates a proxy where each function of the object created using the constructorFunction arg is ran inside of a child process
   */
  public static create<TAdditionalContext, R, Tokens extends Array<InjectionToken<OptionsContext & TAdditionalContext>>>(
    requirePath: string,
    loggingContext: LoggingClientContext,
    options: StrykerOptions,
    additionalInjectableValues: TAdditionalContext,
    workingDirectory: string,
    InjectableClass: InjectableClass<TAdditionalContext & OptionsContext, R, Tokens>
  ): ChildProcessProxy<R> {
    return new ChildProcessProxy(requirePath, InjectableClass.name, loggingContext, options, additionalInjectableValues, workingDirectory);
  }

  private send(message: WorkerMessage) {
    this.worker.send(serialize(message, [File]));
  }

  private initProxy(): Promisified<T> {
    // This proxy is a genuine javascript `Proxy` class
    // More info: https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Proxy
    const self = this;
    return new Proxy({} as Promisified<T>, {
      get(_, propertyKey) {
        if (typeof propertyKey === 'string') {
          return self.forward(propertyKey);
        } else {
          return undefined;
        }
      }
    });
  }

  private forward(methodName: string) {
    return (...args: any[]) => {
      if (this.currentError) {
        return Promise.reject(this.currentError);
      } else {
        const workerTask = new Task<void>();
        const correlationId = this.workerTasks.push(workerTask) - 1;
        this.initTask.promise.then(() => {
          this.send({
            args,
            correlationId,
            kind: WorkerMessageKind.Call,
            methodName
          });
        });
        return workerTask.promise;
      }
    };
  }

  private listenForMessages() {
    this.worker.on('message', (serializedMessage: string) => {
      const message: ParentMessage = deserialize(serializedMessage, [File]);
      switch (message.kind) {
        case ParentMessageKind.Initialized:
          this.initTask.resolve(undefined);
          break;
        case ParentMessageKind.Result:
          this.workerTasks[message.correlationId].resolve(message.result);
          delete this.workerTasks[message.correlationId];
          break;
        case ParentMessageKind.Rejection:
          this.workerTasks[message.correlationId].reject(new Error(message.error));
          delete this.workerTasks[message.correlationId];
          break;
        case ParentMessageKind.DisposeCompleted:
          if (this.disposeTask) {
            this.disposeTask.resolve(undefined);
          }
          break;
        default:
          this.logUnidentifiedMessage(message);
          break;
      }
    });
  }

  private listenToStdoutAndStderr() {
    const handleData = (builder: StringBuilder) => (data: Buffer | string) => {
      const output = data.toString();
      builder.append(output);
      if (this.log.isTraceEnabled()) {
        this.log.trace(output);
      }
    };

    if (this.worker.stdout) {
      this.worker.stdout.on('data', handleData(this.stdoutBuilder));
    }

    if (this.worker.stderr) {
      this.worker.stderr.on('data', handleData(this.stderrBuilder));
    }
  }

  private reportError(error: Error) {
    this.workerTasks.filter(task => !task.isCompleted).forEach(task => task.reject(error));
  }

  private readonly handleUnexpectedExit = (code: number, signal: string) => {
    this.isDisposed = true;
    const output = StringBuilder.concat(this.stderrBuilder, this.stdoutBuilder);

    if (processOutOfMemory()) {
      this.currentError = new OutOfMemoryError(this.worker.pid, code);
      this.log.warn(`Child process [pid ${this.currentError.pid}] ran out of memory. Stdout and stderr are logged on debug level.`);
      this.log.debug(stdoutAndStderr());
    } else {
      this.currentError = new ChildProcessCrashedError(
        this.worker.pid,
        `Child process [pid ${this.worker.pid}] exited unexpectedly with exit code ${code} (${signal || 'without signal'}). ${stdoutAndStderr()}`,
        code,
        signal
      );
      this.log.warn(this.currentError.message, this.currentError);
    }

    this.reportError(this.currentError);

    function processOutOfMemory() {
      return output.includes('JavaScript heap out of memory');
    }

    function stdoutAndStderr() {
      if (output.length) {
        return `Last part of stdout and stderr was:${os.EOL}${padLeft(output)}`;
      } else {
        return 'Stdout and stderr were empty.';
      }
    }
  };

  private readonly handleError = (error: Error) => {
    if (this.innerProcessIsCrashed(error)) {
      this.log.warn(`Child process [pid ${this.worker.pid}] has crashed. See other warning messages for more info.`, error);
      this.reportError(
        new ChildProcessCrashedError(this.worker.pid, `Child process [pid ${this.worker.pid}] has crashed`, undefined, undefined, error)
      );
    } else {
      this.reportError(error);
    }
  };

  private innerProcessIsCrashed(error: Error) {
    return isErrnoException(error) && (error.code === BROKEN_PIPE_ERROR_CODE || error.code === IPC_CHANNEL_CLOSED_ERROR_CODE);
  }

  public async dispose(): Promise<void> {
    if (!this.isDisposed) {
      this.worker.removeListener('close', this.handleUnexpectedExit);
      this.isDisposed = true;
      this.log.debug('Disposing of worker process %s', this.worker.pid);
      this.disposeTask = new ExpirableTask(TIMEOUT_FOR_DISPOSE);
      this.send({ kind: WorkerMessageKind.Dispose });
      try {
        await this.disposeTask.promise;
      } finally {
        this.log.debug('Kill %s', this.worker.pid);
        await kill(this.worker.pid);
      }
    }
  }

  private logUnidentifiedMessage(message: never) {
    this.log.error(`Received unidentified message ${message}`);
  }
}

import * as path from 'path';

import { Config } from '@stryker-mutator/api/config';
import { File } from '@stryker-mutator/api/core';
import { errorToString } from '@stryker-mutator/util';
import { getLogger, Logger } from 'log4js';

import { buildChildProcessInjector } from '../di';
import LogConfigurator from '../logging/LogConfigurator';
import { deserialize, serialize } from '../utils/objectUtils';

import { autoStart, CallMessage, ParentMessage, ParentMessageKind, WorkerMessage, WorkerMessageKind } from './messageProtocol';

export default class ChildProcessProxyWorker {
  private log: Logger;

  public realSubject: any;

  constructor() {
    // Make sure to bind the methods in order to ensure the `this` pointer
    this.handleMessage = this.handleMessage.bind(this);
    process.on('message', this.handleMessage);
  }

  private send(value: ParentMessage) {
    if (process.send) {
      const str = serialize(value, [File]);
      process.send(str);
    }
  }
  private handleMessage(serializedMessage: string) {
    const message = deserialize<WorkerMessage>(serializedMessage, [File]);
    switch (message.kind) {
      case WorkerMessageKind.Init:
        LogConfigurator.configureChildProcess(message.loggingContext);
        this.log = getLogger(ChildProcessProxyWorker.name);
        this.handlePromiseRejections();
        let injector = buildChildProcessInjector((message.options as unknown) as Config);
        const locals = message.additionalInjectableValues as any;
        for (const token of Object.keys(locals)) {
          injector = injector.provideValue(token, locals[token]);
        }
        const RealSubjectClass = require(message.requirePath)[message.requireName];
        const workingDir = path.resolve(message.workingDirectory);
        if (process.cwd() !== workingDir) {
          this.log.debug(`Changing current working directory for this process to ${workingDir}`);
          process.chdir(workingDir);
        }
        this.realSubject = injector.injectClass(RealSubjectClass);
        this.send({ kind: ParentMessageKind.Initialized });
        this.removeAnyAdditionalMessageListeners(this.handleMessage);
        break;
      case WorkerMessageKind.Call:
        new Promise(resolve => resolve(this.doCall(message)))
          .then(result => {
            this.send({
              correlationId: message.correlationId,
              kind: ParentMessageKind.Result,
              result
            });
          })
          .catch(error => {
            this.send({
              correlationId: message.correlationId,
              error: errorToString(error),
              kind: ParentMessageKind.Rejection
            });
          });
        this.removeAnyAdditionalMessageListeners(this.handleMessage);
        break;
      case WorkerMessageKind.Dispose:
        const sendCompleted = () => {
          this.send({ kind: ParentMessageKind.DisposeCompleted });
        };
        LogConfigurator.shutdown()
          .then(sendCompleted)
          .catch(sendCompleted);
        break;
    }
  }

  private doCall(message: CallMessage): {} | PromiseLike<{}> | undefined {
    if (typeof this.realSubject[message.methodName] === 'function') {
      return this.realSubject[message.methodName](...message.args);
    } else {
      return this.realSubject[message.methodName];
    }
  }

  /**
   * Remove any addition message listeners that might me eavesdropping.
   * the @ngtools/webpack plugin listens to messages and throws an error whenever it could not handle a message
   * @see https://github.com/angular/angular-cli/blob/f776d3cf7982b64734c57fe4407434e9f4ec09f7/packages/%40ngtools/webpack/src/type_checker.ts#L79
   * @param exceptListener The listener that should remain
   */
  private removeAnyAdditionalMessageListeners(exceptListener: NodeJS.MessageListener) {
    process.listeners('message').forEach(listener => {
      if (listener !== exceptListener) {
        this.log.debug(
          "Removing an additional message listener, we don't want eavesdropping on our inter-process communication: %s",
          listener.toString()
        );
        process.removeListener('message', listener);
      }
    });
  }

  /**
   * During mutation testing, it's to be expected that promise rejections are not handled synchronously anymore (or not at all)
   * Let's handle those events so future versions of node don't crash
   * See issue 350: https://github.com/stryker-mutator/stryker/issues/350
   */
  private handlePromiseRejections() {
    const unhandledRejections: Array<Promise<void>> = [];
    process.on('unhandledRejection', (reason, promise) => {
      const unhandledPromiseId = unhandledRejections.push(promise);
      this.log.debug(`UnhandledPromiseRejectionWarning: Unhandled promise rejection (rejection id: ${unhandledPromiseId}): ${reason}`);
    });
    process.on('rejectionHandled', promise => {
      const unhandledPromiseId = unhandledRejections.indexOf(promise) + 1;
      this.log.debug(`PromiseRejectionHandledWarning: Promise rejection was handled asynchronously (rejection id: ${unhandledPromiseId})`);
    });
  }
}

// Prevent side effects for merely requiring the file
// Only actually start the child worker when it is requested
if (process.argv.includes(autoStart)) {
  new ChildProcessProxyWorker();
}

import ChildProcessCrashedError from './ChildProcessCrashedError';

export default class OutOfMemoryError extends ChildProcessCrashedError {
  constructor(pid: number, exitCode: number) {
    super(pid, `Process ${pid} ran out of memory`, exitCode);
    this.message = 'Process ';
    Error.captureStackTrace(this, OutOfMemoryError);
    // TS recommendation: https://github.com/Microsoft/TypeScript/wiki/Breaking-Changes#extending-built-ins-like-error-array-and-map-may-no-longer-work
    Object.setPrototypeOf(this, OutOfMemoryError.prototype);
  }
}

import { StrykerOptions } from '@stryker-mutator/api/core';

import LoggingClientContext from '../logging/LoggingClientContext';

export enum WorkerMessageKind {
  'Init',
  'Call',
  'Dispose'
}

export enum ParentMessageKind {
  'Initialized',
  'Result',
  'Rejection',
  'DisposeCompleted'
}

export type WorkerMessage = InitMessage | CallMessage | DisposeMessage;
export type ParentMessage = WorkResult | { kind: ParentMessageKind.Initialized | ParentMessageKind.DisposeCompleted } | RejectionResult;

// Make this an unlikely command line argument
// (prevents incidental start of child process)
export const autoStart = 'childProcessAutoStart12937129s7d';

export interface InitMessage {
  kind: WorkerMessageKind.Init;
  loggingContext: LoggingClientContext;
  options: StrykerOptions;
  workingDirectory: string;
  requireName: string;
  requirePath: string;
  additionalInjectableValues: unknown;
}

export interface DisposeMessage {
  kind: WorkerMessageKind.Dispose;
}

export interface WorkResult {
  kind: ParentMessageKind.Result;
  correlationId: number;
  result: any;
}

export interface RejectionResult {
  kind: ParentMessageKind.Rejection;
  correlationId: number;
  error: string;
}

export interface CallMessage {
  correlationId: number;
  kind: WorkerMessageKind.Call;
  args: any[];
  methodName: string;
}

