import * as fs from 'fs';
import * as path from 'path';

import { Config } from '@stryker-mutator/api/config';
import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { StrykerError } from '@stryker-mutator/util';

import { coreTokens } from '../di';

export const CONFIG_SYNTAX_HELP = '  module.exports = function(config) {\n' + '    config.set({\n' + '      // your config\n' + '    });\n' + '  };';

const DEFAULT_CONFIG_FILE = 'stryker.conf.js';

export default class ConfigReader {
  public static inject = tokens(coreTokens.cliOptions, commonTokens.logger);
  constructor(private readonly cliOptions: Partial<StrykerOptions>, private readonly log: Logger) {}

  public readConfig() {
    const configModule = this.loadConfigModule();
    const config = new Config();
    try {
      configModule(config);
    } catch (e) {
      throw new StrykerError('Error in config file!', e);
    }

    // merge the config from config file and cliOptions (precedence)
    config.set(this.cliOptions);
    if (this.log.isDebugEnabled()) {
      this.log.debug(`Loaded config: ${JSON.stringify(config, null, 2)}`);
    }

    this.fixDeprecations(config);

    return config;
  }
  private fixDeprecations(config: Config) {
    if (!(typeof config.mutator === 'string') && config.mutator.excludedMutations) {
      const newExcludedMutations: string[] = [];
      config.mutator.excludedMutations.forEach(legacyExcludedMutation =>
        newExcludedMutations.push(...this.getNewExcludedMutations(legacyExcludedMutation))
      );
      config.mutator.excludedMutations = newExcludedMutations;
    }
  }
  private getNewExcludedMutations(legacyExcludedMutation: string): string[] {
    const newMutations: string[] = [];
    switch (legacyExcludedMutation) {
      case 'ArrayLiteral':
      case 'ArrayNewExpression':
        newMutations.push('ArrayDeclaration');
        break;
      case 'BinaryExpression':
        newMutations.push(...['ArithmeticOperator', 'EqualityOperator', 'LogicalOperator']);
        break;
      case 'Block':
        newMutations.push('BlockStatement');
        break;
      case 'BooleanSubstitution':
        newMutations.push('BooleanLiteral');
        break;
      case 'DoStatement':
      case 'ForStatement':
      case 'IfStatement':
      case 'SwitchCase':
      case 'WhileStatement':
        newMutations.push('ConditionalExpression');
        break;
      case 'PrefixUnaryExpression':
        newMutations.push('UnaryOperator', 'UpdateOperator', 'BooleanLiteral');
        break;
      case 'PostfixUnaryExpression':
        newMutations.push('UpdateOperator');
        break;
      default:
        break;
    }

    if (newMutations.length > 0) {
      this.log.warn(
        `DEPRECATED: The mutation name "${legacyExcludedMutation}" is deprecated. Please migrate your config. For now ${legacyExcludedMutation} will be replaced with: ${newMutations.join(
          ', '
        )}. A list of mutations and their names can be found here: https://github.com/stryker-mutator/stryker-handbook/blob/master/mutator-types.md`
      );
    } else {
      newMutations.push(legacyExcludedMutation);
    }

    return newMutations;
  }

  private loadConfigModule(): Function {
    // Dummy module to be returned if no config file is loaded.
    let configModule: Function = () => {};

    if (!this.cliOptions.configFile) {
      try {
        fs.accessSync(path.resolve(`./${DEFAULT_CONFIG_FILE}`));
        this.log.info(`Using ${DEFAULT_CONFIG_FILE} in the current working directory.`);
        this.cliOptions.configFile = DEFAULT_CONFIG_FILE;
      } catch (e) {
        this.log.info('No config file specified. Running with command line arguments.');
        this.log.info('Use `stryker init` command to generate your config file.');
      }
    }

    if (this.cliOptions.configFile) {
      this.log.debug(`Loading config ${this.cliOptions.configFile}`);
      const configFileName = path.resolve(this.cliOptions.configFile);
      try {
        configModule = require(configFileName);
      } catch (e) {
        if (e.code === 'MODULE_NOT_FOUND' && e.message.indexOf(this.cliOptions.configFile) !== -1) {
          throw new StrykerError(`File ${configFileName} does not exist!`, e);
        } else {
          this.log.info('Stryker can help you setup a `stryker.conf` file for your project.');
          this.log.info("Please execute `stryker init` in your project's root directory.");
          throw new StrykerError('Invalid config file', e);
        }
      }
      if (typeof configModule !== 'function' && typeof configModule !== 'object') {
        this.log.fatal('Config file must be an object or export a function!\n' + CONFIG_SYNTAX_HELP);
        throw new StrykerError('Config file must export a function or be a JSON!');
      }
      if (typeof configModule === 'object') {
        return (config: any) => {
          config.set(configModule);
        };
      }
    }

    return configModule;
  }
}

import { Config } from '@stryker-mutator/api/config';
import { LogLevel, MutationScoreThresholds, StrykerOptions, ALL_REPORT_TYPES } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { TestFramework } from '@stryker-mutator/api/test_framework';
import { StrykerError } from '@stryker-mutator/util';

import { coreTokens } from '../di';

export default class ConfigValidator {
  private isValid = true;
  public static inject = tokens(commonTokens.logger, commonTokens.options, coreTokens.testFramework);
  constructor(
    private readonly log: Logger,
    private readonly options: Readonly<StrykerOptions>,
    private readonly testFramework: TestFramework | null
  ) {}

  public validate() {
    this.validateTestFramework();
    this.validateThresholds();
    this.validateMutator();
    this.validateLogLevel('logLevel');
    this.validateLogLevel('fileLogLevel');
    this.validateTimeout();
    this.validateIsNumber('maxConcurrentTestRunners', this.options.maxConcurrentTestRunners);
    this.validateIsStringArray('plugins', this.options.plugins);
    this.validateIsStringArray('reporters', this.options.reporters);
    this.validateIsStringArray('transpilers', this.options.transpilers);
    this.validateIsString('tempDirName', this.options.tempDirName);
    this.validateIsOptionalDeepString('dashboard', 'project');
    this.validateIsOptionalDeepString('dashboard', 'module');
    this.validateIsOptionalDeepString('dashboard', 'version');
    this.validateDeepEnum('dashboard', 'reportType', ALL_REPORT_TYPES);
    this.validateIsDeepString('dashboard', 'baseUrl');
    this.validateCoverageAnalysis();
    this.validateCoverageAnalysisWithRespectToTranspilers();
    this.crashIfNeeded();
  }

  private validateTestFramework() {
    if (this.options.coverageAnalysis === 'perTest' && !this.testFramework) {
      this.invalidate(
        'Configured coverage analysis "perTest" requires there to be a testFramework configured. Either configure a testFramework or set coverageAnalysis to "all" or "off".'
      );
    }
  }

  private validateMutator() {
    const mutator = this.options.mutator;
    if (typeof mutator === 'object') {
      const mutatorDescriptor = mutator;
      this.validateIsString('mutator.name', mutatorDescriptor.name);
      this.validateIsOptionalStringArray('mutator.excludedMutations', mutatorDescriptor.excludedMutations);
      this.validateIsOptionalArray('mutator.plugins', mutatorDescriptor.plugins);
    } else if (typeof mutator !== 'string') {
      this.invalidate(`Value ${stringify(mutator)} is invalid for \`mutator\`. Expected either a string or an object`);
    }
  }

  private validateThresholds() {
    const thresholds = this.options.thresholds;
    this.validateThresholdsValueExists('high', thresholds.high);
    this.validateThresholdsValueExists('low', thresholds.low);
    this.validateThresholdValue('high', thresholds.high);
    this.validateThresholdValue('low', thresholds.low);
    this.validateThresholdValue('break', thresholds.break);
    if (thresholds.high < thresholds.low) {
      this.invalidate(`\`thresholds.high\` is lower than \`thresholds.low\` (${thresholds.high} < ${thresholds.low})`);
    }
  }

  private validateThresholdValue(name: keyof MutationScoreThresholds, value: number | null) {
    if (typeof value === 'number' && (value < 0 || value > 100 || isNaN(value))) {
      this.invalidate(`Value ${stringify(value)} is invalid for \`thresholds.${name}\`. Expected a number between 0 and 100`);
    }
  }

  private validateThresholdsValueExists(name: keyof MutationScoreThresholds, value: any) {
    if (typeof value !== 'number') {
      this.invalidate(`Value ${stringify(value)} is invalid for \`thresholds.${name}\`. Expected a number between 0 and 100`);
    }
  }

  private validateLogLevel(logProperty: 'logLevel' | 'fileLogLevel') {
    const logLevel = this.options[logProperty];
    const VALID_LOG_LEVEL_VALUES = [
      LogLevel.Fatal,
      LogLevel.Error,
      LogLevel.Warning,
      LogLevel.Information,
      LogLevel.Debug,
      LogLevel.Trace,
      LogLevel.Off
    ];
    if (!VALID_LOG_LEVEL_VALUES.includes(logLevel)) {
      this.invalidate(
        `Value "${logLevel}" is invalid for \`${logProperty}\`. Expected one of the following: ${this.joinQuotedList(VALID_LOG_LEVEL_VALUES)}`
      );
    }
  }

  private validateTimeout() {
    this.validateIsNumber('timeoutMS', this.options.timeoutMS);
    this.validateIsNumber('timeoutFactor', this.options.timeoutFactor);
  }

  private validateCoverageAnalysis() {
    const VALID_COVERAGE_ANALYSIS_VALUES = ['perTest', 'all', 'off'];
    const coverageAnalysis = this.options.coverageAnalysis;
    if (!VALID_COVERAGE_ANALYSIS_VALUES.includes(coverageAnalysis)) {
      this.invalidate(
        `Value ${stringify(coverageAnalysis)} is invalid for \`coverageAnalysis\`. Expected one of the following: ${this.joinQuotedList(
          VALID_COVERAGE_ANALYSIS_VALUES
        )}`
      );
    }
  }

  private validateCoverageAnalysisWithRespectToTranspilers() {
    if (Array.isArray(this.options.transpilers) && this.options.transpilers.length > 1 && this.options.coverageAnalysis !== 'off') {
      this.invalidate(
        `Value "${
          this.options.coverageAnalysis
        }" for \`coverageAnalysis\` is invalid with multiple transpilers (configured transpilers: ${this.options.transpilers.join(
          ', '
        )}). Please report this to the Stryker team if you whish this feature to be implemented`
      );
    }
  }

  private crashIfNeeded() {
    if (!this.isValid) {
      throw new StrykerError('Stryker could not recover from this configuration error, see fatal log message(s) above.');
    }
  }

  private validateIsNumber(fieldName: keyof Config, value: any) {
    if (typeof value !== 'number' || isNaN(value)) {
      this.invalidate(`Value ${stringify(value)} is invalid for \`${fieldName}\`. Expected a number`);
    }
  }

  private validateIsString(fieldName: keyof Config, value: any) {
    if (typeof value !== 'string') {
      this.invalidate(`Value ${stringify(value)} is invalid for \`${fieldName}\`. Expected a string`);
    }
  }

  private validateIsDeepString<T extends keyof Config, T2 extends keyof Config[T]>(fieldName: T, secondFieldName: T2) {
    const value = this.options[fieldName][secondFieldName];
    if (typeof value !== 'string') {
      this.invalidate(`Value ${stringify(value)} is invalid for \`${fieldName}.${secondFieldName}\`. Expected a string`);
    }
  }

  private validateDeepEnum<T extends keyof Config, T2 extends keyof Config[T]>(
    fieldName: T,
    secondFieldName: T2,
    validValues: Config[T][T2] extends infer TValue ? readonly TValue[] : never
  ) {
    const value = this.options[fieldName][secondFieldName];
    if (!validValues.includes(value)) {
      this.invalidate(
        `Value ${stringify(value)} is invalid for \`${fieldName}.${secondFieldName}\`. Expected one of the following: ${validValues
          .map(stringify)
          .join(', ')}`
      );
    }
  }

  private validateIsOptionalDeepString<T extends keyof Config, T2 extends keyof Config[T]>(fieldName: T, secondFieldName: T2) {
    const value = this.options[fieldName][secondFieldName];
    if (typeof value !== 'string' && value !== undefined) {
      // Let my sibling handle the error formatting
      this.validateIsDeepString(fieldName, secondFieldName);
    }
  }

  private validateIsStringArray(fieldName: keyof Config, value: any) {
    if (!Array.isArray(value)) {
      this.invalidate(`Value ${stringify(value)} is invalid for \`${fieldName}\`. Expected an array`);
    } else {
      value.forEach(v => {
        if (typeof v !== 'string') {
          this.invalidate(`Value ${stringify(v)} is an invalid element of \`${fieldName}\`. Expected a string`);
        }
      });
    }
  }

  private validateIsArray(fieldName: keyof Config, value: unknown[]) {
    if (!Array.isArray(value)) {
      this.invalidate(`Value "${value}" is invalid for \`${fieldName}\`. Expected an array`);
    }
  }

  private validateIsOptionalStringArray(fieldName: keyof Config, value: string[] | undefined) {
    value === undefined || this.validateIsStringArray(fieldName, value);
  }

  private validateIsOptionalArray(fieldName: keyof Config, value: unknown[] | undefined | null) {
    value === undefined || value === null || this.validateIsArray(fieldName, value);
  }

  private invalidate(message: string) {
    this.log.fatal(message);
    this.isValid = false;
  }

  private joinQuotedList(arr: string[]) {
    return arr.map(v => `"${v}"`).join(', ');
  }
}

function stringify(value: unknown): string {
  if (typeof value === 'number' && isNaN(value)) {
    return 'NaN';
  } else {
    return JSON.stringify(value);
  }
}

import { tokens } from '@stryker-mutator/api/plugin';

import { coreTokens } from '../di';

import ConfigReader from './ConfigReader';

export function readConfig(configReader: ConfigReader) {
  return configReader.readConfig();
}

readConfig.inject = tokens(coreTokens.configReader);

import {
  ClassPlugin,
  commonTokens,
  FactoryPlugin,
  Plugin,
  PluginContexts,
  PluginInterfaces,
  PluginKind,
  PluginResolver,
  tokens
} from '@stryker-mutator/api/plugin';
import { InjectableFunctionWithInject, InjectionToken, Injector } from 'typed-inject';

export class PluginCreator<TPluginKind extends PluginKind> {
  private constructor(
    private readonly kind: TPluginKind,
    private readonly pluginResolver: PluginResolver,
    private readonly injector: Injector<PluginContexts[TPluginKind]>
  ) {}

  public create(name: string): PluginInterfaces[TPluginKind] {
    const plugin = this.pluginResolver.resolve(this.kind, name);
    if (this.isFactoryPlugin(plugin)) {
      return this.injector.injectFunction(plugin.factory);
    } else if (this.isClassPlugin(plugin)) {
      return this.injector.injectClass(plugin.injectableClass);
    } else {
      throw new Error(`Plugin "${this.kind}:${name}" could not be created, missing "factory" or "injectableClass" property.`);
    }
  }

  private isFactoryPlugin(plugin: Plugin<PluginKind>): plugin is FactoryPlugin<TPluginKind, Array<InjectionToken<PluginContexts[TPluginKind]>>> {
    return !!(plugin as FactoryPlugin<TPluginKind, Array<InjectionToken<PluginContexts[TPluginKind]>>>).factory;
  }
  private isClassPlugin(plugin: Plugin<PluginKind>): plugin is ClassPlugin<TPluginKind, Array<InjectionToken<PluginContexts[TPluginKind]>>> {
    return !!(plugin as ClassPlugin<TPluginKind, Array<InjectionToken<PluginContexts[TPluginKind]>>>).injectableClass;
  }

  public static createFactory<TPluginKind extends PluginKind, TContext extends PluginContexts[TPluginKind]>(
    kind: TPluginKind
  ): InjectableFunctionWithInject<TContext, PluginCreator<TPluginKind>, [typeof commonTokens.pluginResolver, typeof commonTokens.injector]> {
    function factory(pluginResolver: PluginResolver, injector: Injector<TContext>): PluginCreator<TPluginKind> {
      return new PluginCreator(kind, pluginResolver, injector);
    }
    factory.inject = tokens(commonTokens.pluginResolver, commonTokens.injector);
    return factory;
  }
}

import * as path from 'path';

import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, Plugin, PluginKind, PluginResolver, Plugins } from '@stryker-mutator/api/plugin';
import { fsAsPromised } from '@stryker-mutator/util';
import { tokens } from 'typed-inject';

import { importModule } from '../utils/fileUtils';

import * as coreTokens from './coreTokens';

const IGNORED_PACKAGES = ['core', 'api', 'util'];

interface PluginModule {
  strykerPlugins: Array<Plugin<any>>;
}

export class PluginLoader implements PluginResolver {
  private readonly pluginsByKind: Map<PluginKind, Array<Plugin<any>>> = new Map();

  public static inject = tokens(commonTokens.logger, coreTokens.pluginDescriptors);
  constructor(private readonly log: Logger, private readonly pluginDescriptors: readonly string[]) {}

  public load() {
    this.resolvePluginModules().forEach(moduleName => {
      this.requirePlugin(moduleName);
    });
  }

  public resolve<T extends keyof Plugins>(kind: T, name: string): Plugins[T] {
    const plugins = this.pluginsByKind.get(kind);
    if (plugins) {
      const plugin = plugins.find(plugin => plugin.name.toLowerCase() === name.toLowerCase());
      if (plugin) {
        return plugin as any;
      } else {
        throw new Error(
          `Cannot load ${kind} plugin "${name}". Did you forget to install it? Loaded ${kind} plugins were: ${plugins.map(p => p.name).join(', ')}`
        );
      }
    } else {
      throw new Error(`Cannot load ${kind} plugin "${name}". In fact, no ${kind} plugins were loaded. Did you forget to install it?`);
    }
  }

  public resolveAll<T extends keyof Plugins>(kind: T): Array<Plugins[T]> {
    const plugins = this.pluginsByKind.get(kind);
    return plugins || ([] as any);
  }

  private resolvePluginModules() {
    const modules: string[] = [];
    this.pluginDescriptors.forEach(pluginExpression => {
      if (typeof pluginExpression === 'string') {
        if (pluginExpression.includes('*')) {
          // Plugin directory is the node_modules folder of the module that installed stryker
          // So if current __dirname is './@stryker-mutator/core/src/di' so 4 directories above
          const pluginDirectory = path.dirname(path.resolve(__dirname, '..', '..', '..', '..', pluginExpression));
          const regexp = new RegExp('^' + path.basename(pluginExpression).replace('*', '.*'));

          this.log.debug('Loading %s from %s', pluginExpression, pluginDirectory);
          const plugins = fsAsPromised
            .readdirSync(pluginDirectory)
            .filter(pluginName => !IGNORED_PACKAGES.includes(pluginName) && regexp.test(pluginName))
            .map(pluginName => path.resolve(pluginDirectory, pluginName));
          if (plugins.length === 0) {
            this.log.debug('Expression %s not resulted in plugins to load', pluginExpression);
          }
          plugins
            .map(plugin => {
              this.log.debug('Loading plugin "%s" (matched with expression %s)', plugin, pluginExpression);
              return plugin;
            })
            .forEach(p => modules.push(p));
        } else {
          modules.push(pluginExpression);
        }
      } else {
        this.log.warn('Ignoring plugin %s, as its not a string type', pluginExpression);
      }
    });

    return modules;
  }

  private requirePlugin(name: string) {
    this.log.debug(`Loading plugins ${name}`);
    try {
      const module = importModule(name);
      if (this.isPluginModule(module)) {
        module.strykerPlugins.forEach(plugin => this.loadPlugin(plugin));
      }
    } catch (e) {
      if (e.code === 'MODULE_NOT_FOUND' && e.message.indexOf(name) !== -1) {
        this.log.warn('Cannot find plugin "%s".\n  Did you forget to install it ?\n' + '  npm install %s --save-dev', name, name);
      } else {
        this.log.warn('Error during loading "%s" plugin:\n  %s', name, e.message);
      }
    }
  }

  private loadPlugin(plugin: Plugin<any>) {
    let plugins = this.pluginsByKind.get(plugin.kind);
    if (!plugins) {
      plugins = [];
      this.pluginsByKind.set(plugin.kind, plugins);
    }
    plugins.push(plugin);
  }

  private isPluginModule(module: unknown): module is PluginModule {
    const pluginModule = module as PluginModule;
    return pluginModule && pluginModule.strykerPlugins && Array.isArray(pluginModule.strykerPlugins);
  }
}

