import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { errorToString } from '@stryker-mutator/util';
import { IRestResponse, RestClient } from 'typed-rest-client/RestClient';

import { PackageInfo } from './PackageInfo';
import PromptOption from './PromptOption';

import { initializerTokens } from '.';

interface NpmSearchResult {
  total: number;
  results: Array<{ package: PackageInfo }>;
}

interface NpmPackage {
  name: string;
  initStrykerConfig?: object;
}

const getName = (packageName: string) => {
  return packageName
    .replace('@stryker-mutator/', '')
    .replace('stryker-', '')
    .split('-')[0];
};

const mapSearchResultToPromptOption = (searchResults: NpmSearchResult): PromptOption[] =>
  searchResults.results.map(result => ({
    name: getName(result.package.name),
    pkg: result.package
  }));

const handleResult = (from: string) => <T>(response: IRestResponse<T>): T => {
  if (response.statusCode === 200 && response.result) {
    return response.result;
  } else {
    throw new Error(`Path ${from} resulted in http status code: ${response.statusCode}.`);
  }
};

export default class NpmClient {
  public static inject = tokens(commonTokens.logger, initializerTokens.restClientNpmSearch, initializerTokens.restClientNpm);
  constructor(private readonly log: Logger, private readonly searchClient: RestClient, private readonly packageClient: RestClient) {}

  public getTestRunnerOptions(): Promise<PromptOption[]> {
    return this.search('/v2/search?q=keywords:@stryker-mutator/test-runner-plugin').then(mapSearchResultToPromptOption);
  }

  public getTestFrameworkOptions(testRunnerFilter: string | null): Promise<PromptOption[]> {
    return this.search('/v2/search?q=keywords:@stryker-mutator/test-framework-plugin')
      .then(searchResult => {
        if (testRunnerFilter) {
          searchResult.results = searchResult.results.filter(framework => framework.package.keywords.includes(testRunnerFilter));
        }
        return searchResult;
      })
      .then(mapSearchResultToPromptOption);
  }

  public getMutatorOptions(): Promise<PromptOption[]> {
    return this.search('/v2/search?q=keywords:@stryker-mutator/mutator-plugin').then(mapSearchResultToPromptOption);
  }

  public getTranspilerOptions(): Promise<PromptOption[]> {
    return this.search('/v2/search?q=keywords:@stryker-mutator/transpiler-plugin').then(mapSearchResultToPromptOption);
  }

  public getTestReporterOptions(): Promise<PromptOption[]> {
    return this.search('/v2/search?q=keywords:@stryker-mutator/reporter-plugin').then(mapSearchResultToPromptOption);
  }

  public getAdditionalConfig(pkg: PackageInfo): Promise<object> {
    const path = `/${pkg.name}@${pkg.version}/package.json`;
    return this.packageClient
      .get<NpmPackage>(path)
      .then(handleResult(path))
      .then(pkg => pkg.initStrykerConfig || {})
      .catch(err => {
        this.log.warn(`Could not fetch additional initialization config for dependency ${pkg.name}. You might need to configure it manually`, err);
        return {};
      });
  }

  private search(path: string): Promise<NpmSearchResult> {
    this.log.debug(`Searching: ${path}`);
    return this.searchClient
      .get<NpmSearchResult>(path)
      .then(handleResult(path))
      .catch(err => {
        this.log.error(`Unable to reach npms.io (for query ${path}). Please check your internet connection.`, errorToString(err));
        const result: NpmSearchResult = {
          results: [],
          total: 0
        };
        return result;
      });
  }
}

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { childProcessAsPromised, fsAsPromised } from '@stryker-mutator/util';

import PresetConfiguration from './presets/PresetConfiguration';
import PromptOption from './PromptOption';

import { initializerTokens } from '.';

const STRYKER_CONFIG_FILE = 'stryker.conf.js';

export default class StrykerConfigWriter {
  public static inject = tokens(commonTokens.logger, initializerTokens.out);
  constructor(private readonly log: Logger, private readonly out: typeof console.log) {}

  public guardForExistingConfig() {
    if (fsAsPromised.existsSync(STRYKER_CONFIG_FILE)) {
      const msg = 'Stryker config file "stryker.conf.js" already exists in the current directory. Please remove it and try again.';
      this.log.error(msg);
      throw new Error(msg);
    }
  }

  /**
   * Create stryker.conf.js based on the chosen framework and test runner
   * @function
   */
  public write(
    selectedTestRunner: null | PromptOption,
    selectedTestFramework: null | PromptOption,
    selectedMutator: null | PromptOption,
    selectedTranspilers: null | PromptOption[],
    selectedReporters: PromptOption[],
    selectedPackageManager: PromptOption,
    additionalPiecesOfConfig: Array<Partial<StrykerOptions>>
  ): Promise<void> {
    const configObject: Partial<StrykerOptions> = {
      mutator: selectedMutator ? selectedMutator.name : '',
      packageManager: selectedPackageManager.name,
      reporters: selectedReporters.map(rep => rep.name),
      testRunner: selectedTestRunner ? selectedTestRunner.name : '',
      transpilers: selectedTranspilers ? selectedTranspilers.map(t => t.name) : []
    };

    this.configureTestFramework(configObject, selectedTestFramework);
    Object.assign(configObject, ...additionalPiecesOfConfig);
    return this.writeStrykerConfig(configObject);
  }

  /**
   * Create stryker.conf.js based on the chosen preset
   * @function
   */
  public async writePreset(presetConfig: PresetConfiguration) {
    return this.writeStrykerConfigRaw(
      presetConfig.config,
      `// This config was generated using a preset.
    // Please see the handbook for more information: ${presetConfig.handbookUrl}`
    );
  }

  private configureTestFramework(configObject: Partial<StrykerOptions>, selectedTestFramework: null | PromptOption) {
    if (selectedTestFramework) {
      configObject.testFramework = selectedTestFramework.name;
      configObject.coverageAnalysis = 'perTest';
    } else {
      configObject.coverageAnalysis = 'all';
    }
  }

  private async writeStrykerConfigRaw(rawConfig: string, rawHeader = '') {
    this.out('Writing & formatting stryker.conf.js...');
    const formattedConf = `${rawHeader}
      module.exports = function(config){
        config.set(
          ${rawConfig}
        );
      }`;
    await fsAsPromised.writeFile(STRYKER_CONFIG_FILE, formattedConf);
    try {
      await childProcessAsPromised.exec(`npx prettier --write ${STRYKER_CONFIG_FILE}`);
    } catch (error) {
      this.log.debug('Prettier exited with error', error);
      this.out('Unable to format stryker.conf.js file for you. This is not a big problem, but it might look a bit messy .');
    }
  }

  private writeStrykerConfig(configObject: Partial<StrykerOptions>) {
    return this.writeStrykerConfigRaw(this.wrapInModule(configObject));
  }

  private wrapInModule(configObject: Partial<StrykerOptions>): string {
    return JSON.stringify(configObject, null, 2);
  }
}

import * as child from 'child_process';

import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { Logger } from '@stryker-mutator/api/logging';

import CommandTestRunner from '../test-runner/CommandTestRunner';
import { filterEmpty } from '../utils/objectUtils';

import NpmClient from './NpmClient';
import { PackageInfo } from './PackageInfo';
import Preset from './presets/Preset';
import PromptOption from './PromptOption';
import StrykerConfigWriter from './StrykerConfigWriter';
import { StrykerInquirer } from './StrykerInquirer';

import { initializerTokens } from '.';

const enum PackageManager {
  Npm = 'npm',
  Yarn = 'yarn'
}

export default class StrykerInitializer {
  public static inject = tokens(
    commonTokens.logger,
    initializerTokens.out,
    initializerTokens.npmClient,
    initializerTokens.strykerPresets,
    initializerTokens.configWriter,
    initializerTokens.inquirer
  );
  constructor(
    private readonly log: Logger,
    private readonly out: typeof console.log,
    private readonly client: NpmClient,
    private readonly strykerPresets: Preset[],
    private readonly configWriter: StrykerConfigWriter,
    private readonly inquirer: StrykerInquirer
  ) {}

  /**
   * Runs the initializer will prompt the user for questions about his setup. After that, install plugins and configure Stryker.
   * @function
   */
  public async initialize(): Promise<void> {
    this.configWriter.guardForExistingConfig();
    this.patchProxies();
    const selectedPreset = await this.selectPreset();
    if (selectedPreset) {
      await this.initiatePreset(this.configWriter, selectedPreset);
    } else {
      await this.initiateCustom(this.configWriter);
    }
    this.out('Done configuring stryker. Please review `stryker.conf.js`, you might need to configure transpilers or your test runner correctly.');
    this.out("Let's kill some mutants with this command: `stryker run`");
    this.out('Note: Stryker will use `.stryker-temp` as location for temporary files. Be sure to add it to your ignored files in source control.');
  }

  /**
   * The typed rest client works only with the specific HTTP_PROXY and HTTPS_PROXY env settings.
   * Let's make sure they are available.
   */
  private patchProxies() {
    const copyEnvVariable = (from: string, to: string) => {
      if (process.env[from] && !process.env[to]) {
        process.env[to] = process.env[from];
      }
    };
    copyEnvVariable('http_proxy', 'HTTP_PROXY');
    copyEnvVariable('https_proxy', 'HTTPS_PROXY');
  }

  private async selectPreset(): Promise<Preset | undefined> {
    const presetOptions: Preset[] = this.strykerPresets;
    if (presetOptions.length) {
      this.log.debug(`Found presets: ${JSON.stringify(presetOptions)}`);
      return this.inquirer.promptPresets(presetOptions);
    } else {
      this.log.debug('No presets have been configured, reverting to custom configuration');
      return undefined;
    }
  }

  private async initiatePreset(configWriter: StrykerConfigWriter, selectedPreset: Preset) {
    const presetConfig = await selectedPreset.createConfig();
    await configWriter.writePreset(presetConfig);
    const selectedPackageManager = await this.selectPackageManager();
    this.installNpmDependencies(presetConfig.dependencies, selectedPackageManager);
  }

  private async initiateCustom(configWriter: StrykerConfigWriter) {
    const selectedTestRunner = await this.selectTestRunner();
    const selectedTestFramework =
      selectedTestRunner && !CommandTestRunner.is(selectedTestRunner.name) ? await this.selectTestFramework(selectedTestRunner) : null;
    const selectedMutator = await this.selectMutator();
    const selectedTranspilers = await this.selectTranspilers();
    const selectedReporters = await this.selectReporters();
    const selectedPackageManager = await this.selectPackageManager();
    const npmDependencies = this.getSelectedNpmDependencies(
      [selectedTestRunner, selectedTestFramework, selectedMutator].concat(selectedTranspilers).concat(selectedReporters)
    );
    await configWriter.write(
      selectedTestRunner,
      selectedTestFramework,
      selectedMutator,
      selectedTranspilers,
      selectedReporters,
      selectedPackageManager,
      await this.fetchAdditionalConfig(npmDependencies)
    );
    this.installNpmDependencies(
      npmDependencies.map(pkg => pkg.name),
      selectedPackageManager
    );
  }

  private async selectTestRunner(): Promise<PromptOption | null> {
    const testRunnerOptions = await this.client.getTestRunnerOptions();
    if (testRunnerOptions.length) {
      this.log.debug(`Found test runners: ${JSON.stringify(testRunnerOptions)}`);
      return this.inquirer.promptTestRunners(testRunnerOptions);
    } else {
      this.out('Unable to select a test runner. You will need to configure it manually.');
      return null;
    }
  }

  private async selectReporters(): Promise<PromptOption[]> {
    let reporterOptions: PromptOption[];
    reporterOptions = await this.client.getTestReporterOptions();
    reporterOptions.push(
      {
        name: 'clear-text',
        pkg: null
      },
      {
        name: 'progress',
        pkg: null
      },
      {
        name: 'dashboard',
        pkg: null
      }
    );
    return this.inquirer.promptReporters(reporterOptions);
  }

  private async selectTestFramework(testRunnerOption: PromptOption): Promise<null | PromptOption> {
    let selectedTestFramework: PromptOption | null = null;
    const testFrameworkOptions = await this.client.getTestFrameworkOptions(testRunnerOption.pkg ? testRunnerOption.pkg.name : null);
    if (testFrameworkOptions.length) {
      this.log.debug(`Found test frameworks for ${testRunnerOption.name}: ${JSON.stringify(testFrameworkOptions)}`);
      const none: PromptOption = {
        name: 'None/other',
        pkg: null
      };
      testFrameworkOptions.push(none);
      selectedTestFramework = await this.inquirer.promptTestFrameworks(testFrameworkOptions);
      if (selectedTestFramework === none) {
        selectedTestFramework = null;
        this.out('OK, downgrading coverageAnalysis to "all"');
      }
    } else {
      this.out(`No stryker test framework plugin found that is compatible with ${testRunnerOption.name}, downgrading coverageAnalysis to "all"`);
    }
    return selectedTestFramework;
  }

  private async selectMutator(): Promise<PromptOption | null> {
    const mutatorOptions = await this.client.getMutatorOptions();
    if (mutatorOptions.length) {
      this.log.debug(`Found mutators: ${JSON.stringify(mutatorOptions)}`);
      return this.inquirer.promptMutator(mutatorOptions);
    } else {
      this.out('Unable to select a mutator. You will need to configure it manually.');
      return null;
    }
  }

  private async selectTranspilers(): Promise<PromptOption[] | null> {
    const options = await this.client.getTranspilerOptions();
    if (options.length) {
      this.log.debug(`Found transpilers: ${JSON.stringify(options)}`);
      return this.inquirer.promptTranspilers(options);
    } else {
      this.out('Unable to select transpilers. You will need to configure it manually, if you want to use any.');
      return null;
    }
  }

  private async selectPackageManager(): Promise<PromptOption> {
    return this.inquirer.promptPackageManager([
      {
        name: PackageManager.Npm,
        pkg: null
      },
      {
        name: PackageManager.Yarn,
        pkg: null
      }
    ]);
  }

  private getSelectedNpmDependencies(selectedOptions: Array<PromptOption | null>): PackageInfo[] {
    return filterEmpty(filterEmpty(selectedOptions).map(option => option.pkg));
  }

  /**
   * Install the npm packages
   * @function
   */
  private installNpmDependencies(dependencies: string[], selectedOption: PromptOption): void {
    if (dependencies.length === 0) {
      return;
    }

    const dependencyArg = dependencies.join(' ');
    this.out('Installing NPM dependencies...');
    const cmd = selectedOption.name === PackageManager.Npm ? `npm i --save-dev ${dependencyArg}` : `yarn add ${dependencyArg} --dev`;
    this.out(cmd);
    try {
      child.execSync(cmd, { stdio: [0, 1, 2] });
    } catch (_) {
      this.out(`An error occurred during installation, please try it yourself: "${cmd}"`);
    }
  }

  private async fetchAdditionalConfig(dependencies: PackageInfo[]): Promise<object[]> {
    return filterEmpty(await Promise.all(dependencies.map(dep => this.client.getAdditionalConfig(dep))));
  }
}

import * as inquirer from 'inquirer';

import CommandTestRunner from '../test-runner/CommandTestRunner';

import Preset from './presets/Preset';
import PromptOption from './PromptOption';

export interface PromptResult {
  additionalNpmDependencies: string[];
  additionalConfig: object;
}

export class StrykerInquirer {
  public async promptPresets(options: Preset[]): Promise<Preset | undefined> {
    const choices: Array<inquirer.ChoiceType<string>> = options.map(_ => _.name);
    choices.push(new inquirer.Separator());
    choices.push('None/other');
    const answers = await inquirer.prompt<{ preset: string }>({
      choices,
      message: 'Are you using one of these frameworks? Then select a preset configuration.',
      name: 'preset',
      type: 'list'
    });
    return options.find(_ => _.name === answers.preset);
  }

  public async promptTestRunners(options: PromptOption[]): Promise<PromptOption> {
    const choices: Array<inquirer.ChoiceType<string>> = options.map(_ => _.name);
    choices.push(new inquirer.Separator());
    choices.push(CommandTestRunner.runnerName);
    const answers = await inquirer.prompt<{ testRunner: string }>({
      choices,
      default: 'Mocha',
      message:
        'Which test runner do you want to use? If your test runner isn\'t listed here, you can choose "command" (it uses your `npm test` command, but will come with a big performance penalty)',
      name: 'testRunner',
      type: 'list'
    });
    return options.filter(_ => _.name === answers.testRunner)[0] || { name: CommandTestRunner.runnerName, pkg: null };
  }

  public async promptTestFrameworks(options: PromptOption[]): Promise<PromptOption> {
    const answers = await inquirer.prompt<{ testFramework: string }>({
      choices: options.map(_ => _.name),
      message: 'Which test framework do you want to use?',
      name: 'testFramework',
      type: 'list'
    });
    return options.filter(_ => _.name === answers.testFramework)[0];
  }

  public async promptMutator(options: PromptOption[]): Promise<PromptOption> {
    const answers = await inquirer.prompt<{ mutator: string }>({
      choices: options.map(_ => _.name),
      message: 'What kind of code do you want to mutate?',
      name: 'mutator',
      type: 'list'
    });
    return options.filter(_ => _.name === answers.mutator)[0];
  }

  public async promptTranspilers(options: PromptOption[]): Promise<PromptOption[]> {
    const answers = await inquirer.prompt<{ transpilers: string[] }>({
      choices: options.map(_ => _.name),
      message: '[optional] What kind transformations should be applied to your code?',
      name: 'transpilers',
      type: 'checkbox'
    });
    return options.filter(option => answers.transpilers.some(transpilerName => option.name === transpilerName));
  }

  public async promptReporters(options: PromptOption[]): Promise<PromptOption[]> {
    const answers = await inquirer.prompt<{ reporters: string[] }>({
      choices: options.map(_ => _.name),
      default: ['clear-text', 'progress'],
      message: 'Which reporter(s) do you want to use?',
      name: 'reporters',
      type: 'checkbox'
    });
    return options.filter(option => answers.reporters.some(reporterName => option.name === reporterName));
  }

  public async promptPackageManager(options: PromptOption[]): Promise<PromptOption> {
    const answers = await inquirer.prompt<{ packageManager: string }>({
      choices: options.map(_ => _.name),
      default: ['npm'],
      message: 'Which package manager do you want to use?',
      name: 'packageManager',
      type: 'list'
    });
    return options.filter(_ => _.name === answers.packageManager)[0];
  }
}

