export default class InputFileCollection {
  public readonly files: readonly File[];
  public readonly filesToMutate: readonly File[];

  constructor(files: readonly File[], mutateGlobResult: readonly string[]) {
    this.files = files;
    this.filesToMutate = files.filter(file => mutateGlobResult.some(name => name === file.name));
  }

  public logFiles(log: Logger) {
    if (!this.files.length) {
      log.warn(
        `No files selected. Please make sure you either${os.EOL}` +
          ` (1) Run Stryker inside a Git repository; or${os.EOL}` +
          ' (2) Specify the `files` property in your Stryker configuration (`--files via command line`).'
      );
    } else {
      if (this.filesToMutate.length) {
        log.info(`Found ${this.filesToMutate.length} of ${this.files.length} file(s) to be mutated.`);
      } else {
        log.warn(
          normalizeWhitespaces(`No files marked to be mutated, Stryker will perform a dry-run without actually mutating anything.
        You can configure the \`mutate\` property in your stryker.conf.js file (or use \`--mutate\` via command line).`)
        );
      }
      if (log.isDebugEnabled()) {
        log.debug(
          `All input files: ${JSON.stringify(
            this.files.map(file => file.name),
            null,
            2
          )}`
        );
        log.debug(
          `Files to mutate: ${JSON.stringify(
            this.filesToMutate.map(file => file.name),
            null,
            2
          )}`
        );
      }
    }
  }
}

import * as path from 'path';

import { Config } from '@stryker-mutator/api/config';
import { File, StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { SourceFile } from '@stryker-mutator/api/report';
import { childProcessAsPromised, fsAsPromised, isErrnoException, normalizeWhitespaces, StrykerError } from '@stryker-mutator/util';

import { coreTokens } from '../di';
import StrictReporter from '../reporters/StrictReporter';
import { glob } from '../utils/fileUtils';
import { filterEmpty } from '../utils/objectUtils';

import InputFileCollection from './InputFileCollection';

function toReportSourceFile(file: File): SourceFile {
  return {
    content: file.textContent,
    path: file.name
  };
}

const IGNORE_PATTERN_CHARACTER = '!';

export default class InputFileResolver {
  private readonly mutatePatterns: readonly string[];
  private readonly filePatterns: readonly string[] | undefined;
  private readonly tempDirName: string;

  public static inject = tokens(commonTokens.logger, commonTokens.options, coreTokens.reporter);
  constructor(private readonly log: Logger, { mutate, files, tempDirName }: StrykerOptions, private readonly reporter: StrictReporter) {
    this.tempDirName = tempDirName;
    this.mutatePatterns = mutate || [];
    if (files) {
      this.filePatterns = files;
    }
  }

  public async resolve(): Promise<InputFileCollection> {
    const [inputFileNames, mutateFiles] = await Promise.all([this.resolveInputFiles(), this.resolveMutateFiles()]);
    const files: File[] = await this.readFiles(inputFileNames);
    const inputFileCollection = new InputFileCollection(files, mutateFiles);
    this.reportAllSourceFilesRead(files);
    inputFileCollection.logFiles(this.log);
    return inputFileCollection;
  }

  private resolveInputFiles() {
    if (this.filePatterns) {
      return this.expand(this.filePatterns);
    } else {
      return this.resolveFilesUsingGit();
    }
  }

  private resolveMutateFiles() {
    return this.expand(this.mutatePatterns, !shallowEquals(this.mutatePatterns, new Config().mutate));

    function shallowEquals(arr1: readonly string[], arr2: readonly string[]): boolean {
      if (arr1.length !== arr2.length) {
        return false;
      } else {
        for (let i = 0; i < arr1.length; i++) {
          if (arr1[i] !== arr2[i]) {
            return false;
          }
        }
        return true;
      }
    }
  }

  /**
   * Takes a list of globbing patterns and expands them into files.
   * If a patterns starts with a `!`, it negates the pattern.
   * @param patterns The patterns to expand into files
   */
  private async expand(patterns: readonly string[], logAboutUselessPatterns = true): Promise<string[]> {
    const fileSet = new Set<string>();
    for (const pattern of patterns) {
      if (pattern.startsWith(IGNORE_PATTERN_CHARACTER)) {
        const files = await this.expandPattern(pattern.substr(1), logAboutUselessPatterns);
        files.forEach(fileName => fileSet.delete(fileName));
      } else {
        const files = await this.expandPattern(pattern, logAboutUselessPatterns);
        files.forEach(fileName => fileSet.add(fileName));
      }
    }
    return Array.from(fileSet);
  }

  private async expandPattern(globbingExpression: string, logAboutUselessPatterns: boolean): Promise<string[]> {
    const fileNames = (await glob(globbingExpression)).map(relativeFile => path.resolve(relativeFile));
    if (!fileNames.length && logAboutUselessPatterns) {
      this.log.warn(`Globbing expression "${globbingExpression}" did not result in any files.`);
    }
    return fileNames;
  }

  private async resolveFilesUsingGit(): Promise<string[]> {
    try {
      const { stdout } = await childProcessAsPromised.exec(`git ls-files --others --exclude-standard --cached --exclude /${this.tempDirName}/*`, {
        maxBuffer: 10 * 1000 * 1024
      });
      const fileNames = stdout
        .toString()
        .split('\n')
        .map(line => line.trim())
        .filter(line => line) // remove empty lines
        .map(relativeFileName => path.resolve(relativeFileName));
      return fileNames;
    } catch (error) {
      throw new StrykerError(
        normalizeWhitespaces(
          `Cannot determine input files. Either specify a \`files\`
        array in your stryker configuration, or make sure "${process.cwd()}"
        is located inside a git repository`
        ),
        error
      );
    }
  }

  private reportAllSourceFilesRead(allFiles: File[]) {
    this.reporter.onAllSourceFilesRead(allFiles.map(toReportSourceFile));
  }

  private reportSourceFilesRead(textFile: File) {
    this.reporter.onSourceFileRead(toReportSourceFile(textFile));
  }

  private readFiles(files: string[]): Promise<File[]> {
    return Promise.all(files.map(fileName => this.readFile(fileName))).then(filterEmpty);
  }

  private readFile(fileName: string): Promise<File | null> {
    return fsAsPromised
      .readFile(fileName)
      .then((content: Buffer) => new File(fileName, content))
      .then((file: File) => {
        this.reportSourceFilesRead(file);
        return file;
      })
      .catch(error => {
        if ((isErrnoException(error) && error.code === 'ENOENT') || error.code === 'EISDIR') {
          return null; // file is deleted or a directory. This can be a valid result of the git command
        } else {
          // Rethrow
          throw error;
        }
      });
  }
}

import { LogLevel } from '@stryker-mutator/api/core';
import * as log4js from 'log4js';

import { getFreePort } from '../utils/netUtils';

import LoggingClientContext from './LoggingClientContext';
import { minLevel } from './logUtils';

const enum AppenderName {
  File = 'file',
  FilteredFile = 'filteredFile',
  Console = 'console',
  FilteredConsoleLevel = 'filteredConsoleLevel',
  FilteredConsoleCategory = 'filteredConsoleCategory',
  All = 'all',
  Server = 'server'
}

const layouts: { color: log4js.PatternLayout; noColor: log4js.PatternLayout } = {
  color: {
    pattern: '%[%r (%z) %p %c%] %m',
    type: 'pattern'
  },
  noColor: {
    pattern: '%r (%z) %p %c %m',
    type: 'pattern'
  }
};

interface AppendersConfiguration {
  [name: string]: log4js.Appender;
}

const LOG_FILE_NAME = 'stryker.log';
export default class LogConfigurator {
  private static createMainProcessAppenders(consoleLogLevel: LogLevel, fileLogLevel: LogLevel, allowConsoleColors: boolean): AppendersConfiguration {
    // Add the custom "multiAppender": https://log4js-node.github.io/log4js-node/appenders.html#other-appenders
    const multiAppender = { type: require.resolve('./MultiAppender'), appenders: [AppenderName.FilteredConsoleLevel] };

    const consoleLayout = allowConsoleColors ? layouts.color : layouts.noColor;

    let allAppenders: AppendersConfiguration = {
      [AppenderName.Console]: { type: 'stdout', layout: consoleLayout },
      // Exclude messages like: "ERROR log4js A worker log process hung up unexpectedly" #1245
      [AppenderName.FilteredConsoleCategory]: { type: 'categoryFilter', appender: AppenderName.Console, exclude: 'log4js' },
      [AppenderName.FilteredConsoleLevel]: { type: 'logLevelFilter', appender: AppenderName.FilteredConsoleCategory, level: consoleLogLevel },
      [AppenderName.All]: multiAppender
    };

    // only add file if it is needed. Otherwise log4js will create the file directly, pretty annoying.
    if (fileLogLevel.toUpperCase() !== LogLevel.Off.toUpperCase()) {
      const fileAppender: log4js.FileAppender = { type: 'file', filename: LOG_FILE_NAME, layout: layouts.noColor };
      const filteredFileAppender: log4js.LogLevelFilterAppender = { type: 'logLevelFilter', appender: 'file', level: fileLogLevel };

      // Don't simply add the appenders, instead actually make sure they are ordinal "before" the others.
      // See https://github.com/log4js-node/log4js-node/issues/746
      allAppenders = Object.assign({ [AppenderName.File]: fileAppender, [AppenderName.FilteredFile]: filteredFileAppender }, allAppenders);

      multiAppender.appenders.push(AppenderName.FilteredFile);
    }

    return allAppenders;
  }

  private static createLog4jsConfig(defaultLogLevel: LogLevel, appenders: AppendersConfiguration): log4js.Configuration {
    return {
      appenders,
      categories: {
        default: {
          appenders: [AppenderName.All],
          level: defaultLogLevel
        }
      }
    };
  }

  /**
   * Configure logging for the master process. Either call this method or `configureChildProcess` before any `getLogger` calls.
   * @param consoleLogLevel The log level to configure for the console
   * @param fileLogLevel The log level to configure for the "stryker.log" file
   */
  public static configureMainProcess(
    consoleLogLevel: LogLevel = LogLevel.Information,
    fileLogLevel: LogLevel = LogLevel.Off,
    allowConsoleColors: boolean = true
  ) {
    const appenders = this.createMainProcessAppenders(consoleLogLevel, fileLogLevel, allowConsoleColors);
    log4js.configure(this.createLog4jsConfig(minLevel(consoleLogLevel, fileLogLevel), appenders));
  }

  /**
   * Configure the logging for the server. Includes the master configuration.
   * This method should only be called ONCE, as it starts the log4js server to listen for log events.
   * It returns the logging client context that should be used to configure the child processes.
   *
   * @param consoleLogLevel the console log level
   * @param fileLogLevel the file log level
   * @returns the context
   */
  public static async configureLoggingServer(
    consoleLogLevel: LogLevel,
    fileLogLevel: LogLevel,
    allowConsoleColors: boolean
  ): Promise<LoggingClientContext> {
    const loggerPort = await getFreePort();

    // Include the appenders for the main Stryker process, as log4js has only one single `configure` method.
    const appenders = this.createMainProcessAppenders(consoleLogLevel, fileLogLevel, allowConsoleColors);
    const multiProcessAppender: log4js.MultiprocessAppender = {
      appender: AppenderName.All,
      loggerPort,
      mode: 'master',
      type: 'multiprocess'
    };
    appenders[AppenderName.Server] = multiProcessAppender;
    const defaultLogLevel = minLevel(consoleLogLevel, fileLogLevel);
    log4js.configure(this.createLog4jsConfig(defaultLogLevel, appenders));

    const context: LoggingClientContext = {
      level: defaultLogLevel,
      port: loggerPort
    };
    return context;
  }

  /**
   * Configures the logging for a worker process. Sends all logging to the master process.
   * Either call this method or `configureMainProcess` before any `getLogger` calls.
   * @param context the logging client context used to configure the logging client
   */
  public static configureChildProcess(context: LoggingClientContext) {
    const clientAppender: log4js.MultiprocessAppender = { type: 'multiprocess', mode: 'worker', loggerPort: context.port };
    const appenders: AppendersConfiguration = { [AppenderName.All]: clientAppender };
    log4js.configure(this.createLog4jsConfig(context.level, appenders));
  }

  public static shutdown(): Promise<void> {
    return new Promise((res, rej) => {
      log4js.shutdown(err => {
        if (err) {
          rej(err);
        } else {
          res();
        }
      });
    });
  }
}

import { LoggingEvent } from 'log4js';

export type RuntimeAppender = (loggingEvent: LoggingEvent) => void;

export class MultiAppender {
  constructor(private readonly appenders: RuntimeAppender[]) {}

  public append(loggingEvent: LoggingEvent) {
    this.appenders.forEach(appender => appender(loggingEvent));
  }
}

/**
 * This method is expected by log4js to have this _exact_ name
 * and signature.
 * @see https://log4js-node.github.io/log4js-node/writing-appenders.html
 * @param config The appender configuration delivered by log4js
 * @param _ The layouts provided by log4js
 * @param findAppender A method to locate other appenders
 */
export function configure(config: { appenders: string[] }, _: any, findAppender: (name: string) => RuntimeAppender): RuntimeAppender {
  const multiAppender = new MultiAppender(config.appenders.map(name => findAppender(name)));
  return multiAppender.append.bind(multiAppender);
}

import { LogLevel } from '@stryker-mutator/api/core';
import * as log4js from 'log4js';

/**
 * Determines the minimal log level (where trace < off)
 * @param a one log level
 * @param b other log level
 */
export function minLevel(a: LogLevel, b: LogLevel) {
  if (log4js.levels.getLevel(b).isGreaterThanOrEqualTo(log4js.levels.getLevel(a))) {
    return a;
  } else {
    return b;
  }
}

