  Function,
  Statement
}

/**
 * Represents a statement index inside the coverage maps of a file
 * Either the function map, or statement map
 */
interface StatementIndex {
  kind: StatementIndexKind;
  index: string;
}

export class MutantTestMatcher {
  public static inject = tokens(commonTokens.logger, commonTokens.options, coreTokens.reporter, coreTokens.inputFiles, coreTokens.initialRunResult);
  constructor(
    private readonly log: Logger,
    private readonly options: StrykerOptions,
    private readonly reporter: StrictReporter,
    private readonly input: InputFileCollection,
    private readonly initialRunResult: InitialTestRunResult
  ) {}

  private get baseline(): CoverageCollection | null {
    if (this.isCoveragePerTestResult(this.initialRunResult.runResult.coverage)) {
      return this.initialRunResult.runResult.coverage.baseline;
    } else {
      return null;
    }
  }

  public async matchWithMutants(mutants: readonly Mutant[]): Promise<readonly TestableMutant[]> {
    const testableMutants = this.createTestableMutants(mutants);

    if (this.options.coverageAnalysis === 'off') {
      testableMutants.forEach(mutant => mutant.selectAllTests(this.initialRunResult.runResult, TestSelectionResult.Success));
    } else if (!this.initialRunResult.runResult.coverage) {
      this.log.warn(
        'No coverage result found, even though coverageAnalysis is "%s". Assuming that all tests cover each mutant. This might have a big impact on the performance.',
        this.options.coverageAnalysis
      );
      testableMutants.forEach(mutant => mutant.selectAllTests(this.initialRunResult.runResult, TestSelectionResult.FailedButAlreadyReported));
    } else {
      await Promise.all(testableMutants.map(testableMutant => this.enrichWithCoveredTests(testableMutant)));
    }
    this.reporter.onAllMutantsMatchedWithTests(Object.freeze(testableMutants.map(this.mapMutantOnMatchedMutant)));
    return testableMutants;
  }

  public async enrichWithCoveredTests(testableMutant: TestableMutant) {
    const transpiledLocation = await this.initialRunResult.sourceMapper.transpiledLocationFor({
      fileName: testableMutant.mutant.fileName,
      location: testableMutant.location
    });
    const fileCoverage = this.initialRunResult.coverageMaps[transpiledLocation.fileName];
    const statementIndex = this.findMatchingStatement(new LocationHelper(transpiledLocation.location), fileCoverage);
    if (statementIndex) {
      if (this.isCoveredByBaseline(transpiledLocation.fileName, statementIndex)) {
        testableMutant.selectAllTests(this.initialRunResult.runResult, TestSelectionResult.Success);
      } else {
        this.initialRunResult.runResult.tests.forEach((testResult, id) => {
          if (this.isCoveredByTest(id, transpiledLocation.fileName, statementIndex)) {
            testableMutant.selectTest(testResult, id);
          }
        });
      }
    } else {
      // Could not find a statement corresponding to this mutant
      // This can happen when for example mutating a TypeScript interface
      // It should result in an early result during mutation testing
      // Lets delay error reporting for now
      testableMutant.selectAllTests(this.initialRunResult.runResult, TestSelectionResult.Failed);
    }
  }

  private isCoveredByBaseline(fileName: string, statementIndex: StatementIndex): boolean {
    if (this.baseline) {
      const coverageResult = this.baseline[fileName];
      return this.isCoveredByCoverageCollection(coverageResult, statementIndex);
    } else {
      return false;
    }
  }

  private isCoveredByTest(testId: number, fileName: string, statementIndex: StatementIndex): boolean {
    const coverageCollection = this.findCoverageCollectionForTest(testId);
    const coveredFile = coverageCollection && coverageCollection[fileName];
    return this.isCoveredByCoverageCollection(coveredFile, statementIndex);
  }

  private isCoveredByCoverageCollection(coveredFile: CoverageResult | null, statementIndex: StatementIndex): boolean {
    if (coveredFile) {
      if (statementIndex.kind === StatementIndexKind.Statement) {
        return coveredFile.s[statementIndex.index] > 0;
      } else {
        return coveredFile.f[statementIndex.index] > 0;
      }
    } else {
      return false;
    }
  }

  private createTestableMutants(mutants: readonly Mutant[]): readonly TestableMutant[] {
    const sourceFiles = this.input.filesToMutate.map(file => new SourceFile(file));
    return filterEmpty(
      mutants.map((mutant, index) => {
        const sourceFile = sourceFiles.find(file => file.name === mutant.fileName);
        if (sourceFile) {
          return new TestableMutant(index.toString(), mutant, sourceFile);
        } else {
          this.log.error(
            `Mutant "${mutant.mutatorName}${mutant.replacement}" is corrupt, because cannot find a text file with name ${
              mutant.fileName
            }. List of source files: \n\t${sourceFiles.map(s => s.name).join('\n\t')}`
          );
          return null;
        }
      })
    );
  }

  /**
   * Map the Mutant object on the MatchMutant Object.
   * @param testableMutant The mutant.
   * @returns The MatchedMutant
   */
  private mapMutantOnMatchedMutant(testableMutant: TestableMutant): MatchedMutant {
    const matchedMutant: MatchedMutant = {
      fileName: testableMutant.mutant.fileName,
      id: testableMutant.id,
      mutatorName: testableMutant.mutant.mutatorName,
      replacement: testableMutant.mutant.replacement,
      runAllTests: testableMutant.runAllTests,
      scopedTestIds: testableMutant.selectedTests.map(testSelection => testSelection.id),
      timeSpentScopedTests: testableMutant.timeSpentScopedTests
    };
    return Object.freeze(matchedMutant);
  }

  private findMatchingStatement(location: LocationHelper, fileCoverage: CoverageMaps): StatementIndex | null {
    const statementIndex = this.findMatchingStatementInMap(location, fileCoverage.statementMap);
    if (statementIndex) {
      return {
        index: statementIndex,
        kind: StatementIndexKind.Statement
      };
    } else {
      const functionIndex = this.findMatchingStatementInMap(location, fileCoverage.fnMap);
      if (functionIndex) {
        return {
          index: functionIndex,
          kind: StatementIndexKind.Function
        };
      } else {
        return null;
      }
    }
  }

  /**
   * Finds the smallest statement that covers a location
   * @param needle The location to find.
   * @param haystack the statement map or function map to search in.
   * @returns The index of the smallest statement surrounding the location, or null if not found.
   */
  private findMatchingStatementInMap(needle: LocationHelper, haystack: StatementMap): string | null {
    let smallestStatement: { index: string | null; location: LocationHelper } = {
      index: null,
      location: LocationHelper.MAX_VALUE
    };
    if (haystack) {
      Object.keys(haystack).forEach(statementId => {
        const statementLocation = haystack[statementId];

        if (needle.isCoveredBy(statementLocation) && smallestStatement.location.isSmallerArea(statementLocation)) {
          smallestStatement = {
            index: statementId,
            location: new LocationHelper(statementLocation)
          };
        }
      });
    }
    return smallestStatement.index;
  }

  private findCoverageCollectionForTest(testId: number): CoverageCollection | null {
    if (this.initialRunResult.runResult.coverage) {
      if (this.isCoveragePerTestResult(this.initialRunResult.runResult.coverage)) {
        return this.initialRunResult.runResult.coverage.deviations[testId];
      } else {
        return this.initialRunResult.runResult.coverage;
      }
    } else {
      return null;
    }
  }

  private isCoveragePerTestResult(_coverage: CoverageCollection | CoveragePerTestResult | undefined): _coverage is CoveragePerTestResult {
    return this.options.coverageAnalysis === 'perTest';
  }
}

import { File, MutatorDescriptor } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { Mutant, Mutator } from '@stryker-mutator/api/mutant';
import { commonTokens, PluginKind, tokens } from '@stryker-mutator/api/plugin';

import { coreTokens, PluginCreator } from '../di';

export class MutatorFacade implements Mutator {
  public static inject = tokens(commonTokens.mutatorDescriptor, coreTokens.pluginCreatorMutator, commonTokens.logger);
  constructor(
    private readonly mutatorDescriptor: MutatorDescriptor,
    private readonly pluginCreator: PluginCreator<PluginKind.Mutator>,
    private readonly log: Logger
  ) {}

  public mutate(inputFiles: readonly File[]): readonly Mutant[] {
    const allMutants = this.pluginCreator.create(this.getMutatorName(this.mutatorDescriptor.name)).mutate(inputFiles);
    const includedMutants = this.removeExcludedMutants(allMutants);
    this.logMutantCount(includedMutants.length, allMutants.length);
    return includedMutants;
  }

  private removeExcludedMutants(mutants: readonly Mutant[]): readonly Mutant[] {
    if (this.mutatorDescriptor.excludedMutations.length) {
      return mutants.filter(mutant => !this.mutatorDescriptor.excludedMutations.includes(mutant.mutatorName));
    } else {
      return mutants;
    }
  }

  private getMutatorName(mutator: string | MutatorDescriptor) {
    if (typeof mutator === 'string') {
      return mutator;
    } else {
      return mutator.name;
    }
  }

  private logMutantCount(includedMutantCount: number, totalMutantCount: number) {
    let mutantCountMessage;
    if (includedMutantCount) {
      mutantCountMessage = `${includedMutantCount} Mutant(s) generated`;
    } else {
      mutantCountMessage = "It's a mutant-free world, nothing to test.";
    }
    const numberExcluded = totalMutantCount - includedMutantCount;
    if (numberExcluded) {
      mutantCountMessage += ` (${numberExcluded} Mutant(s) excluded)`;
    }
    this.log.info(mutantCountMessage);
  }
}

import { EOL } from 'os';

import { File, StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { TestFramework } from '@stryker-mutator/api/test_framework';
import { RunResult, RunStatus, TestResult, TestStatus } from '@stryker-mutator/api/test_runner';
import { Transpiler } from '@stryker-mutator/api/transpile';

import { coreTokens } from '../di';
import InputFileCollection from '../input/InputFileCollection';
import LoggingClientContext from '../logging/LoggingClientContext';
import Sandbox from '../Sandbox';
import { coveragePerTestHooks } from '../transpiler/coverageHooks';
import CoverageInstrumenterTranspiler, { CoverageMapsByFile } from '../transpiler/CoverageInstrumenterTranspiler';
import SourceMapper from '../transpiler/SourceMapper';
import { TemporaryDirectory } from '../utils/TemporaryDirectory';
import Timer from '../utils/Timer';

// The initial run might take a while.
// For example: angular-bootstrap takes up to 45 seconds.
// Lets take 5 minutes just to be sure
const INITIAL_RUN_TIMEOUT = 60 * 1000 * 5;
const INITIAL_TEST_RUN_MARKER = 'Initial test run';

export interface InitialTestRunResult {
  runResult: RunResult;
  overheadTimeMS: number;
  sourceMapper: SourceMapper;
  coverageMaps: CoverageMapsByFile;
}

/**
 * A small object that keeps the timing variables of a test run.
 */
interface Timing {
  /**
   * The time that the test runner was actually executing tests in milliseconds.
   */
  net: number;
  /**
   * the time that was spend not executing tests in milliseconds.
   * So the time it took to start the test runner and to report the result.
   */
  overhead: number;
}

export default class InitialTestExecutor {
  public static inject = tokens(
    commonTokens.options,
    commonTokens.logger,
    coreTokens.inputFiles,
    coreTokens.testFramework,
    coreTokens.timer,
    coreTokens.loggingContext,
    coreTokens.transpiler,
    coreTokens.temporaryDirectory
  );

  constructor(
    private readonly options: StrykerOptions,
    private readonly log: Logger,
    private readonly inputFiles: InputFileCollection,
    private readonly testFramework: TestFramework | null,
    private readonly timer: Timer,
    private readonly loggingContext: LoggingClientContext,
    private readonly transpiler: Transpiler,
    private readonly tempDir: TemporaryDirectory
  ) {}

  public async run(): Promise<InitialTestRunResult> {
    this.log.info('Starting initial test run. This may take a while.');

    // Before we can run the tests we transpile the input files.
    // Files that are not transpiled should pass through without transpiling
    const transpiledFiles = await this.transpiler.transpile(this.inputFiles.files);

    // Now that we have the transpiled files, we create a source mapper so
    // we can figure out which files we need to annotate for code coverage
    const sourceMapper = SourceMapper.create(transpiledFiles, this.options);

    // Annotate the transpiled files for code coverage. This allows the
    // test runner to report code coverage (if `coverageAnalysis` is enabled)
    const { coverageMaps, instrumentedFiles } = await this.annotateForCodeCoverage(transpiledFiles, sourceMapper);
    this.logTranspileResult(instrumentedFiles);

    const { runResult, grossTimeMS } = await this.runInSandbox(instrumentedFiles);
    const timing = this.calculateTiming(grossTimeMS, runResult.tests);
    this.validateResult(runResult, timing);
    return {
      coverageMaps,
      overheadTimeMS: timing.overhead,
      runResult,
      sourceMapper
    };
  }

  private async runInSandbox(files: readonly File[]): Promise<{ runResult: RunResult; grossTimeMS: number }> {
    const sandbox = await Sandbox.create(this.options, 0, files, this.testFramework, 0, this.loggingContext, this.tempDir);
    this.timer.mark(INITIAL_TEST_RUN_MARKER);
    const runResult = await sandbox.run(INITIAL_RUN_TIMEOUT, this.getCollectCoverageHooksIfNeeded());
    const grossTimeMS = this.timer.elapsedMs(INITIAL_TEST_RUN_MARKER);
    await sandbox.dispose();
    return { runResult, grossTimeMS };
  }

  private async annotateForCodeCoverage(
    files: readonly File[],
    sourceMapper: SourceMapper
  ): Promise<{ instrumentedFiles: readonly File[]; coverageMaps: CoverageMapsByFile }> {
    const filesToInstrument = this.inputFiles.filesToMutate.map(mutateFile => sourceMapper.transpiledFileNameFor(mutateFile.name));
    const coverageInstrumenterTranspiler = new CoverageInstrumenterTranspiler(this.options, filesToInstrument);
    const instrumentedFiles = await coverageInstrumenterTranspiler.transpile(files);
    return { coverageMaps: coverageInstrumenterTranspiler.fileCoverageMaps, instrumentedFiles };
  }

  private validateResult(runResult: RunResult, timing: Timing): void {
    switch (runResult.status) {
      case RunStatus.Complete:
        const failedTests = this.filterOutFailedTests(runResult);
        if (failedTests.length) {
          this.logFailedTestsInInitialRun(failedTests);
          throw new Error('There were failed tests in the initial test run.');
        }
        if (runResult.tests.length === 0) {
          this.log.warn('No tests were executed. Stryker will exit prematurely. Please check your configuration.');
          return;
        } else {
          this.logInitialTestRunSucceeded(runResult.tests, timing);
          return;
        }
      case RunStatus.Error:
        this.logErrorsInInitialRun(runResult);
        break;
      case RunStatus.Timeout:
        this.logTimeoutInitialRun(runResult);
        break;
    }
    throw new Error('Something went wrong in the initial test run');
  }

  /**
   * Calculates the timing variables for the test run.
   * grossTime = NetTime + overheadTime
   *
   * The overhead time is used to calculate exact timeout values during mutation testing.
   * See timeoutMS setting in README for more information on this calculation
   */
  private calculateTiming(grossTimeMS: number, tests: readonly TestResult[]): Timing {
    const netTimeMS = tests.reduce((total, test) => total + test.timeSpentMs, 0);
    const overheadTimeMS = grossTimeMS - netTimeMS;
    return {
      net: netTimeMS,
      overhead: overheadTimeMS < 0 ? 0 : overheadTimeMS
    };
  }

  private getCollectCoverageHooksIfNeeded(): string | undefined {
    if (this.options.coverageAnalysis === 'perTest') {
      if (this.testFramework) {
        // Add piece of javascript to collect coverage per test results
        this.log.debug('Adding test hooks for coverageAnalysis "perTest".');
        return coveragePerTestHooks(this.testFramework);
      } else {
        this.log.warn(
          'Cannot measure coverage results per test, there is no testFramework and thus no way of executing code right before and after each test.'
        );
      }
    }
    return undefined;
  }

  private logTranspileResult(transpiledFiles: readonly File[]) {
    if (this.options.transpilers.length && this.log.isDebugEnabled()) {
      this.log.debug(
        `Transpiled files: ${JSON.stringify(
          transpiledFiles.map(f => `${f.name}`),
          null,
          2
        )}`
      );
    }
  }

  private filterOutFailedTests(runResult: RunResult) {
    return runResult.tests.filter(testResult => testResult.status === TestStatus.Failed);
  }

  private logInitialTestRunSucceeded(tests: TestResult[], timing: Timing) {
    this.log.info(
      'Initial test run succeeded. Ran %s tests in %s (net %s ms, overhead %s ms).',
      tests.length,
      this.timer.humanReadableElapsed(),
      timing.net,
      timing.overhead
    );
  }

  private logFailedTestsInInitialRun(failedTests: TestResult[]): void {
    let message = 'One or more tests failed in the initial test run:';
    failedTests.forEach(test => {
      message += `${EOL}\t${test.name}`;
      if (test.failureMessages && test.failureMessages.length) {
        message += `${EOL}\t\t${test.failureMessages.join(`${EOL}\t\t`)}`;
      }
    });
    this.log.error(message);
  }
  private logErrorsInInitialRun(runResult: RunResult) {
    let message = 'One or more tests resulted in an error:';
    if (runResult.errorMessages && runResult.errorMessages.length) {
      runResult.errorMessages.forEach(error => (message += `${EOL}\t${error}`));
    }
    this.log.error(message);
  }

  private logTimeoutInitialRun(runResult: RunResult) {
    let message = 'Initial test run timed out! Ran following tests before timeout:';
    runResult.tests.forEach(test => (message += `${EOL}\t${test.name} (${TestStatus[test.status]})`));
    this.log.error(message);
  }
}

import { tokens } from '@stryker-mutator/api/plugin';
import { MutantResult } from '@stryker-mutator/api/report';
import { tap, toArray } from 'rxjs/operators';

import { coreTokens } from '../di';
import StrictReporter from '../reporters/StrictReporter';
import { SandboxPool } from '../SandboxPool';
import TestableMutant from '../TestableMutant';
import { MutantTranspileScheduler } from '../transpiler/MutantTranspileScheduler';

export class MutationTestExecutor {
  public static inject = tokens(coreTokens.reporter, coreTokens.mutantTranspileScheduler, coreTokens.sandboxPool);
  constructor(
    private readonly reporter: StrictReporter,
    private readonly mutantTranspileScheduler: MutantTranspileScheduler,
    private readonly sandboxPool: SandboxPool
  ) {}

  public async run(allMutants: readonly TestableMutant[]): Promise<MutantResult[]> {
    const results = await this.sandboxPool
      .runMutants(this.mutantTranspileScheduler.scheduleTranspileMutants(allMutants))
      .pipe(
        tap(this.reportResult),
        // Signal the mutant transpiler that there is another slot open for transpiling
        tap(this.mutantTranspileScheduler.scheduleNext),
        toArray(),
        tap(this.reportAll)
      )
      .toPromise();

    return results;
  }

  private readonly reportResult = (mutantResult: MutantResult): void => {
    this.reporter.onMutantTested(mutantResult);
  };

  private readonly reportAll = (mutantResults: MutantResult[]): void => {
    this.reporter.onAllMutantsTested(mutantResults);
  };
}

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, PluginKind } from '@stryker-mutator/api/plugin';
import { MatchedMutant, MutantResult, mutationTestReportSchema, Reporter, ScoreResult, SourceFile } from '@stryker-mutator/api/report';
import { tokens } from 'typed-inject';

import { coreTokens } from '../di';
import { PluginCreator } from '../di/PluginCreator';

import StrictReporter from './StrictReporter';

export default class BroadcastReporter implements StrictReporter {
  public static readonly inject = tokens(commonTokens.options, coreTokens.pluginCreatorReporter, commonTokens.logger);

  public readonly reporters: {
    [name: string]: Reporter;
  };
  constructor(
    private readonly options: StrykerOptions,
    private readonly pluginCreator: PluginCreator<PluginKind.Reporter>,
    private readonly log: Logger
  ) {
    this.reporters = {};
    this.options.reporters.forEach(reporterName => this.createReporter(reporterName));
    this.logAboutReporters();
  }

  private createReporter(reporterName: string): void {
    if (reporterName === 'progress' && !process.stdout.isTTY) {
      this.log.info('Detected that current console does not support the "progress" reporter, downgrading to "progress-append-only" reporter');
      reporterName = 'progress-append-only';
    }
    this.reporters[reporterName] = this.pluginCreator.create(reporterName);
  }

  private logAboutReporters(): void {
    const reporterNames = Object.keys(this.reporters);
    if (reporterNames.length) {
      if (this.log.isDebugEnabled()) {
        this.log.debug(`Broadcasting to reporters ${JSON.stringify(reporterNames)}`);
      }
    } else {
      this.log.warn("No reporter configured. Please configure one or more reporters in the (for example: reporters: ['progress'])");
    }
  }

  private broadcast(methodName: keyof Reporter, eventArgs: any): Promise<any> {
    return Promise.all(
      Object.keys(this.reporters).map(async reporterName => {
        const reporter = this.reporters[reporterName];
        if (typeof reporter[methodName] === 'function') {
          const deprecatedMethodName = 'onScoreCalculated';
          if (methodName === deprecatedMethodName) {
            this.log.warn(
              `DEPRECATED: The reporter '${reporterName}' uses '${deprecatedMethodName}' which is deprecated. Please use 'onMutationTestReportReady' and calculate the score as an alternative.`
            );
          }
          try {
            await (reporter[methodName] as any)(eventArgs);
          } catch (error) {
            this.handleError(error, methodName, reporterName);
          }
        }
      })
    );
  }

  public onSourceFileRead(file: SourceFile): void {
    this.broadcast('onSourceFileRead', file);
  }

  public onAllSourceFilesRead(files: SourceFile[]): void {
    this.broadcast('onAllSourceFilesRead', files);
  }

  public onAllMutantsMatchedWithTests(results: readonly MatchedMutant[]): void {
    this.broadcast('onAllMutantsMatchedWithTests', results);
  }

  public onMutantTested(result: MutantResult): void {
    this.broadcast('onMutantTested', result);
  }

  public onAllMutantsTested(results: MutantResult[]): void {
    this.broadcast('onAllMutantsTested', results);
  }

  public onMutationTestReportReady(report: mutationTestReportSchema.MutationTestResult): void {
    this.broadcast('onMutationTestReportReady', report);
  }

  public onScoreCalculated(score: ScoreResult): void {
    this.broadcast('onScoreCalculated', score);
  }

  public async wrapUp(): Promise<void> {
    await this.broadcast('wrapUp', undefined);
  }

  private handleError(error: Error, methodName: string, reporterName: string) {
    this.log.error(`An error occurred during '${methodName}' on reporter '${reporterName}'.`, error);
  }
}

import * as os from 'os';

import { Position, StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens } from '@stryker-mutator/api/plugin';
import { MutantResult, MutantStatus, mutationTestReportSchema, Reporter } from '@stryker-mutator/api/report';
import { calculateMetrics } from 'mutation-testing-metrics';
import { tokens } from 'typed-inject';

import chalk = require('chalk');

import ClearTextScoreTable from './ClearTextScoreTable';

export default class ClearTextReporter implements Reporter {
  public static inject = tokens(commonTokens.logger, commonTokens.options);
  constructor(private readonly log: Logger, private readonly options: StrykerOptions) {
    this.configConsoleColor();
  }

  private readonly out: NodeJS.WritableStream = process.stdout;

  private writeLine(output?: string) {
    this.out.write(`${output || ''}${os.EOL}`);
  }

  private configConsoleColor() {
    if (!this.options.allowConsoleColors) {
      chalk.level = 0; // All colors disabled
    }
  }

  public onAllMutantsTested(mutantResults: MutantResult[]): void {
    this.writeLine();
    let totalTests = 0;

    // use these fn's in order to preserve the 'this` pointer
    const logDebugFn = (input: string) => this.log.debug(input);
    const writeLineFn = (input: string) => this.writeLine(input);

    mutantResults.forEach((result, index) => {
      if (result.testsRan) {
        totalTests += result.testsRan.length;
      }
      switch (result.status) {
        case MutantStatus.Killed:
          this.log.debug(chalk.bold.green('Mutant killed!'));
          this.logMutantResult(result, index, logDebugFn);
          break;
        case MutantStatus.TimedOut:
          this.log.debug(chalk.bold.yellow('Mutant timed out!'));
          this.logMutantResult(result, index, logDebugFn);
          break;
        case MutantStatus.RuntimeError:
          this.log.debug(chalk.bold.yellow('Mutant caused a runtime error!'));
          this.logMutantResult(result, index, logDebugFn);
          break;
        case MutantStatus.TranspileError:
          this.log.debug(chalk.bold.yellow('Mutant caused a transpile error!'));
          this.logMutantResult(result, index, logDebugFn);
          break;
        case MutantStatus.Survived:
          this.logMutantResult(result, index, writeLineFn);
          break;
        case MutantStatus.NoCoverage:
          this.logMutantResult(result, index, writeLineFn);
          break;
      }
    });
    this.writeLine(`Ran ${(totalTests / mutantResults.length).toFixed(2)} tests per mutant on average.`);
  }

  private logMutantResult(result: MutantResult, index: number, logImplementation: (input: string) => void): void {
    logImplementation(`${index}. [${MutantStatus[result.status]}] ${result.mutatorName}`);
    logImplementation(this.colorSourceFileAndLocation(result.sourceFilePath, result.location.start));

    result.originalLines.split('\n').forEach(line => {
      logImplementation(chalk.red('-   ' + line));
    });
    result.mutatedLines.split('\n').forEach(line => {
      logImplementation(chalk.green('+   ' + line));
    });
    logImplementation('');
    if (this.options.coverageAnalysis === 'perTest') {
      this.logExecutedTests(result, logImplementation);
    } else if (result.testsRan && result.testsRan.length > 0) {
      logImplementation('Ran all tests for this mutant.');
    }
  }

  private colorSourceFileAndLocation(sourceFilePath: string, position: Position): string {
    const clearTextReporterConfig = this.options.clearTextReporter;

    if (clearTextReporterConfig && clearTextReporterConfig.allowColor !== false) {
      return `${sourceFilePath}:${position.line}:${position.column}`;
    }

    return [chalk.cyan(sourceFilePath), chalk.yellow(`${position.line}`), chalk.yellow(`${position.column}`)].join(':');
  }

  private logExecutedTests(result: MutantResult, logImplementation: (input: string) => void) {
    const clearTextReporterConfig = this.options.clearTextReporter || {};

    if (!clearTextReporterConfig.logTests) {
      return;
    }

    if (result.testsRan && result.testsRan.length > 0) {
      let testsToLog = 3;
      if (typeof clearTextReporterConfig.maxTestsToLog === 'number') {
        testsToLog = clearTextReporterConfig.maxTestsToLog;
      }

      if (testsToLog > 0) {
        logImplementation('Tests ran: ');
        for (let i = 0; i < testsToLog; i++) {
          if (i > result.testsRan.length - 1) {
            break;
          }

          logImplementation('    ' + result.testsRan[i]);
        }
        if (testsToLog < result.testsRan.length) {
          logImplementation(`  and ${result.testsRan.length - testsToLog} more tests!`);
        }
        logImplementation('');
      }
    }
  }

  public onMutationTestReportReady(report: mutationTestReportSchema.MutationTestResult) {
    const metricsResult = calculateMetrics(report.files);
    this.writeLine(new ClearTextScoreTable(metricsResult, this.options.thresholds).draw());
  }
}

import * as os from 'os';

import { MutationScoreThresholds } from '@stryker-mutator/api/core';
import { MetricsResult } from 'mutation-testing-metrics';

import chalk = require('chalk');
import flatMap = require('lodash.flatmap');

const FILES_ROOT_NAME = 'All files';

type TableCellValueFactory = (row: MetricsResult, ancestorCount: number) => string;

const repeat = (char: string, nTimes: number) => new Array(nTimes > -1 ? nTimes + 1 : 0).join(char);
const spaces = (n: number) => repeat(' ', n);
const dots = (n: number) => repeat('.', n);

/**
 * Represents a column in the clear text table
 */
class Column {
  protected width: number;

  constructor(public header: string, public valueFactory: TableCellValueFactory, public rows: MetricsResult) {
    const maxContentSize = this.determineValueSize();
    this.width = this.pad(dots(maxContentSize)).length;
  }

  protected determineValueSize(row: MetricsResult = this.rows, ancestorCount: number = 0): number {
    const valueWidths = row.childResults.map(child => this.determineValueSize(child, ancestorCount + 1));
    valueWidths.push(this.header.length);
    valueWidths.push(this.valueFactory(row, ancestorCount).length);
    return Math.max(...valueWidths);
  }

  /**
   * Adds padding (spaces) to the front and end of a value
   * @param input The string input
   */
  protected pad(input: string): string {
    return `${spaces(this.width - input.length - 2)} ${input} `;
  }

  public drawLine(): string {
    return repeat('-', this.width);
  }

  public drawTableCell(score: MetricsResult, ancestorCount: number) {
    return this.color(score)(this.pad(this.valueFactory(score, ancestorCount)));
  }

  public drawHeader() {
    return this.pad(this.header);
  }

  protected color(_score: MetricsResult) {
    return (input: string) => input;
  }
}

class MutationScoreColumn extends Column {
  constructor(rows: MetricsResult, private readonly thresholds: MutationScoreThresholds) {
    super('% score', row => row.metrics.mutationScore.toFixed(2), rows);
  }
  protected color(metricsResult: MetricsResult) {
    if (metricsResult.metrics.mutationScore >= this.thresholds.high) {
      return chalk.green;
    } else if (metricsResult.metrics.mutationScore >= this.thresholds.low) {
      return chalk.yellow;
    } else {
      return chalk.red;
    }
  }
}

class FileColumn extends Column {
  constructor(rows: MetricsResult) {
    super('File', (row, ancestorCount) => spaces(ancestorCount) + (ancestorCount === 0 ? FILES_ROOT_NAME : row.name), rows);
  }
  protected pad(input: string): string {
    return `${input} ${spaces(this.width - input.length - 1)}`;
  }
}

/**
 * Represents a clear text table for mutation score
 */
export default class ClearTextScoreTable {
  private readonly columns: Column[];

  constructor(private readonly metricsResult: MetricsResult, thresholds: MutationScoreThresholds) {
    this.columns = [
      new FileColumn(metricsResult),
      new MutationScoreColumn(metricsResult, thresholds),
      new Column('# killed', row => row.metrics.killed.toString(), metricsResult),
      new Column('# timeout', row => row.metrics.timeout.toString(), metricsResult),
      new Column('# survived', row => row.metrics.survived.toString(), metricsResult),
      new Column('# no cov', row => row.metrics.noCoverage.toString(), metricsResult),
      new Column('# error', row => (row.metrics.runtimeErrors + row.metrics.compileErrors).toString(), metricsResult)
    ];
  }

  private drawBorder() {
    return this.drawRow(column => column.drawLine());
  }

  private drawHeader() {
    return this.drawRow(c => c.drawHeader());
  }

  private drawRow(toDraw: (col: Column) => string) {
    return this.columns.map(toDraw).join('|') + '|';
  }

  private drawValues(current = this.metricsResult, ancestorCount = 0): string[] {
    return [this.drawRow(c => c.drawTableCell(current, ancestorCount))].concat(
      flatMap(current.childResults, child => this.drawValues(child, ancestorCount + 1))
    );
  }

  /**
   * Returns a string with the score results drawn in a table.
   */
  public draw() {
    return [this.drawBorder(), this.drawHeader(), this.drawBorder(), this.drawValues().join(os.EOL), this.drawBorder()].join(os.EOL);
  }
}

import * as os from 'os';

import { MutantResult, MutantStatus, Reporter } from '@stryker-mutator/api/report';

