import * as path from 'path';

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { MatchedMutant, MutantResult, mutationTestReportSchema, Reporter, ScoreResult, SourceFile } from '@stryker-mutator/api/report';
import { fsAsPromised } from '@stryker-mutator/util';

import { cleanFolder } from '../utils/fileUtils';

import StrictReporter from './StrictReporter';

const DEFAULT_BASE_FOLDER = 'reports/mutation/events';

export default class EventRecorderReporter implements StrictReporter {
  public static readonly inject = tokens(commonTokens.logger, commonTokens.options);

  private readonly allWork: Array<Promise<void>> = [];
  private readonly createBaseFolderTask: Promise<any>;
  private _baseFolder: string;
  private index = 0;

  constructor(private readonly log: Logger, private readonly options: StrykerOptions) {
    this.createBaseFolderTask = cleanFolder(this.baseFolder);
  }

  private get baseFolder() {
    if (!this._baseFolder) {
      if (this.options.eventReporter && this.options.eventReporter.baseDir) {
        this._baseFolder = this.options.eventReporter.baseDir;
        this.log.debug(`Using configured output folder ${this._baseFolder}`);
      } else {
        this.log.debug(
          `No base folder configuration found (using configuration: eventReporter: { baseDir: 'output/folder' }), using default ${DEFAULT_BASE_FOLDER}`
        );
        this._baseFolder = DEFAULT_BASE_FOLDER;
      }
    }
    return this._baseFolder;
  }

  private writeToFile(methodName: keyof Reporter, data: any) {
    const filename = path.join(this.baseFolder, `${this.format(this.index++)}-${methodName}.json`);
    this.log.debug(`Writing event ${methodName} to file ${filename}`);
    return fsAsPromised.writeFile(filename, JSON.stringify(data), { encoding: 'utf8' });
  }

  private format(input: number) {
    let str = input.toString();
    for (let i = 10000; i > 1; i = i / 10) {
      if (i > input) {
        str = '0' + str;
      }
    }
    return str;
  }

  private work(eventName: keyof Reporter, data: any) {
    this.allWork.push(this.createBaseFolderTask.then(() => this.writeToFile(eventName, data)));
  }

  public onSourceFileRead(file: SourceFile): void {
    this.work('onSourceFileRead', file);
  }

  public onAllSourceFilesRead(files: SourceFile[]): void {
    this.work('onAllSourceFilesRead', files);
  }

  public onAllMutantsMatchedWithTests(results: readonly MatchedMutant[]): void {
    this.work('onAllMutantsMatchedWithTests', results);
  }

  public onMutantTested(result: MutantResult): void {
    this.work('onMutantTested', result);
  }

  public onMutationTestReportReady(report: mutationTestReportSchema.MutationTestResult): void {
    this.work('onMutationTestReportReady', report);
  }

  public onScoreCalculated(score: ScoreResult): void {
    this.work('onScoreCalculated', score);
  }

  public onAllMutantsTested(results: MutantResult[]): void {
    this.work('onAllMutantsTested', results);
  }

  public async wrapUp(): Promise<any> {
    await this.createBaseFolderTask;
    return Promise.all(this.allWork);
  }
}

import * as path from 'path';

import { Location, Position, StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { MutantResult, MutantStatus, mutationTestReportSchema, Reporter } from '@stryker-mutator/api/report';
import { normalizeWhitespaces } from '@stryker-mutator/util';

import { coreTokens } from '../di';
import InputFileCollection from '../input/InputFileCollection';

export class MutationTestReportCalculator {
  public static inject = tokens(coreTokens.reporter, commonTokens.options, coreTokens.inputFiles, commonTokens.logger);

  constructor(
    private readonly reporter: Required<Reporter>,
    private readonly options: StrykerOptions,
    private readonly inputFiles: InputFileCollection,
    private readonly log: Logger
  ) {}

  public report(results: readonly MutantResult[]) {
    this.reporter.onMutationTestReportReady(this.mutationTestReport(results));
  }

  private mutationTestReport(results: readonly MutantResult[]): mutationTestReportSchema.MutationTestResult {
    return {
      files: this.toFileResults(results),
      schemaVersion: '1.0',
      thresholds: this.options.thresholds
    };
  }

  private toFileResults(results: readonly MutantResult[]): mutationTestReportSchema.FileResultDictionary {
    const resultDictionary: mutationTestReportSchema.FileResultDictionary = Object.create(null);
    results.forEach(mutantResult => {
      const fileResult = resultDictionary[mutantResult.sourceFilePath];
      if (fileResult) {
        fileResult.mutants.push(this.toMutantResult(mutantResult));
      } else {
        const sourceFile = this.inputFiles.files.find(file => file.name === mutantResult.sourceFilePath);
        if (sourceFile) {
          resultDictionary[mutantResult.sourceFilePath] = {
            language: this.determineLanguage(sourceFile.name),
            mutants: [this.toMutantResult(mutantResult)],
            source: sourceFile.textContent
          };
        } else {
          this.log.warn(
            normalizeWhitespaces(`File "${mutantResult.sourceFilePath}" not found
          in input files, but did receive mutant result for it. This shouldn't happen`)
          );
        }
      }
    });
    return resultDictionary;
  }

  public determineLanguage(name: string): string {
    const ext = path.extname(name).toLowerCase();
    switch (ext) {
      case '.ts':
      case '.tsx':
        return 'typescript';
      case '.html':
      case '.vue':
        return 'html';
      default:
        return 'javascript';
    }
  }

  private toMutantResult(mutantResult: MutantResult): mutationTestReportSchema.MutantResult {
    return {
      id: mutantResult.id,
      location: this.toLocation(mutantResult.location),
      mutatorName: mutantResult.mutatorName,
      replacement: mutantResult.replacement,
      status: this.toStatus(mutantResult.status)
    };
  }

  private toLocation(location: Location): mutationTestReportSchema.Location {
    return {
      end: this.toPosition(location.end),
      start: this.toPosition(location.start)
    };
  }

  private toPosition(pos: Position): mutationTestReportSchema.Position {
    return {
      column: pos.column + 1, // convert from 0-based to 1-based
      line: pos.line + 1
    };
  }

  private toStatus(status: MutantStatus): mutationTestReportSchema.MutantStatus {
    switch (status) {
      case MutantStatus.Killed:
        return mutationTestReportSchema.MutantStatus.Killed;
      case MutantStatus.NoCoverage:
        return mutationTestReportSchema.MutantStatus.NoCoverage;
      case MutantStatus.RuntimeError:
        return mutationTestReportSchema.MutantStatus.RuntimeError;
      case MutantStatus.Survived:
        return mutationTestReportSchema.MutantStatus.Survived;
      case MutantStatus.TimedOut:
        return mutationTestReportSchema.MutantStatus.Timeout;
      case MutantStatus.TranspileError:
        return mutationTestReportSchema.MutantStatus.CompileError;
      default:
        this.logUnsupportedMutantStatus(status);
        return mutationTestReportSchema.MutantStatus.RuntimeError;
    }
  }

  private logUnsupportedMutantStatus(status: never) {
    this.log.warn('Unable to convert "%s" to a MutantStatus', status);
  }
}

import * as os from 'os';

import { MatchedMutant } from '@stryker-mutator/api/report';

import ProgressKeeper from './ProgressKeeper';

export default class ProgressAppendOnlyReporter extends ProgressKeeper {
  private intervalReference: NodeJS.Timer;

  public onAllMutantsMatchedWithTests(matchedMutants: readonly MatchedMutant[]): void {
    super.onAllMutantsMatchedWithTests(matchedMutants);
    if (matchedMutants.length) {
      this.intervalReference = setInterval(() => this.render(), 10000);
    }
  }

  public onAllMutantsTested(): void {
    clearInterval(this.intervalReference);
  }

  private render() {
    process.stdout.write(
      `Mutation testing ${this.getPercentDone()} (elapsed: ${this.getElapsedTime()}, remaining: ${this.getEtc()}) ` +
        `${this.progress.tested}/${this.progress.total} tested (${this.progress.survived} survived, ${this.progress.timedOut} timed out)` +
        os.EOL
    );
  }

  private getPercentDone() {
    return `${Math.floor((this.progress.tested / this.progress.total) * 100)}%`;
  }
}

import { MatchedMutant, MutantResult, Reporter } from '@stryker-mutator/api/report';
import { MutantStatus } from '@stryker-mutator/api/report';

import Timer from '../utils/Timer';

abstract class ProgressKeeper implements Reporter {
  private timer: Timer;
  protected progress = {
    survived: 0,
    timedOut: 0,
    tested: 0,
    total: 0
  };

  private mutantIdsWithoutCoverage: string[];

  public onAllMutantsMatchedWithTests(matchedMutants: readonly MatchedMutant[]): void {
    this.timer = new Timer();
    this.mutantIdsWithoutCoverage = matchedMutants.filter(m => !m.runAllTests && m.scopedTestIds.length === 0).map(m => m.id);
    this.progress.total = matchedMutants.length - this.mutantIdsWithoutCoverage.length;
  }

  public onMutantTested(result: MutantResult): void {
    if (!this.mutantIdsWithoutCoverage.some(id => result.id === id)) {
      this.progress.tested++;
    }
    if (result.status === MutantStatus.Survived) {
      this.progress.survived++;
    }
    if (result.status === MutantStatus.TimedOut) {
      this.progress.timedOut++;
    }
  }

  protected getElapsedTime() {
    return this.formatTime(this.timer.elapsedSeconds());
  }

  protected getEtc() {
    const totalSecondsLeft = Math.floor((this.timer.elapsedSeconds() / this.progress.tested) * (this.progress.total - this.progress.tested));

    if (isFinite(totalSecondsLeft) && totalSecondsLeft > 0) {
      return this.formatTime(totalSecondsLeft);
    } else {
      return 'n/a';
    }
  }

  private formatTime(timeInSeconds: number) {
    const hours = Math.floor(timeInSeconds / 3600);

    const minutes = Math.floor((timeInSeconds % 3600) / 60);

    return hours > 0 // conditional time formatting
      ? `~${hours}h ${minutes}m`
      : minutes > 0
      ? `~${minutes}m`
      : '<1m';
  }
}
export default ProgressKeeper;

import { MatchedMutant, MutantResult } from '@stryker-mutator/api/report';

import ProgressBar from './ProgressBar';
import ProgressKeeper from './ProgressKeeper';

export default class ProgressBarReporter extends ProgressKeeper {
  private progressBar: ProgressBar;

  public onAllMutantsMatchedWithTests(matchedMutants: readonly MatchedMutant[]): void {
    super.onAllMutantsMatchedWithTests(matchedMutants);
    const progressBarContent =
      'Mutation testing  [:bar] :percent (elapsed: :et, remaining: :etc) :tested/:total tested (:survived survived, :timedOut timed out)';

    this.progressBar = new ProgressBar(progressBarContent, {
      complete: '=',
      incomplete: ' ',
      stream: process.stdout,
      total: this.progress.total,
      width: 50
    });
  }

  public onMutantTested(result: MutantResult): void {
    const ticksBefore = this.progress.tested;
    super.onMutantTested(result);

    const progressBarContent = { ...this.progress, et: this.getElapsedTime(), etc: this.getEtc() };

    if (ticksBefore < this.progress.tested) {
      this.tick(progressBarContent);
    } else {
      this.render(progressBarContent);
    }
  }

  private tick(tickObj: object): void {
    this.progressBar.tick(tickObj);
  }

  private render(renderObj: object): void {
    this.progressBar.render(renderObj);
  }
}

import { getEnvironmentVariable, getEnvironmentVariableOrThrow } from '../../utils/objectUtils';

import { CIProvider } from './Provider';

/**
 * https://circleci.com/docs/2.0/env-vars/#built-in-environment-variables
 */
class CircleProvider implements CIProvider {
  public determineProject(): string {
    return `${this.determineProvider()}/${this.determineRepository()}`;
  }
  public determineVersion(): string | undefined {
    return getEnvironmentVariable('CIRCLE_PR_NUMBER') || getEnvironmentVariable('CIRCLE_BRANCH') || getEnvironmentVariable('CIRCLE_TAG');
  }

  private determineRepository() {
    const username = getEnvironmentVariableOrThrow('CIRCLE_PROJECT_USERNAME');
    const repoName = getEnvironmentVariableOrThrow('CIRCLE_PROJECT_REPONAME');
    return `${username}/${repoName}`;
  }

  private determineProvider() {
    // Repo url can be in 2 forms:
    // - 'git@github.com:company/repo.git'
    // - 'https://github.com/company/repo'
    // See https://discuss.circleci.com/t/circle-repository-url-changed-format-in-v2/15273
    const repoUrl = getEnvironmentVariableOrThrow('CIRCLE_REPOSITORY_URL');
    if (repoUrl.startsWith('git@')) {
      return repoUrl.substr(4).split(':')[0];
    } else {
      return repoUrl.split('//')[1].split('/')[0];
    }
  }
}

export default CircleProvider;

import { getEnvironmentVariableOrThrow } from '../../utils/objectUtils';

import { CIProvider } from './Provider';

/**
 * https://help.github.com/en/actions/automating-your-workflow-with-github-actions/using-environment-variables#default-environment-variables
 */
export default class GithubActionsCIProvider implements CIProvider {
  public determineProject(): string {
    return `github.com/${getEnvironmentVariableOrThrow('GITHUB_REPOSITORY')}`;
  }
  public determineVersion(): string {
    const rawRef = getEnvironmentVariableOrThrow('GITHUB_REF');
    // rawRef will be in the form "refs/pull/:prNumber/merge" or "refs/heads/feat/branch-1"
    const [, type, ...name] = rawRef.split('/');
    if (type === 'pull') {
      return `PR-${name[0]}`;
    } else {
      return name.join('/');
    }
  }
}

import { getEnvironmentVariable } from '../../utils/objectUtils';

import CircleProvider from './CircleProvider';
import TravisProvider from './TravisProvider';
import GithubActionsCIProvider from './GithubActionsProvider';

/**
 * Represents an object that can provide information about a CI/CD provider.
 */
export interface CIProvider {
  /**
   * Determine the repository slug, including the git provider. I.E: github.com/stryker-mutator/stryker or bitbucket.org/org/name.
   */
  determineProject(): string | undefined;
  /**
   * Determine the current version. I.e. branch name, git sha, or tag name
   */
  determineVersion(): string | undefined;
}

/**
 * Return an appropriate instance of CiProvider.
 * @returns An instance of CiProvider, or `null` if it appears Stryker is not running in a CI/CD environment.
 */
export function determineCIProvider() {
  // By far the coolest env. variable from all those listed at
  // https://docs.travis-ci.com/user/environment-variables/#Default-Environment-Variables
  if (getEnvironmentVariable('HAS_JOSH_K_SEAL_OF_APPROVAL')) {
    return new TravisProvider();
  } else if (getEnvironmentVariable('CIRCLECI')) {
    return new CircleProvider();
  } else if (getEnvironmentVariable('GITHUB_ACTION')) {
    return new GithubActionsCIProvider();
  }
  // TODO: Add vsts and gitlab CI

  return null;
}

import { getEnvironmentVariable } from '../../utils/objectUtils';

import { CIProvider } from './Provider';

/**
 * See https://docs.travis-ci.com/user/environment-variables/#default-environment-variables
 */
class TravisProvider implements CIProvider {
  public determineProject(): string | undefined {
    const slug = getEnvironmentVariable('TRAVIS_REPO_SLUG');
    if (slug) {
      return `github.com/${slug}`;
    } else {
      return undefined;
    }
  }
  public determineVersion(): string | undefined {
    return getEnvironmentVariable('TRAVIS_PULL_REQUEST_BRANCH') || getEnvironmentVariable('TRAVIS_BRANCH');
  }
}

export default TravisProvider;

import { StrykerOptions, ReportType } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { mutationTestReportSchema, Reporter } from '@stryker-mutator/api/report';
import { calculateMetrics } from 'mutation-testing-metrics';

import { CIProvider } from '../ci/Provider';

import DashboardReporterClient from './DashboardReporterClient';
import { dashboardReporterTokens } from './tokens';
import { Report } from './Report';

export default class DashboardReporter implements Reporter {
  public static readonly inject = tokens(
    commonTokens.logger,
    dashboardReporterTokens.dashboardReporterClient,
    commonTokens.options,
    dashboardReporterTokens.ciProvider
  );

  constructor(
    private readonly log: Logger,
    private readonly dashboardReporterClient: DashboardReporterClient,
    private readonly options: StrykerOptions,
    private readonly ciProvider: CIProvider | null
  ) {}

  private onGoingWork: Promise<void> | undefined;

  public onMutationTestReportReady(result: mutationTestReportSchema.MutationTestResult): void {
    this.onGoingWork = (async () => {
      const { projectName, version, moduleName } = this.getContextFromEnvironment();
      if (projectName && version) {
        await this.update(this.toReport(result), projectName, version, moduleName);
      } else {
        this.log.info(
          'The report was not send to the dashboard. The dashboard.project and/or dashboard.version values were missing and not detected to be running on a build server.'
        );
      }
    })();
  }

  public async wrapUp() {
    await this.onGoingWork;
  }

  private toReport(result: mutationTestReportSchema.MutationTestResult): Report {
    if (this.options.dashboard.reportType === ReportType.Full) {
      return result;
    } else {
      return {
        mutationScore: calculateMetrics(result.files).metrics.mutationScore
      };
    }
  }

  private async update(report: Report, projectName: string, version: string, moduleName: string | undefined) {
    try {
      const href = await this.dashboardReporterClient.updateReport({
        report,
        moduleName,
        projectName,
        version: version
      });
      this.log.info('Report available at: %s', href);
    } catch (err) {
      this.log.error('Could not upload report.', err);
    }
  }

  private getContextFromEnvironment() {
    return {
      moduleName: this.options.dashboard.module,
      projectName: this.options.dashboard.project || (this.ciProvider && this.ciProvider.determineProject()),
      version: this.options.dashboard.version || (this.ciProvider && this.ciProvider.determineVersion())
    };
  }
}

import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { StrykerError } from '@stryker-mutator/util';
import { HttpClient } from 'typed-rest-client/HttpClient';
import { StrykerOptions } from '@stryker-mutator/api/core';

import { isOK } from '../../utils/netUtils';
import { getEnvironmentVariable } from '../../utils/objectUtils';

import { dashboardReporterTokens } from './tokens';
import { Report } from './Report';

interface ReportResponseBody {
  href: string;
}

const STRYKER_DASHBOARD_API_KEY = 'STRYKER_DASHBOARD_API_KEY';

export default class DashboardReporterClient {
  public static inject = tokens(commonTokens.logger, dashboardReporterTokens.httpClient, commonTokens.options);
  constructor(private readonly log: Logger, private readonly httpClient: HttpClient, private readonly options: StrykerOptions) {}

  public async updateReport({
    report,
    projectName,
    version,
    moduleName
  }: {
    report: Report;
    projectName: string;
    version: string;
    moduleName: string | undefined;
  }): Promise<string> {
    const url = this.getPutUrl(projectName, version, moduleName);
    const serializedBody = JSON.stringify(report);
    this.log.info('PUT report to %s (~%s bytes)', url, serializedBody.length);
    const apiKey = getEnvironmentVariable(STRYKER_DASHBOARD_API_KEY);
    if (apiKey) {
      this.log.debug('Using configured API key from environment "%s"', STRYKER_DASHBOARD_API_KEY);
    }
    this.log.trace('PUT report %s', serializedBody);
    const result = await this.httpClient.put(url, serializedBody, {
      ['X-Api-Key']: apiKey,
      ['Content-Type']: 'application/json'
    });
    const responseBody = await result.readBody();
    if (isOK(result.message.statusCode || 0)) {
      const response: ReportResponseBody = JSON.parse(responseBody);
      return response.href;
    } else if (result.message.statusCode === 401) {
      throw new StrykerError(
        `Error HTTP PUT ${url}. Unauthorized. Did you provide the correct api key in the "${STRYKER_DASHBOARD_API_KEY}" environment variable?`
      );
    } else {
      throw new StrykerError(`Error HTTP PUT ${url}. Response status code: ${result.message.statusCode}. Response body: ${responseBody}`);
    }
  }

  private getPutUrl(repoSlug: string, version: string, moduleName: string | undefined) {
    const base = `${this.options.dashboard.baseUrl}/${repoSlug}/${encodeURIComponent(version)}`;
    if (moduleName) {
      return `${base}?module=${encodeURIComponent(moduleName)}`;
    } else {
      return base;
    }
  }
}

