import { StrykerOptions } from '@stryker-mutator/api/core';
import { RunOptions, RunResult, TestRunner } from '@stryker-mutator/api/test_runner';

import ChildProcessCrashedError from '../child-proxy/ChildProcessCrashedError';
import ChildProcessProxy from '../child-proxy/ChildProcessProxy';
import LoggingClientContext from '../logging/LoggingClientContext';
import { timeout } from '../utils/objectUtils';

import { ChildProcessTestRunnerWorker } from './ChildProcessTestRunnerWorker';

const MAX_WAIT_FOR_DISPOSE = 2000;

/**
 * Runs the given test runner in a child process and forwards reports about test results
 */
export default class ChildProcessTestRunnerDecorator implements TestRunner {
  private readonly worker: ChildProcessProxy<ChildProcessTestRunnerWorker>;

  constructor(options: StrykerOptions, sandboxFileNames: readonly string[], sandboxWorkingDirectory: string, loggingContext: LoggingClientContext) {
    this.worker = ChildProcessProxy.create(
      require.resolve(`./${ChildProcessTestRunnerWorker.name}`),
      loggingContext,
      options,
      { sandboxFileNames },
      sandboxWorkingDirectory,
      ChildProcessTestRunnerWorker
    );
  }

  public init(): Promise<void> {
    return this.worker.proxy.init();
  }

  public run(options: RunOptions): Promise<RunResult> {
    return this.worker.proxy.run(options);
  }

  public async dispose(): Promise<void> {
    await timeout(
      // First let the inner test runner dispose
      this.worker.proxy.dispose().catch(error => {
        // It's OK if the child process is already down.
        if (!(error instanceof ChildProcessCrashedError)) {
          throw error;
        }
      }),

      // ... but don't wait forever on that
      MAX_WAIT_FOR_DISPOSE
    );

    // After that, dispose the child process itself
    await this.worker.dispose();
  }
}

import { StrykerOptions } from '@stryker-mutator/api/core';
import { commonTokens, Injector, OptionsContext, PluginKind, tokens } from '@stryker-mutator/api/plugin';
import { RunOptions, TestRunner } from '@stryker-mutator/api/test_runner';
import { errorToString } from '@stryker-mutator/util';

import { PluginCreator } from '../di';

export class ChildProcessTestRunnerWorker implements TestRunner {
  private readonly underlyingTestRunner: TestRunner;

  public static inject = tokens(commonTokens.sandboxFileNames, commonTokens.options, commonTokens.injector);
  constructor(sandboxFileNames: readonly string[], { testRunner }: StrykerOptions, injector: Injector<OptionsContext>) {
    this.underlyingTestRunner = injector
      .provideValue(commonTokens.sandboxFileNames, sandboxFileNames)
      .injectFunction(PluginCreator.createFactory(PluginKind.TestRunner))
      .create(testRunner);
  }

  public async init(): Promise<void> {
    if (this.underlyingTestRunner.init) {
      await this.underlyingTestRunner.init();
    }
  }

  public async dispose() {
    if (this.underlyingTestRunner.dispose) {
      await this.underlyingTestRunner.dispose();
    }
  }

  public async run(options: RunOptions) {
    const result = await this.underlyingTestRunner.run(options);
    // If the test runner didn't report on coverage, let's try to do it ourselves.
    if (!result.coverage) {
      result.coverage = (global as any).__coverage__;
    }
    if (result.errorMessages) {
      // errorMessages should be a string[]
      // Just in case the test runner implementer forgot to convert `Error`s to string, we will do it here
      // https://github.com/stryker-mutator/stryker/issues/141
      result.errorMessages = result.errorMessages.map(errorToString);
    }
    return result;
  }
}

import { exec } from 'child_process';
import * as os from 'os';

import { StrykerOptions } from '@stryker-mutator/api/core';
import { RunResult, RunStatus, TestRunner, TestStatus } from '@stryker-mutator/api/test_runner';
import { errorToString } from '@stryker-mutator/util';

import { kill } from '../utils/objectUtils';
import Timer from '../utils/Timer';

export interface CommandRunnerSettings {
  command: string;
}

/**
 * A test runner that uses a (bash or cmd) command to execute the tests.
 * Does not know hom many tests are executed or any code coverage results,
 * instead, it mimics a simple test result based on the exit code.
 * The command can be configured, but defaults to `npm test`.
 */
export default class CommandTestRunner implements TestRunner {
  /**
   * "command"
   */
  public static readonly runnerName = CommandTestRunner.name.replace('TestRunner', '').toLowerCase();

  /**
   * Determines whether a given name is "command" (ignore case)
   * @param name Maybe "command", maybe not
   */
  public static is(name: string): boolean {
    return this.runnerName === name.toLowerCase();
  }

  private readonly settings: CommandRunnerSettings;

  private timeoutHandler: undefined | (() => Promise<void>);

  constructor(private readonly workingDir: string, options: StrykerOptions) {
    this.settings = Object.assign(
      {
        command: 'npm test'
      },
      options.commandRunner
    );
  }

  public run(): Promise<RunResult> {
    return new Promise((res, rej) => {
      const timer = new Timer();
      const output: Array<string | Buffer> = [];
      const childProcess = exec(this.settings.command, { cwd: this.workingDir });
      childProcess.on('error', error => {
        kill(childProcess.pid)
          .then(() => handleResolve(errorResult(error)))
          .catch(rej);
      });
      childProcess.on('exit', code => {
        const result = completeResult(code, timer);
        handleResolve(result);
      });
      childProcess.stdout.on('data', chunk => {
        output.push(chunk);
      });
      childProcess.stderr.on('data', chunk => {
        output.push(chunk);
      });

      this.timeoutHandler = async () => {
        handleResolve({ status: RunStatus.Timeout, tests: [] });
        await kill(childProcess.pid);
      };

      const handleResolve = (runResult: RunResult) => {
        removeAllListeners();
        this.timeoutHandler = undefined;
        res(runResult);
      };

      function removeAllListeners() {
        childProcess.stderr.removeAllListeners();
        childProcess.stdout.removeAllListeners();
        childProcess.removeAllListeners();
      }

      function errorResult(error: Error): RunResult {
        return {
          errorMessages: [errorToString(error)],
          status: RunStatus.Error,
          tests: []
        };
      }

      function completeResult(exitCode: number | null, timer: Timer): RunResult {
        const duration = timer.elapsedMs();
        if (exitCode === 0) {
          return {
            status: RunStatus.Complete,
            tests: [
              {
                name: 'All tests',
                status: TestStatus.Success,
                timeSpentMs: duration
              }
            ]
          };
        } else {
          return {
            status: RunStatus.Complete,
            tests: [
              {
                failureMessages: [output.map(buf => buf.toString()).join(os.EOL)],
                name: 'All tests',
                status: TestStatus.Failed,
                timeSpentMs: duration
              }
            ]
          };
        }
      }
    });
  }
  public async dispose(): Promise<void> {
    if (this.timeoutHandler) {
      await this.timeoutHandler();
    }
  }
}

import { StrykerOptions } from '@stryker-mutator/api/core';
