import TimeoutDecorator from './TimeoutDecorator';

export default {
  create(
    options: StrykerOptions,
    sandboxFileNames: readonly string[],
    sandboxWorkingDirectory: string,
    loggingContext: LoggingClientContext
  ): Required<TestRunner> {
    if (CommandTestRunner.is(options.testRunner)) {
      return new RetryDecorator(() => new TimeoutDecorator(() => new CommandTestRunner(sandboxWorkingDirectory, options)));
    } else {
      return new RetryDecorator(
        () => new TimeoutDecorator(() => new ChildProcessTestRunnerDecorator(options, sandboxFileNames, sandboxWorkingDirectory, loggingContext))
      );
    }
  }
};

import { RunOptions, RunResult, RunStatus } from '@stryker-mutator/api/test_runner';
import { errorToString } from '@stryker-mutator/util';
import { getLogger } from 'log4js';

import OutOfMemoryError from '../child-proxy/OutOfMemoryError';

import TestRunnerDecorator from './TestRunnerDecorator';

const ERROR_MESSAGE = 'Test runner crashed. Tried twice to restart it without any luck. Last time the error message was: ';

/**
 * Wraps a test runner and implements the retry functionality.
 */
export default class RetryDecorator extends TestRunnerDecorator {
  private readonly log = getLogger(RetryDecorator.name);

  public async run(options: RunOptions, attemptsLeft = 2, lastError?: Error): Promise<RunResult> {
    if (attemptsLeft > 0) {
      try {
        return await this.innerRunner.run(options);
      } catch (error) {
        if (error instanceof OutOfMemoryError) {
          this.log.info(
            "Test runner process [%s] ran out of memory. You probably have a memory leak in your tests. Don't worry, Stryker will restart the process, but you might want to investigate this later, because this decreases performance.",
            error.pid
          );
        }
        await this.recover();
        return this.run(options, attemptsLeft - 1, error);
      }
    } else {
      await this.recover();
      return { status: RunStatus.Error, errorMessages: [`${ERROR_MESSAGE}${errorToString(lastError)}`], tests: [] };
    }
  }

  private async recover(): Promise<void> {
    await this.dispose();
    this.createInnerRunner();
    return this.init();
  }
}

import { RunOptions, RunResult, TestRunner } from '@stryker-mutator/api/test_runner';

export default class TestRunnerDecorator implements Required<TestRunner> {
  protected innerRunner: TestRunner;

  constructor(private readonly testRunnerProducer: () => TestRunner) {
    this.createInnerRunner();
  }

  public init(): Promise<void> {
    if (this.innerRunner.init) {
      return this.innerRunner.init() || Promise.resolve();
    } else {
      return Promise.resolve();
    }
  }

  protected createInnerRunner() {
    this.innerRunner = this.testRunnerProducer();
  }

  public run(options: RunOptions): Promise<RunResult> {
    return this.innerRunner.run(options);
  }

  public dispose(): Promise<any> {
    if (this.innerRunner.dispose) {
      return this.innerRunner.dispose() || Promise.resolve();
    } else {
      return Promise.resolve();
    }
  }
}

import { RunOptions, RunResult, RunStatus } from '@stryker-mutator/api/test_runner';
import { getLogger } from 'log4js';

import { timeout, TimeoutExpired } from '../utils/objectUtils';

import TestRunnerDecorator from './TestRunnerDecorator';

/**
 * Wraps a test runner and implements the timeout functionality.
 */
export default class TimeoutDecorator extends TestRunnerDecorator {
  private readonly log = getLogger(TimeoutDecorator.name);

  public async run(options: RunOptions): Promise<RunResult> {
    this.log.debug('Starting timeout timer (%s ms) for a test run', options.timeout);
    const result = await timeout(super.run(options), options.timeout);
    if (result === TimeoutExpired) {
      return this.handleTimeout();
    } else {
      return result;
    }
  }

  private handleTimeout(): Promise<RunResult> {
    this.log.debug('Timeout expired, restarting the process and reporting timeout');
    return this.dispose()
      .then(() => this.createInnerRunner())
      .then(() => this.init())
      .then(() => ({ status: RunStatus.Timeout, tests: [] }));
  }
}

