import { File, StrykerOptions } from '@stryker-mutator/api/core';
import { Transpiler } from '@stryker-mutator/api/transpile';
import { StrykerError } from '@stryker-mutator/util';
import { FileCoverageData, Range } from 'istanbul-lib-coverage';
import { createInstrumenter, Instrumenter } from 'istanbul-lib-instrument';

import { COVERAGE_CURRENT_TEST_VARIABLE_NAME } from './coverageHooks';

export interface CoverageMaps {
  statementMap: { [key: string]: Range };
  fnMap: { [key: string]: Range };
}

export interface CoverageMapsByFile {
  [file: string]: CoverageMaps;
}

export default class CoverageInstrumenterTranspiler implements Transpiler {
  private readonly instrumenter: Instrumenter;
  public fileCoverageMaps: CoverageMapsByFile = Object.create(null);

  constructor(private readonly settings: StrykerOptions, private readonly filesToInstrument: readonly string[]) {
    this.instrumenter = createInstrumenter({ coverageVariable: this.coverageVariable, preserveComments: true });
  }

  public async transpile(files: readonly File[]): Promise<readonly File[]> {
    return files.map(file => this.instrumentFileIfNeeded(file));
  }

  /**
   * Coverage variable *must* have the name '__coverage__'. Only that variable
   * is reported back to the TestRunner process when using one of the karma
   * test framework adapters (karma-jasmine, karma-mocha, ...).
   *
   * However, when coverageAnalysis is 'perTest' we don't choose that variable name right away,
   * because we need that variable to hold all coverage results per test. Instead, we use __strykerCoverageCurrentTest__
   * and after each test copy over the value of that current test to the global coverage object __coverage__
   */
  private get coverageVariable() {
    switch (this.settings.coverageAnalysis) {
      case 'perTest':
        return COVERAGE_CURRENT_TEST_VARIABLE_NAME;
      default:
        return '__coverage__';
    }
  }

  private patchRanges(fileCoverage: FileCoverageData) {
    function patchRange(range: Range) {
      // Lines from istanbul are one-based, lines in Stryker are 0-based
      range.end.line--;
      range.start.line--;
    }

    Object.keys(fileCoverage.statementMap).forEach(key => patchRange(fileCoverage.statementMap[key]));
    Object.keys(fileCoverage.branchMap).forEach(key => {
      patchRange(fileCoverage.branchMap[key].loc);
      fileCoverage.branchMap[key].locations.forEach(patchRange);
      fileCoverage.branchMap[key].line--;
    });
    Object.keys(fileCoverage.fnMap).forEach(key => {
      patchRange(fileCoverage.fnMap[key].loc);
      patchRange(fileCoverage.fnMap[key].decl);
      fileCoverage.fnMap[key].line--;
    });

    return fileCoverage;
  }

  private instrumentFileIfNeeded(file: File) {
    if (this.settings.coverageAnalysis !== 'off' && this.filesToInstrument.some(fileName => fileName === file.name)) {
      return this.instrumentFile(file);
    } else {
      return file;
    }
  }

  private instrumentFile(sourceFile: File): File {
    try {
      const content = this.instrumenter.instrumentSync(sourceFile.textContent, sourceFile.name);
      const fileCoverage = this.patchRanges(this.instrumenter.lastFileCoverage());
      this.fileCoverageMaps[sourceFile.name] = this.retrieveCoverageMaps(fileCoverage);
      return new File(sourceFile.name, Buffer.from(content));
    } catch (error) {
      throw new StrykerError(`Could not instrument "${sourceFile.name}" for code coverage`, error);
    }
  }

  private retrieveCoverageMaps(input: FileCoverageData): CoverageMaps {
    const output: CoverageMaps = {
      fnMap: {},
      statementMap: input.statementMap
    };
    Object.keys(input.fnMap).forEach(key => (output.fnMap[key] = input.fnMap[key].loc));
    return output;
  }
}

import { File } from '@stryker-mutator/api/core';
import { Disposable } from '@stryker-mutator/api/plugin';
import { tokens } from '@stryker-mutator/api/plugin';
import { Transpiler } from '@stryker-mutator/api/transpile';
import { errorToString } from '@stryker-mutator/util';
import { Observable, range } from 'rxjs';
import { BehaviorSubject } from 'rxjs';
import { from } from 'rxjs';
import { flatMap, zip } from 'rxjs/operators';

import { coreTokens } from '../di';
import SourceFile from '../SourceFile';
import TestableMutant from '../TestableMutant';
import TranspiledMutant from '../TranspiledMutant';

import TranspileResult from './TranspileResult';

const INITIAL_CONCURRENCY = 100;

export class MutantTranspileScheduler implements Disposable {
  private currentMutatedFile: SourceFile;
  private readonly concurrencyTicket$ = new BehaviorSubject<number>(INITIAL_CONCURRENCY);

  public static inject = tokens(coreTokens.transpiler, coreTokens.transpiledFiles);

  /**
   * Creates a mutant transpiler
   */
  constructor(private readonly transpiler: Transpiler, private readonly unMutatedFiles: readonly File[]) {}

  public scheduleTranspileMutants(allMutants: readonly TestableMutant[]): Observable<TranspiledMutant> {
    return from(allMutants).pipe(
      zip(this.concurrencyTicket$.pipe(flatMap(n => range(0, n)))),
      flatMap(([mutant]) => this.transpileMutant(mutant), 1 /* IMPORTANT! Never transpile multiple mutants at once! */)
    );
  }

  /**
   * Schedule next mutant to be transpiled
   */
  public readonly scheduleNext = () => {
    this.concurrencyTicket$.next(1);
  };

  /**
   * Dispose
   */
  public dispose() {
    this.concurrencyTicket$.complete();
  }

  private createTranspiledMutant(mutant: TestableMutant, transpileResult: TranspileResult) {
    return new TranspiledMutant(mutant, transpileResult, someFilesChanged(this.unMutatedFiles));

    function someFilesChanged(unMutatedFiles: readonly File[]): boolean {
      return transpileResult.outputFiles.some(file => fileChanged(file, unMutatedFiles));
    }

    function fileChanged(file: File, unMutatedFiles: readonly File[]) {
      if (unMutatedFiles) {
        const unMutatedFile = unMutatedFiles.find(f => f.name === file.name);
        return !unMutatedFile || unMutatedFile.textContent !== file.textContent;
      } else {
        return true;
      }
    }
  }

  private async transpileMutant(mutant: TestableMutant): Promise<TranspiledMutant> {
    const filesToTranspile: File[] = [];
    if (this.currentMutatedFile && this.currentMutatedFile.name !== mutant.fileName) {
      filesToTranspile.push(this.currentMutatedFile.file);
    }
    this.currentMutatedFile = mutant.sourceFile;
    const mutatedFile = new File(mutant.fileName, Buffer.from(mutant.mutatedCode));
    filesToTranspile.push(mutatedFile);
    try {
      const transpiledFiles = await this.transpiler.transpile(filesToTranspile);
      return this.createTranspiledMutant(mutant, { outputFiles: transpiledFiles, error: null });
    } catch (error) {
      return this.createTranspiledMutant(mutant, { outputFiles: [], error: errorToString(error) });
    }
  }
}

import * as path from 'path';

import { File, Location, Position, StrykerOptions } from '@stryker-mutator/api/core';
import { StrykerError } from '@stryker-mutator/util';
import { getLogger } from 'log4js';
import { RawSourceMap, SourceMapConsumer } from 'source-map';

import { base64Decode } from '../utils/objectUtils';

const SOURCE_MAP_URL_REGEX = /\/\/\s*#\s*sourceMappingURL=(.*)/g;

// This file contains source mapping logic.
// It reads transpiled output files (*.js) and scans it for comments like these: sourceMappingURL=*.js.map
// If it finds it, it will use mozilla's source-map to implement the `transpiledLocationFor` method.

export interface MappedLocation {
  fileName: string;
  location: Location;
}

export class SourceMapError extends StrykerError {
  constructor(message: string, innerError?: Error) {
    super(
      `${message}. Cannot analyse code coverage. Setting \`coverageAnalysis: "off"\` in your stryker.conf.js will prevent this error, but forces Stryker to run each test for each mutant.`,
      innerError
    );
    Error.captureStackTrace(this, SourceMapError);
    // TS recommendation: https://github.com/Microsoft/TypeScript/wiki/Breaking-Changes#extending-built-ins-like-error-array-and-map-may-no-longer-work
    Object.setPrototypeOf(this, SourceMapError.prototype);
  }
}

/**
 * Represents an object that can calculated a transpiled location for a given original location
 * It is implemented with the [composite pattern](https://en.wikipedia.org/wiki/Composite_pattern)
 * Use the `create` method to retrieve a specific `SourceMapper` implementation
 */
export default abstract class SourceMapper {
  /**
   * Calculated a transpiled location for a given original location
   * @param originalLocation The original location to be converted to a transpiled location
   */
  public abstract async transpiledLocationFor(originalLocation: MappedLocation): Promise<MappedLocation>;

  public abstract transpiledFileNameFor(originalFileName: string): string;

  public static create(transpiledFiles: readonly File[], options: StrykerOptions): SourceMapper {
    if (options.transpilers.length && options.coverageAnalysis !== 'off') {
      return new TranspiledSourceMapper(transpiledFiles);
    } else {
      return new PassThroughSourceMapper();
    }
  }
}

export class TranspiledSourceMapper extends SourceMapper {
  private sourceMaps: SourceMapBySource;
  private readonly log = getLogger(SourceMapper.name);

  constructor(private readonly transpiledFiles: readonly File[]) {
    super();
  }

  /**
   * @inheritDoc
   */
  public transpiledFileNameFor(originalFileName: string) {
    const sourceMap = this.getSourceMap(originalFileName);
    return sourceMap.transpiledFile.name;
  }

  /**
   * @inheritdoc
   */
  public async transpiledLocationFor(originalLocation: MappedLocation): Promise<MappedLocation> {
    const sourceMap = this.getSourceMap(originalLocation.fileName);
    const relativeSource = this.getRelativeSource(sourceMap, originalLocation);
    const start = await sourceMap.generatedPositionFor(originalLocation.location.start, relativeSource);
    const end = await sourceMap.generatedPositionFor(originalLocation.location.end, relativeSource);
    return {
      fileName: sourceMap.transpiledFile.name,
      location: {
        end,
        start
      }
    };
  }

  private getRelativeSource(from: SourceMap, to: MappedLocation) {
    return path.relative(path.dirname(from.sourceMapFileName), to.fileName).replace(/\\/g, '/');
  }

  /**
   * Gets the source map for given file
   */
  private getSourceMap(sourceFileName: string): SourceMap {
    if (!this.sourceMaps) {
      this.sourceMaps = this.createSourceMaps();
    }
    const sourceMap: SourceMap | undefined = this.sourceMaps[path.resolve(sourceFileName)];
    if (sourceMap) {
      return sourceMap;
    } else {
      throw new SourceMapError(`Source map not found for "${sourceFileName}"`);
    }
  }

  /**
   * Creates all source maps for lazy loading purposes
   */
  private createSourceMaps(): SourceMapBySource {
    const sourceMaps: SourceMapBySource = Object.create(null);
    this.transpiledFiles.forEach(transpiledFile => {
      const sourceMapFile = this.getSourceMapForFile(transpiledFile);
      if (sourceMapFile) {
        const rawSourceMap = this.getRawSourceMap(sourceMapFile);
        const sourceMap = new SourceMap(transpiledFile, sourceMapFile.name, rawSourceMap);
        rawSourceMap.sources.forEach(source => {
          const sourceFileName = path.resolve(path.dirname(sourceMapFile.name), source);
          sourceMaps[sourceFileName] = sourceMap;
        });
      }
    });
    return sourceMaps;
  }

  private getRawSourceMap(sourceMapFile: File): RawSourceMap {
    try {
      return JSON.parse(sourceMapFile.textContent);
    } catch (error) {
      throw new SourceMapError(`Source map file "${sourceMapFile.name}" could not be parsed as json`, error);
    }
  }

  private getSourceMapForFile(transpiledFile: File): File | null {
    const sourceMappingUrl = this.getSourceMapUrl(transpiledFile);
    if (sourceMappingUrl) {
      return this.getSourceMapFileFromUrl(sourceMappingUrl, transpiledFile);
    } else {
      return null;
    }
  }

  /**
   * Gets the source map file from a url.
   * @param sourceMapUrl The source map url. Can be a data url (data:application/json;base64,ABC...), or an actual file url
   * @param transpiledFile The transpiled file for which the data url is
   */
  private getSourceMapFileFromUrl(sourceMapUrl: string, transpiledFile: File): File {
    const sourceMapFile = this.isInlineUrl(sourceMapUrl)
      ? this.getInlineSourceMap(sourceMapUrl, transpiledFile)
      : this.getExternalSourceMap(sourceMapUrl, transpiledFile);
    return sourceMapFile;
  }

  private isInlineUrl(sourceMapUrl: string) {
    return sourceMapUrl.startsWith('data:');
  }

  /**
   * Gets the source map from a data url
   */
  private getInlineSourceMap(sourceMapUrl: string, transpiledFile: File): File {
    const supportedDataPrefix = 'data:application/json;base64,';
    if (sourceMapUrl.startsWith(supportedDataPrefix)) {
      const content = base64Decode(sourceMapUrl.substr(supportedDataPrefix.length));
      return new File(transpiledFile.name, content);
    } else {
      throw new SourceMapError(
        `Source map file for "${transpiledFile.name}" cannot be read. Data url "${sourceMapUrl.substr(
          0,
          sourceMapUrl.lastIndexOf(',')
        )}" found, where "${supportedDataPrefix.substr(0, supportedDataPrefix.length - 1)}" was expected`
      );
    }
  }

  /**
   * Gets the source map from a file
   */
  private getExternalSourceMap(sourceMapUrl: string, transpiledFile: File) {
    const sourceMapFileName = path.resolve(path.dirname(transpiledFile.name), sourceMapUrl);
    const sourceMapFile = this.transpiledFiles.find(file => path.resolve(file.name) === sourceMapFileName);
    if (sourceMapFile) {
      return sourceMapFile;
    } else {
      throw new SourceMapError(
        `Source map file "${sourceMapUrl}" (referenced by "${transpiledFile.name}") cannot be found in list of transpiled files`
      );
    }
  }

  /**
   * Gets the source map url from a transpiled file (the last comment with sourceMappingURL= ...)
   */
  private getSourceMapUrl(transpiledFile: File): string | null {
    SOURCE_MAP_URL_REGEX.lastIndex = 0;
    let currentMatch: RegExpExecArray | null;
    let lastMatch: RegExpExecArray | null = null;
    // Retrieve the final sourceMappingURL comment in the file
    while ((currentMatch = SOURCE_MAP_URL_REGEX.exec(transpiledFile.textContent))) {
      lastMatch = currentMatch;
    }
    if (lastMatch) {
      this.log.debug('Source map url found in transpiled file "%s"', transpiledFile.name);
      return lastMatch[1];
    } else {
      this.log.debug('No source map url found in transpiled file "%s"', transpiledFile.name);
      return null;
    }
  }
}

export class PassThroughSourceMapper extends SourceMapper {
  /**
   * @inheritdoc
   */
  public transpiledFileNameFor(originalFileName: string): string {
    return originalFileName;
  }

  /**
   * @inheritdoc
   */
  public async transpiledLocationFor(originalLocation: MappedLocation): Promise<MappedLocation> {
    return Promise.resolve(originalLocation);
  }
}

class SourceMap {
  private sourceMap: SourceMapConsumer | undefined;
  constructor(public transpiledFile: File, public sourceMapFileName: string, private readonly rawSourceMap: RawSourceMap) {}
  public async generatedPositionFor(originalPosition: Position, relativeSource: string): Promise<Position> {
    if (!this.sourceMap) {
      this.sourceMap = await new SourceMapConsumer(this.rawSourceMap);
    }

    const transpiledPosition = await this.sourceMap.generatedPositionFor({
      bias: SourceMapConsumer.LEAST_UPPER_BOUND,
      column: originalPosition.column,
      line: originalPosition.line + 1, // SourceMapConsumer works 1-based
      source: relativeSource
    });

    return Promise.resolve({
      column: transpiledPosition.column || 0,
      line: (transpiledPosition.line || 1) - 1 // Stryker works 0-based
    });
  }
}

interface SourceMapBySource {
  [sourceFileName: string]: SourceMap;
}

import { File, StrykerOptions } from '@stryker-mutator/api/core';
import { commonTokens, PluginKind, tokens } from '@stryker-mutator/api/plugin';
import { Transpiler } from '@stryker-mutator/api/transpile';
import { StrykerError } from '@stryker-mutator/util';

import { coreTokens } from '../di';
import { PluginCreator } from '../di/PluginCreator';

class NamedTranspiler {
  constructor(public name: string, public transpiler: Transpiler) {}
}

export class TranspilerFacade implements Transpiler {
  private readonly innerTranspilers: NamedTranspiler[];

  public static inject = tokens(commonTokens.options, coreTokens.pluginCreatorTranspiler);

  constructor(options: StrykerOptions, pluginCreator: PluginCreator<PluginKind.Transpiler>) {
    this.innerTranspilers = options.transpilers.map(transpilerName => new NamedTranspiler(transpilerName, pluginCreator.create(transpilerName)));
  }

  public transpile(files: readonly File[]): Promise<readonly File[]> {
    return this.performTranspileChain(files);
  }

  private async performTranspileChain(
    input: readonly File[],
    remainingChain: NamedTranspiler[] = this.innerTranspilers.slice()
  ): Promise<readonly File[]> {
    const current = remainingChain.shift();
    if (current) {
      const output = await current.transpiler.transpile(input).catch(error => {
        throw new StrykerError(`An error occurred in transpiler "${current.name}"`, error);
      });
      return this.performTranspileChain(output, remainingChain);
    } else {
      return input;
    }
  }
}

import { TestFramework } from '@stryker-mutator/api/test_framework';

import { wrapInClosure } from '../utils/objectUtils';

export const COVERAGE_CURRENT_TEST_VARIABLE_NAME = '__strykerCoverageCurrentTest__';

const cloneFunctionFragment = `
function clone(source) {
    var result = source;
    if (Array.isArray(source)) {
        result = [];
        source.forEach(function (child, index) {
            result[index] = clone(child);
        });
    } else if (typeof source == "object") {
        result = {};
        for (var i in source) {
            result[i] = clone(source[i]);
        }
    }
    return result;
}`;

const BEFORE_EACH_FRAGMENT_PER_TEST = `
if (!globalCoverage.baseline && window.${COVERAGE_CURRENT_TEST_VARIABLE_NAME}) {
globalCoverage.baseline = clone(window.${COVERAGE_CURRENT_TEST_VARIABLE_NAME});
}`;

const AFTER_EACH_FRAGMENT_PER_TEST = `
globalCoverage.deviations[id] = coverageResult = {};
id++;
var coveragePerFile = window.${COVERAGE_CURRENT_TEST_VARIABLE_NAME};
if(coveragePerFile) {
Object.keys(coveragePerFile).forEach(function (file) {
    var coverage = coveragePerFile[file];
    var baseline = globalCoverage.baseline[file];
    var fileResult = { s: {}, f: {} };
    var touchedFile = false;
    for(var i in coverage.s){
      if(!baseline || coverage.s[i] !== baseline.s[i]){
        fileResult.s[i] = coverage.s[i];
        touchedFile = true;
      }
    }
    for(var i in coverage.f){
      if(!baseline || coverage.f[i] !== baseline.f[i]){
        fileResult.f[i] = coverage.f[i];
        touchedFile = true;
      }
    }
    if(touchedFile){
      coverageResult[file] = fileResult;
    }
});
}`;

export function coveragePerTestHooks(testFramework: TestFramework): string {
  return wrapInClosure(`
        var id = 0, globalCoverage, coverageResult;
        window.__coverage__ = globalCoverage = { deviations: {} };
        ${testFramework.beforeEach(BEFORE_EACH_FRAGMENT_PER_TEST)}
        ${testFramework.afterEach(AFTER_EACH_FRAGMENT_PER_TEST)}
        ${cloneFunctionFragment};
    `);
}

import { File, StrykerOptions } from '@stryker-mutator/api/core';
