import LoggingClientContext from '../logging/LoggingClientContext';

import { ChildProcessTranspiler } from './ChildProcessTranspiler';

/**
 * Creates a transpiler. If one is configured, spawns that in a child process
 */
export function transpilerFactory(
  options: StrykerOptions,
  injector: Injector<TranspilerPluginContext & { [coreTokens.loggingContext]: LoggingClientContext }>
): Transpiler & Disposable {
  if (options.transpilers.length) {
    return injector.injectClass(ChildProcessTranspiler);
  } else {
    return {
      transpile(files: readonly File[]) {
        return Promise.resolve(files);
      },
      dispose() {
        // noop
      }
    };
  }
}
transpilerFactory.inject = tokens(commonTokens.options, commonTokens.injector);

import { Location } from '@stryker-mutator/api/core';

export default class LocationHelper {
  public static MAX_VALUE = new LocationHelper(
    Object.freeze({
      end: Object.freeze({ column: Number.POSITIVE_INFINITY, line: Number.POSITIVE_INFINITY }),
      start: Object.freeze({ column: 0, line: -1 })
    })
  );

  constructor(private readonly loc: Location) {}

  /**
   * Indicates whether the current location is covered by an other location.
   * @param maybeWrapper The location that is questioned to be wrapping this location.
   * @returns true if this location is covered by given location, otherwise false
   */
  public isCoveredBy(maybeWrapper: Location): boolean {
    const isAfterStart =
      this.loc.start.line > maybeWrapper.start.line ||
      (this.loc.start.line === maybeWrapper.start.line && this.loc.start.column >= maybeWrapper.start.column);
    const isBeforeEnd =
      this.loc.end.line < maybeWrapper.end.line || (this.loc.end.line === maybeWrapper.end.line && this.loc.end.column <= maybeWrapper.end.column);
    return isAfterStart && isBeforeEnd;
  }

  /**
   * Indicates whether the given location is smaller than this location.
   * @param maybeSmaller The area which is questioned to cover a smaller area than this location.
   * @returns true if the given location covers a smaller area than this one.
   */
  public isSmallerArea(maybeSmaller: Location) {
    let firstLocationHasSmallerArea = false;
    const lineDifference = this.loc.end.line - this.loc.start.line - (maybeSmaller.end.line - maybeSmaller.start.line);
    const coversLessLines = lineDifference > 0;
    const coversLessColumns =
      lineDifference === 0 && maybeSmaller.start.column - this.loc.start.column + (this.loc.end.column - maybeSmaller.end.column) > 0;
    if (coversLessLines || coversLessColumns) {
      firstLocationHasSmallerArea = true;
    }
    return firstLocationHasSmallerArea;
  }
}

import { EOL } from 'os';

const DEFAULT_MAX_SIZE = 2048;

export default class StringBuilder {
  private currentLength = 0;
  private readonly strings: string[] = [];
  private readonly maxSize = DEFAULT_MAX_SIZE;

  public append(str: string) {
    this.strings.push(str);
    this.currentLength += str.length;
    while (this.currentLength > this.maxSize && this.strings.length > 1) {
      const popped = this.strings.shift() as string;
      this.currentLength -= popped.length;
    }
  }

  public toString() {
    return this.strings.join('');
  }

  public static concat(...builders: StringBuilder[]): string {
    return builders
      .map(b => b.toString())
      .filter(Boolean)
      .join(EOL);
  }
}

import { timeout, TimeoutExpired } from './objectUtils';

/**
 * Wraps a promise in a Task api for convenience.
 */
export class Task<T = void> {
  protected _promise: Promise<T>;
  private resolveFn: (value?: T | PromiseLike<T>) => void;
  private rejectFn: (reason: any) => void;
  private _isCompleted = false;

  constructor() {
    this._promise = new Promise<T>((resolve, reject) => {
      this.resolveFn = resolve;
      this.rejectFn = reject;
    });
  }

  public get promise() {
    return this._promise;
  }

  public get isCompleted() {
    return this._isCompleted;
  }

  public resolve = (result: T | PromiseLike<T>): void => {
    this._isCompleted = true;
    this.resolveFn(result);
  };

  public reject = (reason: any): void => {
    this._isCompleted = true;
    this.rejectFn(reason);
  };
}

/**
 * A task that can expire after the given time.
 */
export class ExpirableTask<T = void> extends Task<T | typeof TimeoutExpired> {
  constructor(timeoutMS: number) {
    super();
    this._promise = timeout(this._promise, timeoutMS);
  }
}

import * as path from 'path';

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { fsAsPromised } from '@stryker-mutator/util';
import * as mkdirp from 'mkdirp';
import { Disposable } from 'typed-inject';

import { deleteDir } from './fileUtils';

export class TemporaryDirectory implements Disposable {
  private readonly temporaryDirectory: string;
  private isInitialized = false;

  public static readonly inject = tokens(commonTokens.logger, commonTokens.options);
  constructor(private readonly log: Logger, options: StrykerOptions) {
    this.temporaryDirectory = path.resolve(options.tempDirName);
  }

  public initialize() {
    this.isInitialized = true;
    this.log.debug('Using temp directory "%s"', this.temporaryDirectory);
    mkdirp.sync(this.temporaryDirectory);
  }

  /**
   * Creates a new random directory with the specified prefix.
   * @param prefix The prefix.
   * @returns The path to the directory.
   */
  public createRandomDirectory(prefix: string): string {
    if (!this.isInitialized) {
      throw new Error('initialize() was not called!');
    }
    const dir = path.resolve(this.temporaryDirectory, `${prefix}${this.random()}`);
    mkdirp.sync(dir);
    return dir;
  }

  /**
   * Copies a file.
   * @param fromFilename The path to the existing file.
   * @param toFilename The path to copy the file to.
   * @param instrumenter An optional additional instrumenter to stream the file through
   * @returns A promise to eventually copy the file.
   */
  public copyFile(fromFilename: string, toFilename: string, instrumenter: NodeJS.ReadWriteStream | null): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      let readStream: NodeJS.ReadableStream = fsAsPromised.createReadStream(fromFilename, { encoding: 'utf8' });
      const writeStream = fsAsPromised.createWriteStream(toFilename);
      readStream.on('error', reject);
      writeStream.on('error', reject);
      if (instrumenter) {
        readStream = readStream.pipe(instrumenter);
      }
      readStream.pipe(writeStream);
      readStream.on('end', () => resolve());
    });
  }

  /**
   * Deletes the Stryker-temp directory
   */
  public async dispose() {
    if (!this.isInitialized) {
      throw new Error('initialize() was not called!');
    }
    this.log.debug('Deleting stryker temp directory %s', this.temporaryDirectory);
    try {
      await deleteDir(this.temporaryDirectory);
    } catch (e) {
      return this.log.info(`Failed to delete stryker temp directory ${this.temporaryDirectory}`);
    }
  }

  /**
   * Creates a random integer number.
   * @returns A random integer.
   */
  public random(): number {
    return Math.ceil(Math.random() * 10000000);
  }
}

export default class Timer {
  private readonly now: () => Date;
  private start: Date;
  private markers: {
    [name: string]: Date;
  };

  constructor(now = () => new Date()) {
    this.now = now;
    this.reset();
  }

  public reset() {
    this.markers = Object.create(null);
    this.start = this.now();
  }

  public humanReadableElapsed() {
    const elapsedSeconds = this.elapsedSeconds();
    return Timer.humanReadableElapsedMinutes(elapsedSeconds) + Timer.humanReadableElapsedSeconds(elapsedSeconds);
  }

  public elapsedSeconds() {
    const elapsedMs = this.elapsedMs();
    return Math.floor(elapsedMs / 1000);
  }

  public elapsedMs(sinceMarker?: string) {
    if (sinceMarker && this.markers[sinceMarker]) {
      return this.now().getTime() - this.markers[sinceMarker].getTime();
    } else {
      return this.now().getTime() - this.start.getTime();
    }
  }

  public mark(name: string) {
    this.markers[name] = this.now();
  }

  private static humanReadableElapsedSeconds(elapsedSeconds: number) {
    const restSeconds = elapsedSeconds % 60;
    if (restSeconds === 1) {
      return `${restSeconds} second`;
    } else {
      return `${restSeconds} seconds`;
    }
  }

  private static humanReadableElapsedMinutes(elapsedSeconds: number) {
    const elapsedMinutes = Math.floor(elapsedSeconds / 60);
    if (elapsedMinutes > 1) {
      return `${elapsedMinutes} minutes `;
    } else if (elapsedMinutes > 0) {
      return `${elapsedMinutes} minute `;
    } else {
      return '';
    }
  }
}

import * as path from 'path';

import { fsAsPromised } from '@stryker-mutator/util';
import * as nodeGlob from 'glob';
import * as mkdirp from 'mkdirp';
import * as rimraf from 'rimraf';

export function glob(expression: string): Promise<string[]> {
  return new Promise<string[]>((resolve, reject) => {
    nodeGlob(expression, { nodir: true }, (error, matches) => {
      error ? reject(error) : resolve(matches);
    });
  });
}

export function deleteDir(dirToDelete: string): Promise<void> {
  return new Promise<void>((resolve, reject) => {
    rimraf(dirToDelete, error => {
      error ? reject(error) : resolve();
    });
  });
}

export async function cleanFolder(folderName: string) {
  try {
    await fsAsPromised.lstat(folderName);
    await deleteDir(folderName);
    return mkdirp.sync(folderName);
  } catch (e) {
    return mkdirp.sync(folderName);
  }
}

/**
 * Wrapper around the 'require' function (for testability)
 */
export function importModule(moduleName: string): unknown {
  return require(moduleName);
}

/**
 * Writes data to a specified file.
 * @param fileName The path to the file.
 * @param data The content of the file.
 * @returns A promise to eventually save the file.
 */
export function writeFile(fileName: string, data: string | Buffer): Promise<void> {
  if (Buffer.isBuffer(data)) {
    return fsAsPromised.writeFile(fileName, data);
  } else {
    return fsAsPromised.writeFile(fileName, data, 'utf8');
  }
}

/**
 * Creates a symlink at `from` that points to `to`
 * @param to The thing you want to point to
 * @param from The thing you want to point from
 */
export function symlinkJunction(to: string, from: string) {
  return fsAsPromised.symlink(to, from, 'junction');
}

/**
 * Looks for the node_modules folder from basePath up to root.
 * returns the first occurrence of the node_modules, or null of none could be found.
 * @param basePath starting point
 */
export async function findNodeModules(basePath: string): Promise<string | null> {
  basePath = path.resolve(basePath);
  const nodeModules = path.resolve(basePath, 'node_modules');
  if (await fsAsPromised.exists(nodeModules)) {
    return nodeModules;
  } else {
    const parent = path.dirname(basePath);
    if (parent === basePath) {
      return null;
    } else {
      return findNodeModules(path.dirname(basePath));
    }
  }
}

import getPort = require('get-port');

/**
 * A wrapper around `getPort` for testing purposes
 */
export const getFreePort = getPort;

export function isOK(statusCode: number): boolean {
  return statusCode >= 200 && statusCode < 300;
}

import treeKill = require('tree-kill');
import { StrykerError } from '@stryker-mutator/util';

export { serialize, deserialize } from 'surrial';

export function freezeRecursively<T extends { [prop: string]: any }>(target: T): T {
  Object.freeze(target);
  Object.keys(target).forEach(key => {
    const value = target[key];
    if (typeof value === 'object' && value !== null) {
      freezeRecursively(value);
    }
  });
  return target;
}

export function filterEmpty<T>(input: Array<T | null | void>) {
  return input.filter(item => item !== undefined && item !== null) as T[];
}

export function wrapInClosure(codeFragment: string) {
  return `
    (function (window) {
      ${codeFragment}
    })((Function('return this'))());`;
}

/**
 * A wrapper around `process.env` (for testability)
 */
export function getEnvironmentVariable(nameEnvironmentVariable: string): string | undefined {
  return process.env[nameEnvironmentVariable];
}

export function getEnvironmentVariableOrThrow(name: string): string {
  const value = getEnvironmentVariable(name);
  if (value === undefined) {
    throw new StrykerError(`Missing environment variable "${name}"`);
  } else {
    return value;
  }
}

/**
 * A wrapper around `process.exitCode = n` (for testability)
 */
export function setExitCode(n: number) {
  process.exitCode = n;
}

export function base64Decode(base64EncodedString: string) {
  return Buffer.from(base64EncodedString, 'base64').toString('utf8');
}

export function kill(pid: number): Promise<void> {
  return new Promise((res, rej) => {
    treeKill(pid, 'SIGKILL', (err?: { code?: number } & Error) => {
      if (err && !canIgnore(err.code)) {
        rej(err);
      } else {
        res();
      }
    });

    function canIgnore(code: number | undefined) {
      // https://docs.microsoft.com/en-us/windows/desktop/Debug/system-error-codes--0-499-
      // these error codes mean the program is _already_ closed.
      return code === 255 || code === 128;
    }
  });
}

export const TimeoutExpired: unique symbol = Symbol('TimeoutExpired');
export function timeout<T>(promise: Promise<T>, ms: number): Promise<T | typeof TimeoutExpired> {
  const sleep = new Promise<T | typeof TimeoutExpired>((res, rej) => {
    const timer = setTimeout(() => res(TimeoutExpired), ms);
    promise
      .then(result => {
        clearTimeout(timer);
        res(result);
      })
      .catch(error => {
        clearTimeout(timer);
        rej(error);
      });
  });
  return sleep;
}

export function padLeft(input: string): string {
  return input
    .split('\n')
    .map(str => '\t' + str)
    .join('\n');
}
