import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger, LoggerFactoryMethod } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { CoverageCollection, CoveragePerTestResult, RunResult, RunStatus, TestResult, TestRunner } from '@stryker-mutator/api/test_runner';
import * as karma from 'karma';

import strykerKarmaConf = require('./starters/stryker-karma.conf');
import ProjectStarter from './starters/ProjectStarter';
import StrykerKarmaSetup, { KARMA_CONFIG_KEY } from './StrykerKarmaSetup';
import StrykerReporter from './StrykerReporter';
import TestHooksMiddleware from './TestHooksMiddleware';

export interface ConfigOptions extends karma.ConfigOptions {
  detached?: boolean;
}

export default class KarmaTestRunner implements TestRunner {
  private currentTestResults: TestResult[];
  private currentErrorMessages: string[];
  private currentCoverageReport?: CoverageCollection | CoveragePerTestResult;
  private currentRunStatus: RunStatus;
  private readonly testHooksMiddleware = TestHooksMiddleware.instance;
  private readonly starter: ProjectStarter;
  public port: undefined | number;

  public static inject = tokens(commonTokens.logger, commonTokens.getLogger, commonTokens.options);
  constructor(private readonly log: Logger, getLogger: LoggerFactoryMethod, options: StrykerOptions) {
    const setup = this.loadSetup(options);
    this.starter = new ProjectStarter(getLogger, setup);
    this.setGlobals(setup, getLogger);
    this.cleanRun();
    this.listenToServerStart();
    this.listenToRunComplete();
    this.listenToSpecComplete();
    this.listenToCoverage();
    this.listenToError();
  }

  public init(): Promise<void> {
    return new Promise((res, rej) => {
      StrykerReporter.instance.once('browsers_ready', res);
      this.starter
        .start()
        .then(() => {
          /*noop*/
        })
        .catch(rej);
    });
  }

  public async run({ testHooks }: { testHooks?: string }): Promise<RunResult> {
    this.testHooksMiddleware.currentTestHooks = testHooks || '';
    if (this.currentRunStatus !== RunStatus.Error) {
      // Only run when there was no compile error
      // An compile error can happen in case of angular-cli
      await this.runServer();
    }
    const runResult = this.collectRunResult();
    this.cleanRun();
    return runResult;
  }

  private loadSetup(options: StrykerOptions): StrykerKarmaSetup {
    const defaultKarmaConfig: StrykerKarmaSetup = {
      projectType: 'custom'
    };
    return Object.assign(defaultKarmaConfig, options[KARMA_CONFIG_KEY]);
  }

  private setGlobals(setup: StrykerKarmaSetup, getLogger: LoggerFactoryMethod) {
    strykerKarmaConf.setGlobals({
      getLogger,
      karmaConfig: setup.config,
      karmaConfigFile: setup.configFile
    });
  }

  private cleanRun() {
    this.currentTestResults = [];
    this.currentErrorMessages = [];
    this.currentCoverageReport = undefined;
    this.currentRunStatus = RunStatus.Complete;
  }

  // Don't use dispose() to stop karma (using karma.stopper.stop)
  // It only works when in `detached` mode, as specified here: http://karma-runner.github.io/1.0/config/configuration-file.html

  private listenToSpecComplete() {
    StrykerReporter.instance.on('test_result', (testResult: TestResult) => {
      this.currentTestResults.push(testResult);
    });
  }

  private listenToServerStart() {
    StrykerReporter.instance.on('server_start', (port: number) => {
      this.port = port;
    });
    StrykerReporter.instance.on('server_start', () => {});
  }

  private listenToCoverage() {
    StrykerReporter.instance.on('coverage_report', (coverageReport: CoverageCollection | CoveragePerTestResult) => {
      this.currentCoverageReport = coverageReport;
    });
  }

  private listenToRunComplete() {
    StrykerReporter.instance.on('run_complete', (runStatus: RunStatus) => {
      this.currentRunStatus = runStatus;
    });
  }

  private listenToError() {
    StrykerReporter.instance.on('browser_error', (error: string) => {
      this.currentErrorMessages.push(error);
    });
    StrykerReporter.instance.on('compile_error', (errors: string[]) => {
      errors.forEach(error => this.currentErrorMessages.push(error));
      this.currentRunStatus = RunStatus.Error;
    });
  }

  private runServer() {
    return new Promise<void>(resolve => {
      karma.runner.run({ port: this.port }, exitCode => {
        this.log.debug('karma run done with ', exitCode);
        resolve();
      });
    });
  }

  private collectRunResult(): RunResult {
    return {
      coverage: this.currentCoverageReport,
      errorMessages: this.currentErrorMessages,
      status: this.determineRunState(),
      tests: this.currentTestResults
    };
  }

  private determineRunState() {
    // Karma will report an Error if no tests had executed.
    // This is not an "error" in Stryker terms
    if (this.currentRunStatus === RunStatus.Error && !this.currentErrorMessages.length && !this.currentTestResults.length) {
      return RunStatus.Complete;
    } else if (this.currentErrorMessages.length) {
      // Karma will return Complete when there are runtime errors
      return RunStatus.Error;
    } else {
      return this.currentRunStatus;
    }
  }
}

import * as karma from 'karma';

export const KARMA_CONFIG_KEY = 'karma';

export type ProjectKind = 'custom' | 'angular-cli';

export interface NgTestArguments {
  [key: string]: string | undefined;
}

export interface NgConfigOptions {
  testArguments?: NgTestArguments;
}

export default interface StrykerKarmaSetup {
  projectType: ProjectKind;
  configFile?: string;
  config?: karma.ConfigOptions;
  ngConfig?: NgConfigOptions;
}

import { EventEmitter } from 'events';

import { CoverageCollection, CoverageCollectionPerTest, RunStatus, TestResult, TestStatus } from '@stryker-mutator/api/test_runner';
import * as karma from 'karma';

export interface KarmaSpec {
  description: string;
  id: string;
  skipped: boolean;
  success: boolean;
  time: number;
  suite: string[];
  log: string[];
}

/**
 * This is a singleton implementation of a KarmaReporter.
 * It is loaded by karma and functions as a bridge between the karma world and the stryker world
 *
 * It uses properties as functions because karma is not able to find actual methods.
 *
 * i.e. use `public readonly onFoo = () => {}` instead of `onFoo() { }`.
 */
export default class StrykerReporter extends EventEmitter implements karma.Reporter {
  public adapters: any[] = [];

  private constructor() {
    super();
  }

  private static _instance = new StrykerReporter();
  public static get instance(): StrykerReporter {
    if (!this._instance) {
      this._instance = new StrykerReporter();
    }
    return this._instance;
  }

  public readonly onListening = (port: number) => {
    this.emit('server_start', port);
  };

  public readonly onSpecComplete = (_browser: any, spec: KarmaSpec) => {
    const name = spec.suite.reduce((name, suite) => name + suite + ' ', '') + spec.description;
    let status = TestStatus.Failed;
    if (spec.skipped) {
      status = TestStatus.Skipped;
    } else if (spec.success) {
      status = TestStatus.Success;
    }
    const testResult: TestResult = {
      failureMessages: spec.log,
      name,
      status,
      timeSpentMs: spec.time
    };
    this.emit('test_result', testResult);
  };

  public readonly onRunComplete = (runResult: karma.TestResults) => {
    this.emit('run_complete', this.collectRunState(runResult));
  };

  public readonly onLoadError = (...args: any[]) => {
    this.emit('load_error', ...args);
  };

  public readonly onBrowserComplete = (_browser: any, result: { coverage: CoverageCollection | CoverageCollectionPerTest }) => {
    this.emit('coverage_report', result.coverage);
  };

  public readonly onBrowsersReady = () => {
    this.emit('browsers_ready');
  };

  public readonly onBrowserError = (_browser: any, error: any) => {
    // Karma 2.0 has different error messages
    if (error.message) {
      this.emit('browser_error', error.message);
    } else {
      this.emit('browser_error', error.toString());
    }
  };

  public readonly onCompileError = (errors: string[]) => {
    // This is called from angular cli logic
    // https://github.com/angular/angular-cli/blob/012672161087a05ae5ecffbed5d1ee307ce1e0ad/packages/angular_devkit/build_angular/src/angular-cli-files/plugins/karma.ts#L96
    this.emit('compile_error', errors);
  };

  private collectRunState(runResult: karma.TestResults): RunStatus {
    if (runResult.disconnected) {
      return RunStatus.Timeout;
    } else if (runResult.error) {
      return RunStatus.Error;
    } else {
      return RunStatus.Complete;
    }
  }
}

import * as path from 'path';
import * as url from 'url';

import { RequestHandler } from 'express';

export const TEST_HOOKS_FILE_NAME = require.resolve('./testHooksForStryker');

export default class TestHooksMiddleware {
  private constructor() {
    // This `.bind` call is important! The `handler` will be executed with `.apply` (or friends) and otherwise the `this` won't point to this instance!
    this.handler = this.handler.bind(this);
  }

  private static _instance: TestHooksMiddleware;
  public static get instance(): TestHooksMiddleware {
    if (!this._instance) {
      this._instance = new TestHooksMiddleware();
    }
    return this._instance;
  }

  public currentTestHooks: string = '';

  public handler: RequestHandler = (request, response, next) => {
    const pathName = url.parse(request.url).pathname;
    if (pathName && path.normalize(pathName).endsWith(path.join('src', path.basename(TEST_HOOKS_FILE_NAME)))) {
      response.writeHead(200, {
        'Cache-Control': 'no-cache',
        'Content-Type': 'application/javascript'
      });
      response.end(this.currentTestHooks);
    } else {
      next();
    }
  };
}

import { declareClassPlugin, PluginKind } from '@stryker-mutator/api/plugin';

import KarmaTestRunner from './KarmaTestRunner';

export const strykerPlugins = [declareClassPlugin(PluginKind.TestRunner, 'karma', KarmaTestRunner)];

import { LoggerFactoryMethod } from '@stryker-mutator/api/logging';

import StrykerKarmaSetup from '../StrykerKarmaSetup';

import * as angularStarter from './angularStarter';
import * as karmaStarter from './karmaStarter';

export default class ProjectStarter {
  constructor(private readonly getLogger: LoggerFactoryMethod, private readonly setup: StrykerKarmaSetup) {}
  public start() {
    if (this.setup.projectType === 'angular-cli') {
      return angularStarter.start(this.getLogger, this.setup.ngConfig);
    } else {
      return karmaStarter.start();
    }
  }
}

import * as path from 'path';

import { Logger, LoggerFactoryMethod } from '@stryker-mutator/api/logging';
import decamelize from 'decamelize';
import * as semver from 'semver';

import { NgConfigOptions, NgTestArguments } from '../StrykerKarmaSetup';
import { requireModule } from '../utils';

const MIN_ANGULAR_CLI_VERSION = '6.1.0';

export async function start(getLogger: LoggerFactoryMethod, ngConfig?: NgConfigOptions): Promise<void> {
  const logger: Logger = getLogger(path.basename(__filename));
  verifyAngularCliVersion();

  // Make sure require angular cli from inside this function, that way it won't break if angular isn't installed and this file is required.
  let cli = requireModule('@angular/cli');
  if ('default' in cli) {
    cli = cli.default;
  }
  const cliArgs = ['test', '--progress=false', `--karma-config=${require.resolve('./stryker-karma.conf')}`];
  if (ngConfig && ngConfig.testArguments) {
    const testArguments: NgTestArguments = ngConfig.testArguments;

    const ngTestArguments = Object.keys(testArguments);
    verifyNgTestArguments(ngTestArguments);
    ngTestArguments.forEach(key => {
      const decamelizedKey = decamelize(key, '-');
      if ('progress' !== key && 'karma-config' !== decamelizedKey) {
        cliArgs.push(`--${decamelizedKey}=${testArguments[key]}`);
      }
    });
  }
  const actualCommand = `ng ${cliArgs.join(' ')}`;
  logger.debug(`Starting Angular tests: ${actualCommand}`);
  return cli({
    cliArgs,
    inputStream: process.stdin,
    outputStream: process.stdout
  }).then((exitCode: number) => {
    if (exitCode > 0) {
      throw new Error(
        `\`ng test\` command failed with exit code ${exitCode}. Please run with logLevel 'trace' to see the angular-cli console output (actual command was ${actualCommand})`
      );
    }
  });
}

function verifyAngularCliVersion() {
  const version = semver.coerce(requireModule('@angular/cli/package').version);
  if (!version || semver.lt(version, MIN_ANGULAR_CLI_VERSION)) {
    throw new Error(`Your @angular/cli version (${version}) is not supported. Please install ${MIN_ANGULAR_CLI_VERSION} or higher`);
  }
}
function verifyNgTestArguments(ngTestArguments: string[]) {
  const prefixedArguments = ngTestArguments.filter(key => key.trim().startsWith('-'));
  if (prefixedArguments.length > 0) {
    throw new Error(`Don't prefix arguments with dashes ('-'). Stryker will do this automatically. Problematic arguments are ${prefixedArguments}.`);
  }
}

import { requireModule } from '../utils';

export async function start(): Promise<void> {
  // Make sure require karma from inside this function, that way it won't break if karma isn't installed and this file is required.
  const karma = requireModule('karma');
  await new karma.Server({
    configFile: require.resolve('./stryker-karma.conf')
  }).start();
}

import * as path from 'path';

import { Logger, LoggerFactoryMethod } from '@stryker-mutator/api/logging';
import { Config, ConfigOptions } from 'karma';

import StrykerReporter from '../StrykerReporter';
import TestHooksMiddleware, { TEST_HOOKS_FILE_NAME } from '../TestHooksMiddleware';
import { requireModule } from '../utils';

function setDefaultOptions(config: Config) {
  config.set({
    browsers: ['PhantomJS'],
    frameworks: ['jasmine']
  });
}

function setUserKarmaConfigFile(config: Config, log: Logger) {
  if (globalSettings.karmaConfigFile && typeof globalSettings.karmaConfigFile === 'string') {
    const configFileName = path.resolve(globalSettings.karmaConfigFile);
    log.debug('Importing config from "%s"', configFileName);
    try {
      const userConfig = requireModule(configFileName);
      userConfig(config);
      config.configFile = configFileName; // override config to ensure karma is as user-like as possible
    } catch (error) {
      if (error.code === 'MODULE_NOT_FOUND') {
        log.error(
          `Unable to find karma config at "${globalSettings.karmaConfigFile}" (tried to load from ${configFileName}). Please check your stryker config. You might need to make sure the file is included in the sandbox directory.`
        );
      } else {
        log.error(`Could not read karma configuration from ${globalSettings.karmaConfigFile}.`, error);
      }
    }
  }
}

/**
 * Sets configuration that is needed to control the karma life cycle. Namely it shouldn't watch files and not quit after first test run.
 * @param config The config to use
 */
function setLifeCycleOptions(config: Config) {
  config.set({
    // No auto watch, stryker will inform us when we need to test
    autoWatch: false,
    // Override browserNoActivityTimeout. Default value 10000 might not enough to send perTest coverage results
    browserNoActivityTimeout: 1000000,
    // Never detach, always run in this same process (is already a separate process)
    detached: false,
    // Don't stop after first run
    singleRun: false
  });
}

function setUserKarmaConfig(config: Config) {
  if (globalSettings.karmaConfig) {
    config.set(globalSettings.karmaConfig);
  }
}

function setBasePath(config: Config) {
  if (!config.basePath) {
    // We need to set the base path, so karma won't use this file to base everything of
    if (globalSettings.karmaConfigFile) {
      config.basePath = path.resolve(path.dirname(globalSettings.karmaConfigFile));
    } else {
      config.basePath = process.cwd();
    }
  }
}

function addPlugin(karmaConfig: ConfigOptions, karmaPlugin: any) {
  karmaConfig.plugins = karmaConfig.plugins || ['karma-*'];
  karmaConfig.plugins.push(karmaPlugin);
}

/**
 * Configures the test hooks middleware.
 * It adds a non-existing file to the top `files` array.
 * Further more it configures a middleware that serves the file.
 */
function configureTestHooksMiddleware(config: Config) {
  // Add test run middleware file
  config.files = config.files || [];

  config.files.unshift({ pattern: TEST_HOOKS_FILE_NAME, included: true, watched: false, served: false, nocache: true }); // Add a custom hooks file to provide hooks
  const middleware: string[] = (config as any).middleware || ((config as any).middleware = []);
  middleware.unshift(TestHooksMiddleware.name);
  addPlugin(config, { [`middleware:${TestHooksMiddleware.name}`]: ['value', TestHooksMiddleware.instance.handler] });
}

function configureStrykerReporter(config: Config) {
  addPlugin(config, { [`reporter:${StrykerReporter.name}`]: ['value', StrykerReporter.instance] });
  if (!config.reporters) {
    config.reporters = [];
  }
  config.reporters.push(StrykerReporter.name);
}

const noopLogger = {
  isTraceEnabled() {
    return false;
  },
  isDebugEnabled() {
    return false;
  },
  isInfoEnabled() {
    return false;
  },
  isWarnEnabled() {
    return false;
  },
  isErrorEnabled() {
    return false;
  },
  isFatalEnabled() {
    return false;
  },
  trace() {
    // noop
  },
  debug() {
    // noop
  },
  info() {
    // noop
  },
  warn() {
    // noop
  },
  error() {
    // noop
  },
  fatal() {
    // noop
  }
};

const globalSettings: {
  karmaConfig?: ConfigOptions;
  karmaConfigFile?: string;
  getLogger: LoggerFactoryMethod;
} = {
  getLogger() {
    return noopLogger;
  }
};

export = Object.assign(
  (config: Config) => {
    const log = globalSettings.getLogger(path.basename(__filename));
    setDefaultOptions(config);
    setUserKarmaConfigFile(config, log);
    setUserKarmaConfig(config);
    setBasePath(config);
    setLifeCycleOptions(config);
    configureTestHooksMiddleware(config);
    configureStrykerReporter(config);
  },
  {
    /**
     * Provide global settings for next configuration
     * This is the only way we can pass through any values between the `KarmaTestRunner` and the stryker-karma.conf file.
     * (not counting environment variables)
     */
    setGlobals(globals: { karmaConfig?: ConfigOptions; karmaConfigFile?: string; getLogger?: LoggerFactoryMethod }) {
      globalSettings.karmaConfig = globals.karmaConfig;
      globalSettings.karmaConfigFile = globals.karmaConfigFile;
      globalSettings.getLogger = globals.getLogger || (() => noopLogger);
    }
  }
);

export function requireModule(name: string): any {
  return require(name);
}
