import * as Mocha from 'mocha';
import * as multimatch from 'multimatch';

import { MochaOptions } from './MochaOptions';

let loadOptions: undefined | ((argv?: string[] | string) => { [key: string]: any } | undefined);
let handleFiles: undefined | ((options: MochaOptions) => string[]);

try {
  /*
   * If read, object containing parsed arguments
   * @since 6.0.0'
   * @see https://mochajs.org/api/module-lib_cli_options.html#.loadOptions
   */
  loadOptions = require('mocha/lib/cli/options').loadOptions;
} catch {
  // Mocha < 6 doesn't support `loadOptions`
}

try {
  // https://github.com/mochajs/mocha/blob/master/lib/cli/run-helpers.js#L132
  handleFiles = require('mocha/lib/cli/run-helpers').handleFiles;
  if (!handleFiles) {
    // Might be moved: https://github.com/mochajs/mocha/commit/15b96afccaf508312445770e3af1c145d90b28c6#diff-39b692a81eb0c9f3614247af744ab4a8
    handleFiles = require('mocha/lib/cli/collect-files');
  }
} catch {
  // Mocha < 6 doesn't support `handleFiles`
}

/**
 * Wraps Mocha class and require for testability
 */
export default class LibWrapper {
  public static Mocha = Mocha;
  public static require = require;
  public static multimatch = multimatch;
  public static loadOptions = loadOptions;
  public static handleFiles = handleFiles;
}

import { Config, ConfigEditor } from '@stryker-mutator/api/config';
import { tokens } from '@stryker-mutator/api/plugin';

import MochaOptionsLoader from './MochaOptionsLoader';
import { mochaOptionsKey } from './utils';

export default class MochaConfigEditor implements ConfigEditor {
  public static inject = tokens('loader');
  constructor(private readonly loader: MochaOptionsLoader) {}

  public edit(config: Config): void {
    config[mochaOptionsKey] = this.loader.load(config);
  }
}

import * as fs from 'fs';
import * as path from 'path';

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';

import LibWrapper from './LibWrapper';
import { MochaOptions } from './MochaOptions';
import { filterConfig, mochaOptionsKey, serializeArguments } from './utils';

/**
 * Subset of defaults for mocha options
 * @see https://github.com/mochajs/mocha/blob/master/lib/mocharc.json
 */
export const DEFAULT_MOCHA_OPTIONS = Object.freeze({
  extension: ['js'],
  file: [],
  ignore: [],
  opts: './test/mocha.opts',
  spec: ['test'],
  timeout: 2000,
  ui: 'bdd'
});

export default class MochaOptionsLoader {
  public static inject = tokens(commonTokens.logger);
  constructor(private readonly log: Logger) {}

  public load(strykerOptions: StrykerOptions): MochaOptions {
    const mochaOptions = { ...strykerOptions[mochaOptionsKey] } as MochaOptions;
    return { ...DEFAULT_MOCHA_OPTIONS, ...this.loadMochaOptions(mochaOptions), ...mochaOptions };
  }

  private loadMochaOptions(overrides: MochaOptions) {
    if (LibWrapper.loadOptions) {
      this.log.debug("Mocha >= 6 detected. Using mocha's `%s` to load mocha options", LibWrapper.loadOptions.name);
      return this.loadMocha6Options(overrides);
    } else {
      this.log.warn('DEPRECATED: Mocha < 6 detected. Please upgrade to at least Mocha version 6.');
      this.log.debug('Mocha < 6 detected. Using custom logic to parse mocha options');
      return this.loadLegacyMochaOptsFile(overrides.opts);
    }
  }

  private loadMocha6Options(overrides: MochaOptions) {
    const args = serializeArguments(overrides);
    const loadOptions = LibWrapper.loadOptions || (() => ({}));
    const rawConfig = loadOptions(args) || {};
    if (this.log.isTraceEnabled()) {
      this.log.trace(`Mocha: ${loadOptions.name}([${args.map(arg => `'${arg}'`).join(',')}]) => ${JSON.stringify(rawConfig)}`);
    }
    const options = filterConfig(rawConfig);
    if (this.log.isDebugEnabled()) {
      this.log.debug(`Loaded options: ${JSON.stringify(options, null, 2)}`);
    }
    return options;
  }

  private loadLegacyMochaOptsFile(opts: false | string | undefined): MochaOptions {
    switch (typeof opts) {
      case 'boolean':
        this.log.debug('Not reading additional mochaOpts from a file');
        return {};
      case 'undefined':
        const defaultMochaOptsFileName = path.resolve(DEFAULT_MOCHA_OPTIONS.opts);
        if (fs.existsSync(defaultMochaOptsFileName)) {
          return this.readMochaOptsFile(defaultMochaOptsFileName);
        } else {
          this.log.debug('No mocha opts file found, not loading additional mocha options (%s.opts was not defined).', mochaOptionsKey);
          return {};
        }
      case 'string':
        const optsFileName = path.resolve(opts);
        if (fs.existsSync(optsFileName)) {
          return this.readMochaOptsFile(optsFileName);
        } else {
          this.log.error(`Could not load opts from "${optsFileName}". Please make sure opts file exists.`);
          return {};
        }
    }
  }

  private readMochaOptsFile(optsFileName: string) {
    this.log.info(`Loading mochaOpts from "${optsFileName}"`);
    return this.parseOptsFile(fs.readFileSync(optsFileName, 'utf8'));
  }

  private parseOptsFile(optsFileContent: string): MochaOptions {
    const options = optsFileContent.split('\n').map(val => val.trim());
    const mochaRunnerOptions: MochaOptions = Object.create(null);
    options.forEach(option => {
      const args = option.split(' ').filter(Boolean);
      if (args[0]) {
        switch (args[0]) {
          case '--require':
          case '-r':
            args.shift();
            if (!mochaRunnerOptions.require) {
              mochaRunnerOptions.require = [];
            }
            mochaRunnerOptions.require.push(...args);
            break;
          case '--timeout':
          case '-t':
            mochaRunnerOptions.timeout = this.parseNextInt(args);
            break;
          case '--async-only':
          case '-A':
            mochaRunnerOptions.asyncOnly = true;
            break;
          case '--ui':
          case '-u':
            mochaRunnerOptions.ui = this.parseNextString(args);
            break;
          case '--grep':
          case '-g':
            let arg = `${this.parseNextString(args)}`;
            if (arg.startsWith('/') && arg.endsWith('/')) {
              arg = arg.substring(1, arg.length - 1);
            }
            mochaRunnerOptions.grep = new RegExp(arg);
            break;
          default:
            this.log.debug(`Ignoring option "${args[0]}" as it is not supported.`);
            break;
        }
      }
    });
    return mochaRunnerOptions;
  }

  private parseNextInt(args: string[]): number | undefined {
    if (args.length > 1) {
      return parseInt(args[1], 10);
    } else {
      return undefined;
    }
  }

  private parseNextString(args: string[]): string | undefined {
    if (args.length > 1) {
      return args[1];
    } else {
      return undefined;
    }
  }
}

import * as path from 'path';

import { StrykerOptions } from '@stryker-mutator/api/core';
import { Logger } from '@stryker-mutator/api/logging';
import { commonTokens, tokens } from '@stryker-mutator/api/plugin';
import { RunResult, RunStatus, TestRunner } from '@stryker-mutator/api/test_runner';

import LibWrapper from './LibWrapper';
import { MochaOptions } from './MochaOptions';
import { StrykerMochaReporter } from './StrykerMochaReporter';
import { evalGlobal, mochaOptionsKey } from './utils';

const DEFAULT_TEST_PATTERN = 'test/**/*.js';

export default class MochaTestRunner implements TestRunner {
  private testFileNames: string[];
  private readonly mochaOptions: MochaOptions;

  public static inject = tokens(commonTokens.logger, commonTokens.sandboxFileNames, commonTokens.options);
  constructor(private readonly log: Logger, private readonly allFileNames: readonly string[], options: StrykerOptions) {
    this.mochaOptions = options[mochaOptionsKey];
    this.additionalRequires();
    StrykerMochaReporter.log = log;
  }

  public init(): void {
    if (LibWrapper.handleFiles) {
      this.log.debug("Mocha >= 6 detected. Using mocha's `handleFiles` to load files");
      this.testFileNames = this.mocha6DiscoverFiles(LibWrapper.handleFiles);
    } else {
      this.log.debug('Mocha < 6 detected. Using custom logic to discover files');
      this.testFileNames = this.legacyDiscoverFiles();
    }
  }

  private mocha6DiscoverFiles(handleFiles: (options: MochaOptions) => string[]): string[] {
    const originalProcessExit = process.exit;
    try {
      // process.exit unfortunate side effect: https://github.com/mochajs/mocha/blob/07ea8763c663bdd3fe1f8446cdb62dae233f4916/lib/cli/run-helpers.js#L174
      (process as any).exit = () => {};
      const files = handleFiles(this.mochaOptions);
      return files;
    } finally {
      process.exit = originalProcessExit;
    }
  }

  private legacyDiscoverFiles(): string[] {
    const globPatterns = this.mochaFileGlobPatterns();
    const globPatternsAbsolute = globPatterns.map(glob => path.resolve(glob));
    const fileNames = LibWrapper.multimatch(this.allFileNames.slice(), globPatternsAbsolute);
    if (fileNames.length) {
      this.log.debug(`Using files: ${JSON.stringify(fileNames, null, 2)}`);
    } else {
      this.log.debug(`Tried ${JSON.stringify(globPatternsAbsolute, null, 2)} on files: ${JSON.stringify(this.allFileNames, null, 2)}.`);
      throw new Error(
        `[${MochaTestRunner.name}] No files discovered (tried pattern(s) ${JSON.stringify(
          globPatterns,
          null,
          2
        )}). Please specify the files (glob patterns) containing your tests in ${mochaOptionsKey}.files in your stryker.conf.js file.`
      );
    }
    return fileNames;
  }
  private mochaFileGlobPatterns(): string[] {
    // Use both `spec` as `files`
    const globPatterns: string[] = [];
    if (this.mochaOptions.spec) {
      globPatterns.push(...this.mochaOptions.spec);
    }

    if (typeof this.mochaOptions.files === 'string') {
      // `files` if for backward compat
      globPatterns.push(this.mochaOptions.files);
    } else if (this.mochaOptions.files) {
      globPatterns.push(...this.mochaOptions.files);
    }
    if (!globPatterns.length) {
      globPatterns.push(DEFAULT_TEST_PATTERN);
    }
    return globPatterns;
  }

  public run({ testHooks }: { testHooks?: string }): Promise<RunResult> {
    return new Promise<RunResult>((resolve, reject) => {
      try {
        this.purgeFiles();
        const mocha = new LibWrapper.Mocha({ reporter: StrykerMochaReporter as any, bail: true });
        this.configure(mocha);
        this.addTestHooks(mocha, testHooks);
        this.addFiles(mocha);
        try {
          mocha.run(() => {
            const reporter = StrykerMochaReporter.currentInstance;
            if (reporter) {
              const result: RunResult = reporter.runResult;
              resolve(result);
            } else {
              const errorMsg = 'The StrykerMochaReporter was not instantiated properly. Could not retrieve the RunResult.';
              this.log.error(errorMsg);
              resolve({
                errorMessages: [errorMsg],
                status: RunStatus.Error,
                tests: []
              });
            }
          });
        } catch (error) {
          resolve({
            errorMessages: [error],
            status: RunStatus.Error,
            tests: []
          });
        }
      } catch (error) {
        this.log.error(error);
        reject(error);
      }
    });
  }

  private purgeFiles() {
    this.allFileNames.forEach(fileName => delete require.cache[fileName]);
  }

  private addFiles(mocha: Mocha) {
    this.testFileNames.forEach(fileName => {
      mocha.addFile(fileName);
    });
  }

  private addTestHooks(mocha: Mocha, testHooks: string | undefined): void {
    if (testHooks) {
      const suite = (mocha as any).suite;
      suite.emit('pre-require', global, '', mocha);
      suite.emit('require', evalGlobal(testHooks), '', mocha);
      suite.emit('post-require', global, '', mocha);
    }
  }

  private configure(mocha: Mocha) {
    const options = this.mochaOptions;

    function setIfDefined<T>(value: T | undefined, operation: (input: T) => void) {
      if (typeof value !== 'undefined') {
        operation.apply(mocha, [value]);
      }
    }

    if (options) {
      setIfDefined(options.asyncOnly, mocha.asyncOnly);
      setIfDefined(options.timeout, mocha.timeout);
      setIfDefined(options.ui, mocha.ui);
      setIfDefined(options.grep, mocha.grep);
    }
  }

  private additionalRequires() {
    if (this.mochaOptions.require) {
      const modulesToRequire = this.mochaOptions.require.map(moduleName => (moduleName.startsWith('.') ? path.resolve(moduleName) : moduleName));
      modulesToRequire.forEach(LibWrapper.require);
    }
  }
}

import { Logger } from '@stryker-mutator/api/logging';
import { RunResult, RunStatus, TestStatus } from '@stryker-mutator/api/test_runner';

import Timer from './Timer';

export class StrykerMochaReporter {
  /*
   * The stryker logger instance injected into this plugin
   * Needs to be set from 'the outside' because mocha doesn't really have a nice way of providing
   * data to reporters...
   */
  public static log: Logger;
  public runResult: RunResult;
  private readonly timer = new Timer();
  private passedCount = 0;

  public static currentInstance: StrykerMochaReporter | undefined;

  constructor(private readonly runner: NodeJS.EventEmitter) {
    this.registerEvents();
    StrykerMochaReporter.currentInstance = this;
  }

  private registerEvents() {
    this.runner.on('start', () => {
      this.passedCount = 0;
      this.timer.reset();
      this.runResult = {
        errorMessages: [],
        status: RunStatus.Error,
        tests: []
      };
      StrykerMochaReporter.log.debug('Starting Mocha test run');
    });

    this.runner.on('pass', (test: any) => {
      this.runResult.tests.push({
        name: test.fullTitle(),
        status: TestStatus.Success,
        timeSpentMs: this.timer.elapsedMs()
      });
      this.passedCount++;
      this.timer.reset();
    });

    this.runner.on('fail', (test: any, err: any) => {
      this.runResult.tests.push({
        failureMessages: [err.message],
        name: test.fullTitle(),
        status: TestStatus.Failed,
        timeSpentMs: this.timer.elapsedMs()
      });
      if (!this.runResult.errorMessages) {
        this.runResult.errorMessages = [];
      }
      this.runResult.errorMessages.push(err.message);
      if (StrykerMochaReporter.log.isTraceEnabled()) {
        StrykerMochaReporter.log.trace(`Test failed: ${test.fullTitle()}. Error: ${err.message}`);
      }
    });

    this.runner.on('end', () => {
      this.runResult.status = RunStatus.Complete;
      StrykerMochaReporter.log.debug('Mocha test run completed: %s/%s passed', this.passedCount, this.runResult.tests.length);
    });
  }
}

// A test can mock away the 'real' Date class. Capture it while we still can.
const RealDate = Date;
export default class Timer {
  private start: Date;

  constructor() {
    this.reset();
  }

  public reset() {
    this.start = new RealDate();
  }

  public elapsedMs() {
    return new RealDate().getTime() - this.start.getTime();
  }
}

import { BaseContext, commonTokens, declareClassPlugin, declareFactoryPlugin, Injector, PluginKind, tokens } from '@stryker-mutator/api/plugin';

import MochaConfigEditor from './MochaConfigEditor';
import MochaOptionsLoader from './MochaOptionsLoader';
import MochaTestRunner from './MochaTestRunner';

export const strykerPlugins = [
  declareFactoryPlugin(PluginKind.ConfigEditor, 'mocha-runner', mochaConfigEditorFactory),
  declareClassPlugin(PluginKind.TestRunner, 'mocha', MochaTestRunner)
];

mochaConfigEditorFactory.inject = tokens(commonTokens.injector);
function mochaConfigEditorFactory(injector: Injector<BaseContext>): MochaConfigEditor {
  return injector.provideClass('loader', MochaOptionsLoader).injectClass(MochaConfigEditor);
}

import { MochaOptions } from './MochaOptions';

/**
 * Executes a piece of javascript code in global scope while passing the `require` function
 * @param body The JavaScript to execute
 */
export function evalGlobal(body: string) {
  const fn = new Function('require', body);
  fn(require);
}

export function serializeArguments(mochaOptions: MochaOptions) {
  const args: string[] = [];
  Object.keys(mochaOptions).forEach(key => {
    args.push(`--${key}`);
    const value: any = (mochaOptions as any)[key];
    if (typeof value === 'string') {
      args.push(value);
    } else if (Array.isArray(value)) {
      args.push(value.join(','));
    }
  });
  return args;
}

export const mochaOptionsKey = 'mochaOptions';

const SUPPORTED_MOCHA_OPTIONS = Object.freeze(['extension', 'require', 'timeout', 'async-only', 'ui', 'grep', 'exclude', 'ignore', 'spec', 'file']);

/**
 * Filter out those config values that are actually useful to run mocha with Stryker
 * @param rawConfig The raw parsed mocha configuration
 */
export function filterConfig(rawConfig: { [key: string]: any }): MochaOptions {
  const options: MochaOptions = {};
  Object.keys(rawConfig)
    .filter(rawOption => SUPPORTED_MOCHA_OPTIONS.some(supportedOption => rawOption === supportedOption))
    .forEach(option => ((options as any)[option] = rawConfig[option]));

  // Config file can also contain positional arguments. They are provided under the `_` key
  // For example:
  // When mocha.opts contains "--async-only test/**/*.js", then "test/**/*.js will be the positional argument
  // We must provide it to mocha as "spec"
  if (rawConfig._ && rawConfig._.length) {
    if (!options.spec) {
      options.spec = [];
    }
    const specs = options.spec;
    rawConfig._.forEach((positionalArgument: string) => specs.push(positionalArgument));
  }
  return options;
}
